<body>

    <div id="control-panel">
        <label for="slider-n">n</label>
        <input type="range" id="slider-n" min="1" max="20" value="1" step="1">
        <input type="number" id="input-n" min="1" max="20" value="1" step="1">
    </div>

    <div id="visualization-area">
        <div id="function-plot-container">
            <canvas id="canvas-function"></canvas>
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
        </div>
        <div id="sum-plot-container">
            <canvas id="canvas-sum"></canvas>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // Shared state variable for n
            let n = 1;

            // Predefined colors for up to 20 terms
            const colors = [
                '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', '#6495ed', '#9370db', '#8a2be2',
                '#ff69b4', '#ff4500', '#ffd700', '#32cd32', '#4682b4', '#dda0dd', '#f08080', '#ff8c00',
                '#adff2f', '#00ced1', '#ba55d3', '#db7093'
            ];

            const sliderN = document.getElementById('slider-n');
            const inputN = document.getElementById('input-n');
            const functionPlotContainer = document.getElementById('function-plot-container');
            const sumPlotContainer = document.getElementById('sum-plot-container');

            let functionPlotInstance, sumPlotInstance;

            // Sketch for the function plot (left)
            const sketchFunctionPlot = (p) => {
                const padding = { top: 20, right: 20, bottom: 40, left: 40 };
                const xMax = 10.5;
                const yMax = 2.2;

                const mapX = (val) => p.map(val, 0, xMax, padding.left, p.width - padding.right);
                const mapY = (val) => p.map(val, 0, yMax, p.height - padding.bottom, padding.top); // Inverts Y-axis

                p.setup = () => {
                    const { width, height } = functionPlotContainer.getBoundingClientRect();
                    p.createCanvas(width, height);
                    p.noLoop(); // Redraw only on demand
                };

                p.windowResized = () => {
                    const { width, height } = functionPlotContainer.getBoundingClientRect();
                    p.resizeCanvas(width, height);
                };

                p.draw = () => {
                    p.background(255);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.fill(0);
                    p.textSize(12);

                    // Draw Axes
                    p.line(mapX(0), mapY(0), mapX(xMax - 0.5), mapY(0)); // X-axis
                    p.line(mapX(0), mapY(0), mapX(0), mapY(yMax)); // Y-axis

                    // Draw X-axis ticks and labels
                    p.textAlign(p.CENTER, p.TOP);
                    for (let i = 0; i <= 10; i++) {
                        p.line(mapX(i), mapY(0), mapX(i), mapY(-0.05));
                        p.text(i, mapX(i), mapY(0) + 5);
                    }

                    // Draw Y-axis ticks and labels
                    p.textAlign(p.RIGHT, p.CENTER);
                    for (let i = 0; i <= 2; i++) {
                        p.line(mapX(0), mapY(i), mapX(-0.1), mapY(i));
                        p.text(i, mapX(0) - 5, mapY(i));
                    }

                    // Draw rectangles for terms 1 to n
                    for (let k = 1; k <= n; k++) {
                        p.fill(colors[(k - 1) % colors.length]);
                        p.noStroke();
                        const x1 = k;
                        const y1 = 2 / k;
                        const x2 = k + 1;
                        const y2 = 2 / (k + 1);
                        p.rect(mapX(x1), mapY(y1), mapX(x2) - mapX(x1), mapY(y2) - mapY(y1));
                    }

                    // Draw dashed lines for k=1, 2, 3
                    p.stroke(180);
                    p.strokeWeight(1);
                    for (let k = 1; k <= 3; k++) {
                        if (k > n) break;
                        drawDashedLine(p, mapX(k), mapY(0), mapX(k), mapY(2/k), 5);
                    }

                    // Draw the function y = 2/x
                    p.noFill();
                    p.stroke('blue');
                    p.strokeWeight(2);
                    p.beginShape();
                    for (let x = 0.5; x < xMax; x += 0.1) {
                        const y = 2 / x;
                        p.vertex(mapX(x), mapY(y));
                    }
                    p.endShape();
                };

                function drawDashedLine(p, x1, y1, x2, y2, dashLen) {
                    p.push();
                    const d = p.dist(x1, y1, x2, y2);
                    const dashCount = p.floor(d / dashLen);
                    const dx = (x2 - x1) / dashCount;
                    const dy = (y2 - y1) / dashCount;
                    for (let i = 0; i < dashCount; i += 2) {
                        p.line(x1 + i * dx, y1 + i * dy, x1 + (i + 1) * dx, y1 + (i + 1) * dy);
                    }
                    p.pop();
                }
            };

            // Sketch for the summation plot (right)
            const sketchSumPlot = (p) => {
                const padding = { top: 20, right: 20, bottom: 40, left: 40 };
                const xMax = 1.5;
                const yMax = 2.2;

                const mapX = (val) => p.map(val, 0, xMax, padding.left, p.width - padding.right);
                const mapY = (val) => p.map(val, 0, yMax, p.height - padding.bottom, padding.top);

                p.setup = () => {
                    const { width, height } = sumPlotContainer.getBoundingClientRect();
                    p.createCanvas(width, height);
                    p.noLoop();
                };

                p.windowResized = () => {
                    const { width, height } = sumPlotContainer.getBoundingClientRect();
                    p.resizeCanvas(width, height);
                };

                p.draw = () => {
                    p.background(255);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.fill(0);
                    p.textSize(12);

                    // Draw Axes
                    p.line(mapX(0), mapY(0), mapX(xMax - 0.5), mapY(0)); // X-axis
                    p.line(mapX(0), mapY(0), mapX(0), mapY(yMax)); // Y-axis

                    // Draw X-axis ticks and labels
                    p.textAlign(p.CENTER, p.TOP);
                    for (let i = 0; i <= 1; i++) {
                        p.line(mapX(i), mapY(0), mapX(i), mapY(-0.05));
                        p.text(i, mapX(i), mapY(0) + 5);
                    }

                    // Draw Y-axis ticks and labels
                    p.textAlign(p.RIGHT, p.CENTER);
                    for (let i = 0; i <= 2; i++) {
                        p.line(mapX(0), mapY(i), mapX(-0.1), mapY(i));
                        p.text(i, mapX(0) - 5, mapY(i));
                    }

                    // Draw stacked rectangles
                    let y_stack = 0;
                    const rectWidth = mapX(1) - mapX(0);

                    // Loop from n down to 1 to stack correctly (yellow on top)
                    for (let k = n; k >= 1; k--) {
                        p.fill(colors[(k - 1) % colors.length]);
                        p.stroke(150);
                        p.strokeWeight(0.5);

                        const h = 2 / k - 2 / (k + 1);
                        const rectY = mapY(y_stack + h);
                        const rectHeight = mapY(y_stack) - mapY(y_stack + h);

                        p.rect(mapX(0), rectY, rectWidth, rectHeight);
                        y_stack += h;
                    }
                };
            };

            // Instantiate p5 sketches
            functionPlotInstance = new p5(sketchFunctionPlot, 'function-plot-container');
            sumPlotInstance = new p5(sketchSumPlot, 'sum-plot-container');

            // --- Event Listeners and Control Logic ---

            function handleNChange(newValue) {
                const value = parseInt(newValue, 10);
                if (isNaN(value)) return;

                n = Math.max(1, Math.min(20, value)); // Clamp value

                // Synchronize controls
                sliderN.value = n;
                inputN.value = n;

                // Trigger redraw on both canvases
                if (functionPlotInstance && sumPlotInstance) {
                    functionPlotInstance.redraw();
                    sumPlotInstance.redraw();
                }
            }

            sliderN.addEventListener('input', (e) => handleNChange(e.target.value));
            inputN.addEventListener('change', (e) => handleNChange(e.target.value)); // Use 'change' for number input to avoid rapid updates while typing

            // Handle window resizing
            window.addEventListener('resize', () => {
                if (functionPlotInstance && sumPlotInstance) {
                    functionPlotInstance.windowResized();
                    sumPlotInstance.windowResized();
                    functionPlotInstance.redraw();
                    sumPlotInstance.redraw();
                }
            });

            // Initial draw for n=1
            handleNChange(1);
        });
    </script>

</body>
