            width: 60px;
            text-align: right;
            font-family: monospace;
            font-size: 14px;
        }

        .hidden {
            display: none;
        }
    </style>
</head>
<body>

    <div id="main-container">
        <div id="control-panel">
            <div class="button-group">
                <button id="btn-monod" class="active">Monod</button>
                <button id="btn-si">substrate inhibition</button>
            </div>
            <div class="button-group">
                <button id="btn-bifurcation" class="active">bifurcation diagram</button>
                <button id="btn-nontrivial">nontrivial steady state</button>
            </div>
            <div class="slider-container">
                <label for="slider-x2f">x<sub>2f</sub></label>
                <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4.0">
                <span id="value-x2f"></span>
            </div>
            <div class="slider-container">
                <label for="slider-umax">&mu;<sub>max</sub></label>
                <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                <span id="value-umax"></span>
            </div>
            <div class="slider-container">
                <label for="slider-km">k<sub>m</sub></label>
                <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                <span id="value-km"></span>
            </div>
            <div id="k1-slider-container" class="slider-container hidden">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1"></span>
            </div>
            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                <span id="value-ds"></span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function () {
            // --- STATE MANAGEMENT ---
            let currentModel = 'monod';
            let currentView = 'bifurcation';
            const params = {
                x2f: 4.0,
                umax: 0.6,
                km: 0.12,
                k1: 0.4545,
                ds: 0.6,
            };

            // --- DOM ELEMENT REFERENCES ---
            const sliders = {
                x2f: document.getElementById('slider-x2f'),
                umax: document.getElementById('slider-umax'),
                km: document.getElementById('slider-km'),
                k1: document.getElementById('slider-k1'),
                ds: document.getElementById('slider-ds'),
            };
            const values = {
                x2f: document.getElementById('value-x2f'),
                umax: document.getElementById('value-umax'),
                km: document.getElementById('value-km'),
                k1: document.getElementById('value-k1'),
                ds: document.getElementById('value-ds'),
            };
            const buttons = {
                monod: document.getElementById('btn-monod'),
                si: document.getElementById('btn-si'),
                bifurcation: document.getElementById('btn-bifurcation'),
                nontrivial: document.getElementById('btn-nontrivial'),
            };
            const k1SliderContainer = document.getElementById('k1-slider-container');
            const plotContainer = document.getElementById('plot-container');

            // --- MODEL FORMULAS ---
            const monodGrowthRate = (x2) => params.umax * x2 / (params.km + x2);
            const siGrowthRate = (x2) => params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
            const getGrowthRateFunc = () => currentModel === 'monod' ? monodGrowthRate : siGrowthRate;

            // --- INITIALIZATION ---
            function init() {
                // Set up sliders and their value displays
                for (const key in sliders) {
                    sliders[key].value = params[key];
                    updateValueDisplay(key);
                    sliders[key].addEventListener('input', handleSliderChange);
                }

                // Set up button listeners
                buttons.monod.addEventListener('click', () => setModel('monod'));
                buttons.si.addEventListener('click', () => setModel('si'));
                buttons.bifurcation.addEventListener('click', () => setView('bifurcation'));
                buttons.nontrivial.addEventListener('click', () => setView('nontrivial'));

                // Initial plot
                updatePlot();
            }

            // --- EVENT HANDLERS ---
            function handleSliderChange(event) {
                const key = event.target.id.replace('slider-', '');
                params[key] = parseFloat(event.target.value);
                updateValueDisplay(key);
                updatePlot();
            }

            function setModel(model) {
                if (currentModel === model) return;
                currentModel = model;
                buttons.monod.classList.toggle('active', model === 'monod');
                buttons.si.classList.toggle('active', model === 'si');
                k1SliderContainer.classList.toggle('hidden', model === 'monod');
                updatePlot();
            }

            function setView(view) {
                if (currentView === view) return;
                currentView = view;
                buttons.bifurcation.classList.toggle('active', view === 'bifurcation');
                buttons.nontrivial.classList.toggle('active', view === 'nontrivial');
                updatePlot();
            }

            // --- HELPER FUNCTIONS ---
            function updateValueDisplay(key) {
                let value = params[key];
                let decimals;
                switch (key) {
                    case 'x2f': decimals = 1; break;
                    case 'k1': decimals = 4; break;
                    default: decimals = 2; break;
                }
                values[key].textContent = value.toFixed(decimals);
            }

            function linspace(start, end, num) {
                const step = (end - start) / (num - 1);
                return Array.from({ length: num }, (_, i) => start + i * step);
            }

            // --- PLOTTING LOGIC ---
            function updatePlot() {
                if (currentView === 'bifurcation') {
                    drawBifurcationDiagram();
                } else {
                    drawNontrivialStatePlot();
                }
            }

            function drawBifurcationDiagram() {
                const growthRateFunc = getGrowthRateFunc();
                const traces = [];
                const numPoints = 501;
                const x2_vals = linspace(1e-6, params.x2f, numPoints);
                const D_vals = x2_vals.map(growthRateFunc);

                // 1. Nontrivial Steady State Curve (D vs x2)
                if (currentModel === 'monod') {
                    traces.push({
                        x: D_vals, y: x2_vals,
                        mode: 'lines', name: 'Stable Nontrivial SS',
                        line: { color: 'blue', width: 2 }
                    });
                } else { // Substrate Inhibition
                    const x2_crit = Math.sqrt(params.km / params.k1);
                    const stable_x2 = x2_vals.filter(x => x <= x2_crit);
                    const unstable_x2 = x2_vals.filter(x => x >= x2_crit);

                    if (stable_x2.length > 0) {
                        traces.push({
                            x: stable_x2.map(growthRateFunc), y: stable_x2,
                            mode: 'lines', name: 'Stable Nontrivial SS',
                            line: { color: 'blue', width: 2 }
                        });
                    }
                    if (unstable_x2.length > 0) {
                         traces.push({
                            x: unstable_x2.map(growthRateFunc), y: unstable_x2,
                            mode: 'lines', name: 'Unstable Nontrivial SS',
                            line: { color: 'red', width: 2, dash: 'dash' }
                        });
                    }
                }

                // 2. Trivial Steady State Line (x2 = x2f)
                const D_bif = growthRateFunc(params.x2f);
                traces.push({ // Unstable part
                    x: [0, D_bif], y: [params.x2f, params.x2f],
                    mode: 'lines', name: 'Unstable Trivial SS',
                    line: { color: 'red', width: 2, dash: 'dash' }
                });
                traces.push({ // Stable part
                    x: [D_bif, 1.0], y: [params.x2f, params.x2f],
                    mode: 'lines', name: 'Stable Trivial SS',
                    line: { color: 'blue', width: 2 }
                });

                // 3. Intersection Points
                const intersection_x = [];
                const intersection_y = [];
                const intersection_colors = [];

                // Find nontrivial intersections
                for (let i = 1; i < numPoints; i++) {
                    if ((D_vals[i-1] - params.ds) * (D_vals[i] - params.ds) < 0) {
                        const x_prev = x2_vals[i-1], y_prev = D_vals[i-1];
                        const x_curr = x2_vals[i], y_curr = D_vals[i];
                        const x2_sol = x_prev + (params.ds - y_prev) * (x_curr - x_prev) / (y_curr - y_prev);

                        let is_stable = true;
                        if (currentModel === 'si') {
                            // Stability determined by dD/dx2 > 0
                            // Sign of dD/dx2 is sign of (km - k1*x2^2)
                            is_stable = (params.km - params.k1 * x2_sol * x2_sol) > 0;
                        }

                        intersection_x.push(params.ds);
                        intersection_y.push(x2_sol);
                        intersection_colors.push(is_stable ? 'magenta' : 'cyan');
                    }
                }
                if (intersection_x.length > 0) {
                    traces.push({
                        x: intersection_x, y: intersection_y,
                        mode: 'markers', name: 'Nontrivial Solutions',
                        marker: { color: intersection_colors, size: 10, symbol: 'diamond' }
                    });
                }

                // Trivial intersection
                traces.push({
                    x: [params.ds], y: [params.x2f],
                    mode: 'markers', name: 'Trivial Solution',
                    marker: { color: 'green', size: 12, symbol: 'circle' }
                });

                const layout = {
                    title: 'Bifurcation Diagram',
                    xaxis: { title: 'dilution rate', range: [0, 1.0], zeroline: false },
                    yaxis: { title: 'X<sub>2</sub>', range: [0, 6.0], zeroline: false },
                    shapes: [{
                        type: 'line',
                        x0: params.ds, x1: params.ds,
                        y0: 0, y1: 6.0,
                        line: { color: 'grey', width: 2 }
                    }],
                    showlegend: false,
                    margin: { t: 40, b: 40, l: 40, r: 20 },
                    hovermode: false
                };

                Plotly.newPlot(plotContainer, traces, layout, {responsive: true});
            }

            function drawNontrivialStatePlot() {
                if (currentModel === 'monod') {
                    // This view is less meaningful for Monod, but we'll show it
                    const x2_vals = linspace(1e-6, 7, 501);
                    const mu_vals = x2_vals.map(monodGrowthRate);
                    const traces = [{
                        x: x2_vals, y: mu_vals,
                        mode: 'lines', name: 'Growth Rate',
                        line: { color: 'blue', width: 2 }
                    }, {
                        x: [0, 7], y: [params.ds, params.ds],
                        mode: 'lines', name: 'Dilution Rate',
                        line: { color: 'gray', width: 2 }
                    }];
                    const layout = {
                        title: 'Specific Growth Rate (Monod)',
                        xaxis: { title: 'substrate concentration', range: [0, 7], zeroline: false },
                        yaxis: { title: 'specific growth rate coefficient', range: [0, 1], zeroline: false },
                        showlegend: false,
                        margin: { t: 40, b: 40, l: 60, r: 20 },
                         hovermode: false
                    };
                    Plotly.newPlot(plotContainer, traces, layout, {responsive: true});
                    return;
                }

                // Main logic for Substrate Inhibition
                const x2_vals = linspace(1e-6, 7, 501);
                const mu_vals = x2_vals.map(siGrowthRate);
                const traces = [];

                // 1. Growth curve
                traces.push({
                    x: x2_vals, y: mu_vals,
                    mode: 'lines', name: 'Growth Rate',
                    line: { color: 'blue', width: 2 }
                });

                // 2. Dilution rate line
                traces.push({
                    x: [0, 7], y: [params.ds, params.ds],
                    mode: 'lines', name: 'Dilution Rate',
                    line: { color: 'gray', width: 2 }
                });

                // 3. Bistability region lines and shading
                const x2_peak = Math.sqrt(params.km / params.k1);
                const D_upper = siGrowthRate(x2_peak);
                const D_lower = siGrowthRate(params.x2f);

                const shapes = [{ // Shaded region
                    type: 'rect',
                    xref: 'paper', yref: 'y',
                    x0: 0, x1: 1,
                    y0: D_lower, y1: D_upper,
                    fillcolor: 'rgba(255, 165, 0, 0.2)',
                    line: { width: 0 }
                }];

                traces.push({ // Upper bound line
                    x: [0, 7], y: [D_upper, D_upper],
                    mode: 'lines', name: 'Upper Bistability Bound',
                    line: { color: 'red', width: 2, dash: 'dash' }
                });
                traces.push({ // Lower bound line
                    x: [0, 7], y: [D_lower, D_lower],
                    mode: 'lines', name: 'Lower Bistability Bound',
                    line: { color: 'red', width: 2, dash: 'dash' }
                });

                // 4. Intersection Points
                const intersection_x = [];
                const intersection_y = [];
                const intersection_colors = [];

                for (let i = 1; i < mu_vals.length; i++) {
                    if ((mu_vals[i-1] - params.ds) * (mu_vals[i] - params.ds) < 0) {
                        const x_prev = x2_vals[i-1], y_prev = mu_vals[i-1];
                        const x_curr = x2_vals[i], y_curr = mu_vals[i];
                        const x2_sol = x_prev + (params.ds - y_prev) * (x_curr - x_prev) / (y_curr - y_prev);

                        const is_stable = x2_sol < x2_peak;

                        intersection_x.push(x2_sol);
                        intersection_y.push(params.ds);
                        intersection_colors.push(is_stable ? 'magenta' : 'cyan');
                    }
                }
                if (intersection_x.length > 0) {
                    traces.push({
                        x: intersection_x, y: intersection_y,
                        mode: 'markers', name: 'Nontrivial Solutions',
                        marker: { color: intersection_colors, size: 10, symbol: 'diamond' }
                    });
                }

                const layout = {
                    title: 'Specific Growth Rate (Substrate Inhibition)',
                    xaxis: { title: 'substrate concentration', range: [0, 7], zeroline: false },
                    yaxis: { title: 'specific growth rate coefficient', range: [0, 1], zeroline: false },
                    shapes: shapes,
                    showlegend: false,
                    margin: { t: 40, b: 40, l: 60, r: 20 },
                    hovermode: false
                };

                Plotly.newPlot(plotContainer, traces, layout, {responsive: true});
            }

            // Start the application
            init();
        });
    </script>
</body>
