            scene.add(sunVectorArrow);

            // Latitude lines
            const circleGeom = new THREE.BufferGeometry().setFromPoints(new THREE.Path().absarc(0, 0, EARTH_RADIUS, 0, Math.PI * 2, false).getSpacedPoints(128));
            equatorLine = new THREE.Line(circleGeom, new THREE.LineBasicMaterial({ color: 0x00ff00 }));
            earth.add(equatorLine);

            const dashedCircleGeom = new THREE.BufferGeometry().setFromPoints(new THREE.Path().absarc(0, 0, 1, 0, Math.PI * 2, false).getSpacedPoints(128));
            selectedLatLine = new THREE.Line(dashedCircleGeom, new THREE.LineDashedMaterial({ color: 0x0000ff, dashSize: 0.05, gapSize: 0.025 }));
            selectedLatLine.computeLineDistances();
            earth.add(selectedLatLine);

            animate();
        }

        function initPlotly() {
            const commonLayout = {
                margin: { t: 30, b: 40, l: 50, r: 20 },
                xaxis: { title: 'time (hr)', range: [0, 24], zeroline: false },
                showlegend: false,
                paper_bgcolor: 'rgba(0,0,0,0)',
                plot_bgcolor: 'rgba(0,0,0,0)',
            };

            Plotly.newPlot(plotInstantDiv, [{
                x: [], y: [], mode: 'lines', line: { color: 'red' } // equator
            }, {
                x: [], y: [], mode: 'lines', line: { color: 'black' } // selected lat
            }, {
                x: [], y: [], mode: 'lines', line: { color: 'blue' } // N pole
            }, {
                x: [], y: [], mode: 'markers', marker: { color: 'black', size: 10 } // marker
            }], { 
                ...commonLayout, 
                title: 'energy', 
                yaxis: { title: 'watts/m^2', range: [0, 1400] }
            }, { responsive: true, displayModeBar: false });

            Plotly.newPlot(plotCumulativeDiv, [{
                x: [], y: [], mode: 'lines', line: { color: 'red' }
            }, {
                x: [], y: [], mode: 'lines', line: { color: 'black' }
            }, {
                x: [], y: [], mode: 'lines', line: { color: 'blue' }
            }], { 
                ...commonLayout, 
                title: 'energy', 
                yaxis: { title: 'watts/m^2', range: [0, 14000] }
            }, { responsive: true, displayModeBar: false });
        }


        // --- UPDATE LOGIC ---

        function update() {
            // 1. Read values from sliders
            const season = parseFloat(sliderSeason.value);
            const latitude = parseFloat(sliderLatitude.value);
            const timeOfDay = parseFloat(sliderTime.value);

            // 2. Update value displays
            valueSeason.textContent = season.toFixed(3);
            valueLatitude.textContent = latitude.toFixed(4);
            valueTime.textContent = timeOfDay.toFixed(2);

            // 3. Physics Calculations
            const solarDeclinationRad = degToRad(23.5 * Math.cos(2 * Math.PI * season));
            const latRad = degToRad(latitude);
            const timeRad = (timeOfDay / 24) * 2 * Math.PI;

            // 4. Update 3D Globe
            earth.rotation.y = timeRad;

            const sunDirection = new THREE.Vector3(0, Math.sin(solarDeclinationRad), -Math.cos(solarDeclinationRad)).normalize();
            directionalLight.position.copy(sunDirection);

            // Update selected latitude line
            const latLineRadius = EARTH_RADIUS * Math.cos(latRad);
            const latLineY = EARTH_RADIUS * Math.sin(latRad);
            selectedLatLine.scale.set(latLineRadius, latLineRadius, 1);
            selectedLatLine.position.y = latLineY;

            // Update vectors (position is at 0 longitude relative to the rotating globe)
            const surfacePoint = new THREE.Vector3(
                EARTH_RADIUS * Math.cos(latRad),
                EARTH_RADIUS * Math.sin(latRad),
                0 
            );

            // The arrows are in world space, so we apply the earth's rotation to the point
            const worldSurfacePoint = surfacePoint.clone().applyEuler(earth.rotation);

            normalVectorArrow.position.copy(worldSurfacePoint);
            const normalDirection = worldSurfacePoint.clone().normalize();
            normalVectorArrow.setDirection(normalDirection);

            sunVectorArrow.position.copy(worldSurfacePoint);
            const toSunDirection = sunDirection.clone().multiplyScalar(-1);
            sunVectorArrow.setDirection(toSunDirection);

            // 5. Update Plots
            const hours = Array.from({ length: 101 }, (_, i) => i * 0.24); // 0 to 24 in small steps
            const latitudes = [0, latitude, 90]; // Equator, selected, N Pole
            const instantaneousData = [[], [], []];
            const cumulativeData = [[], [], []];

            latitudes.forEach((lat, index) => {
                let cumulativeEnergy = 0;
                const lat_rad = degToRad(lat);
                for (let i = 0; i < hours.length; i++) {
                    const h = hours[i];
                    const hraRad = degToRad(15 * (h - 12));

                    const cosZenith = Math.sin(lat_rad) * Math.sin(solarDeclinationRad) + 
                                    Math.cos(lat_rad) * Math.cos(solarDeclinationRad) * Math.cos(hraRad);

                    const instEnergy = SOLAR_CONSTANT * Math.max(0, cosZenith);
                    instantaneousData[index].push(instEnergy);

                    const timeStep = (i > 0) ? (hours[i] - hours[i-1]) : 0;
                    cumulativeEnergy += instEnergy * timeStep;
                    cumulativeData[index].push(cumulativeEnergy);
                }
            });

            // Update traces
            Plotly.react(plotInstantDiv, [{
                x: hours, y: instantaneousData[0]
            }, {
                x: hours, y: instantaneousData[1]
            }, {
                x: hours, y: instantaneousData[2]
            }, {
                x: [timeOfDay], y: [instantaneousData[1][Math.round(timeOfDay / 0.24)]]
            }]);

            Plotly.react(plotCumulativeDiv, [{
                x: hours, y: cumulativeData[0]
            }, {
                x: hours, y: cumulativeData[1]
            }, {
                x: hours, y: cumulativeData[2]
            }]);
        }


        // --- ANIMATION LOOP ---
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // --- EVENT LISTENERS ---
        sliderSeason.addEventListener('input', update);
        sliderLatitude.addEventListener('input', update);
        sliderTime.addEventListener('input', update);

        window.addEventListener('resize', () => {
            const aspect = globeContainer.clientWidth / globeContainer.clientHeight;
            camera.aspect = aspect;
            camera.updateProjectionMatrix();
            renderer.setSize(globeContainer.clientWidth, globeContainer.clientHeight);
        });


        // --- RUN ---
        initThree();
        initPlotly();
        update(); // Initial call to set up view from default slider values
    </script>
</body>
</html>
