          46: { a: {n: 69, d: 14}, b: {n: 1288, d: 21}, c: {n: 18017, d: 294} },
          47: { a: {n: 2115, d: 388}, b: {n: 12416, d: 105}, c: {n: 198083881, d: 40740} },
          53: { a: {n: 583, d: 84}, b: {n: 2352, d: 53}, c: {n: 69697, d: 4452} },
          54: { a: {n: 9, d: 1}, b: {n: 12, d: 1}, c: {n: 15, d: 1} },
          55: { a: {n: 11, d: 2}, b: {n: 20, d: 1}, c: {n: 41, d: 2} },
          61: { a: {n: 366, d: 5}, b: {n: 5, d: 3}, c: {n: 1103, d: 15} },
