            p5canvas.parent('canvas-container');

            slider = select('#slider-area');
            input = select('#input-area');

            slider.input(updateFromSlider);
            input.input(updateFromInput);

            noLoop();
            redraw();
        }

        function windowResized() {
            const canvasContainer = document.getElementById('canvas-container');
            const w = canvasContainer.offsetWidth;
            const h = canvasContainer.offsetHeight;
            resizeCanvas(w, h);
            redraw();
        }

        function updateFromSlider() {
            input.value(slider.value());
            redraw();
        }

        function updateFromInput() {
            slider.value(input.value());
            redraw();
        }

        function draw() {
            background(255);
            const n = parseInt(slider.value());
            const data = congruentData[n];

            if (data) {
                drawTriangle(data);
            } else {
                drawNotCongruentMessage(n);
            }
        }

        function drawTriangle(sides) {
            const val_a = sides.a.n / sides.a.d;
            const val_b = sides.b.n / sides.b.d;

            const padding = 100;
            const scaleFactor = min((width - padding) / val_a, (height - padding) / val_b);

            const scaled_a = val_a * scaleFactor;
            const scaled_b = val_b * scaleFactor;

            const x_offset = (width - scaled_a) / 2;
            const y_offset = (height + scaled_b) / 2;

            const v1 = { x: x_offset, y: y_offset };
            const v2 = { x: x_offset + scaled_a, y: y_offset };
            const v3 = { x: x_offset, y: y_offset - scaled_b };

            fill('#FFF2E5');
            stroke('#000000');
            strokeWeight(2);

            beginShape();
            vertex(v1.x, v1.y);
            vertex(v2.x, v2.y);
            vertex(v3.x, v3.y);
            endShape(CLOSE);

            // Draw labels
            const labelFontSize = min(16, width / 40);

            // Label for side 'a' (base)
            drawFraction(sides.a, v1.x + scaled_a / 2, v1.y + 40, 0, labelFontSize);

            // Label for side 'b' (height) - placed to the right
            drawFraction(sides.b, v2.x + 40, v2.y - scaled_b / 2, 0, labelFontSize);

            // Label for side 'c' (hypotenuse)
            const midHypotenuse = {
                x: (v2.x + v3.x) / 2,
                y: (v2.y + v3.y) / 2
            };
            const angle = atan2(v3.y - v2.y, v3.x - v2.x);
            const offsetDist = 40;
            const label_c_pos = {
                x: midHypotenuse.x - offsetDist * sin(angle),
                y: midHypotenuse.y + offsetDist * cos(angle)
            };
            drawFraction(sides.c, label_c_pos.x, label_c_pos.y, angle, labelFontSize);
        }

        function formatNumber(num) {
            return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
        }

        function drawFraction(fraction, x, y, rotation, fontSize) {
            push();
            translate(x, y);
            rotate(rotation);

            fill('#8B5A2B');
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(fontSize);

            const numStr = formatNumber(fraction.n);
            const denStr = formatNumber(fraction.d);

            const numWidth = textWidth(numStr);
            const denWidth = textWidth(denStr);
            const lineWidth = max(numWidth, denWidth) + 4;

            const lineY = 0;
            const textYOffset = fontSize * 0.7;

            text(numStr, 0, lineY - textYOffset);
            text(denStr, 0, lineY + textYOffset);

            stroke('#8B5A2B');
            strokeWeight(1);
            line(-lineWidth / 2, lineY, lineWidth / 2, lineY);

            pop();
        }

        function drawNotCongruentMessage(n) {
            fill('#FF0000');
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(20);
            text(`${n} is not a congruent number`, width / 2, height / 2);
        }
    </script>
