            alpha: document.getElementById('label-alpha'),
            beta: document.getElementById('label-beta'),
            w: document.getElementById('label-w'),
            r: document.getElementById('label-r'),
            Q: document.getElementById('label-Q'),
            C: document.getElementById('label-C')
        };
        const buttons = {
            reset: document.getElementById('btn-reset'),
            qFromDual: document.getElementById('btn-Q-from-dual'),
            cFromPrimal: document.getElementById('btn-C-from-primal')
        };
        const outputs = {
            primal: document.getElementById('output-primal'),
            dual: document.getElementById('output-dual')
        };
        const plotContainers = {
            primal: document.getElementById('plot-primal'),
            dual: document.getElementById('plot-dual')
        };

        const defaultValues = {
            alpha: 0.5, beta: 0.5, w: 1.0, r: 1.0, Q: 3.0, C: 4.0
        };

        let state = { ...defaultValues };
        let results = {
            primal: { x: 0, y: 0, C: 0 },
            dual: { x: 0, y: 0, Q: 0 }
        };

        // --- Three.js Scenes ---
        const scenes = { primal: null, dual: null };
        const cameras = { primal: null, dual: null };
        const renderers = { primal: null, dual: null };
        const controls = { primal: null, dual: null };
        const plotObjects = {
            primal: {},
            dual: {}
        };
        const BOUNDING_BOX_SIZE = 10;
        const SEGMENTS = 50;

        function createScene(container) {
            const scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            const camera = new THREE.PerspectiveCamera(50, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.set(12, 10, 15);

            const renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            container.appendChild(renderer.domElement);

            const controls = new OrbitControls(camera, renderer.domElement);
            controls.target.set(BOUNDING_BOX_SIZE / 2, BOUNDING_BOX_SIZE / 2, BOUNDING_BOX_SIZE / 2);
            controls.update();

            scene.add(new THREE.AmbientLight(0xffffff, 0.7));
            const light = new THREE.DirectionalLight(0xffffff, 0.5);
            light.position.set(1, 1, 1);
            scene.add(light);

            const box = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(BOUNDING_BOX_SIZE, BOUNDING_BOX_SIZE, BOUNDING_BOX_SIZE)), 0xaaaaaa);
            box.position.set(BOUNDING_BOX_SIZE/2, BOUNDING_BOX_SIZE/2, BOUNDING_BOX_SIZE/2);
            scene.add(box);

            const axesHelper = new THREE.AxesHelper(BOUNDING_BOX_SIZE * 1.2);
            scene.add(axesHelper);

            return { scene, camera, renderer, controls };
        }

        function init() {
            // Primal Scene
            const primalSetup = createScene(plotContainers.primal);
            scenes.primal = primalSetup.scene;
            cameras.primal = primalSetup.camera;
            renderers.primal = primalSetup.renderer;
            controls.primal = primalSetup.controls;

            plotObjects.primal.isoquantCurtain = new THREE.Mesh(new THREE.BufferGeometry(), new THREE.MeshStandardMaterial({ color: 0xffa500, side: THREE.DoubleSide, transparent: true, opacity: 0.8 }));
            plotObjects.primal.isocostPlane = new THREE.Mesh(new THREE.PlaneGeometry(30, 30), new THREE.MeshStandardMaterial({ color: 0x808080, side: THREE.DoubleSide, transparent: true, opacity: 0.6 }));
            plotObjects.primal.solutionVector = new THREE.Line(new THREE.BufferGeometry(), new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 3 }));
            scenes.primal.add(plotObjects.primal.isoquantCurtain, plotObjects.primal.isocostPlane, plotObjects.primal.solutionVector);

            // Dual Scene
            const dualSetup = createScene(plotContainers.dual);
            scenes.dual = dualSetup.scene;
            cameras.dual = dualSetup.camera;
            renderers.dual = dualSetup.renderer;
            controls.dual = dualSetup.controls;

            plotObjects.dual.productionSurface = new THREE.Mesh(new THREE.BufferGeometry(), new THREE.MeshStandardMaterial({ color: 0xffa500, side: THREE.DoubleSide }));
            plotObjects.dual.budgetCurtain = new THREE.Mesh(new THREE.BufferGeometry(), new THREE.MeshStandardMaterial({ color: 0x808080, side: THREE.DoubleSide, transparent: true, opacity: 0.6 }));
            plotObjects.dual.intersectionCurve = new THREE.Line(new THREE.BufferGeometry(), new THREE.LineBasicMaterial({ color: 0xff0000, linewidth: 2 }));
            plotObjects.dual.optimalPoint = new THREE.Mesh(new THREE.SphereGeometry(0.15), new THREE.MeshStandardMaterial({ color: 0x555555 }));
            scenes.dual.add(plotObjects.dual.productionSurface, plotObjects.dual.budgetCurtain, plotObjects.dual.intersectionCurve, plotObjects.dual.optimalPoint);

            // Event Listeners
            Object.values(sliders).forEach(slider => slider.addEventListener('input', update));
            buttons.reset.addEventListener('click', resetToDefaults);
            buttons.cFromPrimal.addEventListener('click', setCFromPrimal);
            buttons.qFromDual.addEventListener('click', setQFromDual);

            window.addEventListener('resize', onWindowResize, false);

            update();
            animate();
        }

        function update() {
            // 1. Read values from sliders
            for (const key in sliders) {
                state[key] = parseFloat(sliders[key].value);
                labels[key].textContent = state[key].toFixed(key.length === 1 ? (key === 'Q' || key === 'C' ? 1 : 2) : 2);
            }

            // 2. Solve problems
            solvePrimal();
            solveDual();

            // 3. Update output displays
            outputs.primal.textContent = `x = ${results.primal.x.toFixed(2)}, y = ${results.primal.y.toFixed(2)}, C = ${results.primal.C.toFixed(2)}`;
            outputs.dual.textContent = `x = ${results.dual.x.toFixed(2)}, y = ${results.dual.y.toFixed(2)}, Q = ${results.dual.Q.toFixed(2)}`;

            // 4. Update visualizations
            updatePrimalPlot();
            updateDualPlot();
        }

        function solvePrimal() {
            const { alpha, beta, w, r, Q } = state;
            const k = (w * beta) / (r * alpha);
            const x = Math.pow(Q / Math.pow(k, beta), 1 / (alpha + beta));
            const y = x * k;
            const C = w * x + r * y;
            results.primal = { x, y, C };
        }

        function solveDual() {
            const { alpha, beta, w, r, C } = state;
            const x = (C * alpha) / (w * (alpha + beta));
            const y = (C * beta) / (r * (alpha + beta));
            const Q = Math.pow(x, alpha) * Math.pow(y, beta);
            results.dual = { x, y, Q };
        }

        function updatePrimalPlot() {
            const { alpha, beta, w, r, Q } = state;
            const { x: x_p, y: y_p, C: C_out } = results.primal;

            // Isoquant Curtain
            const vertices = [];
            const indices = [];
            let pointCount = 0;
            const y_func = x => Math.pow(Q / Math.pow(x, alpha), 1 / beta);

            for (let i = 0; i <= SEGMENTS; i++) {
                const x = (i / SEGMENTS) * BOUNDING_BOX_SIZE + 0.01;
                const y = y_func(x);
                if (y > 0 && y < BOUNDING_BOX_SIZE) {
                    vertices.push(x, y, 0);
                    vertices.push(x, y, BOUNDING_BOX_SIZE);
                    if (pointCount > 0) {
                        const idx = pointCount * 2;
                        indices.push(idx - 2, idx - 1, idx);
                        indices.push(idx - 1, idx + 1, idx);
                    }
                    pointCount++;
                }
            }
            plotObjects.primal.isoquantCurtain.geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            plotObjects.primal.isoquantCurtain.geometry.setIndex(indices);
            plotObjects.primal.isoquantCurtain.geometry.computeVertexNormals();

            // Isocost Plane C = w*x + r*y
            const plane = plotObjects.primal.isocostPlane;
            plane.geometry.dispose();
            plane.geometry = new THREE.PlaneGeometry(30, 30);
            plane.position.set(0, 0, 0); // Position is at origin
            plane.rotation.set(0, 0, 0); // Reset rotation
            const normal = new THREE.Vector3(w, r, -1).normalize();
            const quaternion = new THREE.Quaternion().setFromUnitVectors(new THREE.Vector3(0, 0, 1), normal);
            plane.quaternion.copy(quaternion);

            // Solution Vector
            const points = [new THREE.Vector3(0, 0, 0), new THREE.Vector3(x_p, y_p, C_out)];
            plotObjects.primal.solutionVector.geometry.setFromPoints(points);
        }

        function updateDualPlot() {
            const { alpha, beta, w, r, C } = state;
            const { x: x_d, y: y_d, Q: Q_out } = results.dual;

            // Production Surface
            const prodFunc = (u, v, target) => {
                const x = u * BOUNDING_BOX_SIZE;
                const y = v * BOUNDING_BOX_SIZE;
                const z = Math.pow(x, alpha) * Math.pow(y, beta);
                target.set(x, y, z);
            };
            plotObjects.dual.productionSurface.geometry.dispose();
            plotObjects.dual.productionSurface.geometry = new ParametricGeometry(prodFunc, SEGMENTS, SEGMENTS);

            // Budget Curtain
            const vertices = [];
            const indices = [];
            let pointCount = 0;
            const y_func = x => (C - w * x) / r;

            for (let i = 0; i <= SEGMENTS; i++) {
                const x = (i / SEGMENTS) * (C / w);
                const y = y_func(x);
                 if (x >= 0 && x <= BOUNDING_BOX_SIZE && y >= 0 && y <= BOUNDING_BOX_SIZE) {
                    vertices.push(x, y, 0);
                    vertices.push(x, y, BOUNDING_BOX_SIZE * 1.5); // make it tall enough
                    if (pointCount > 0) {
                        const idx = pointCount * 2;
                        indices.push(idx - 2, idx, idx - 1);
                        indices.push(idx - 1, idx, idx + 1);
                    }
                    pointCount++;
                }
            }
            plotObjects.dual.budgetCurtain.geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            plotObjects.dual.budgetCurtain.geometry.setIndex(indices);
            plotObjects.dual.budgetCurtain.geometry.computeVertexNormals();

            // Intersection Curve
            const curvePoints = [];
            for (let i = 0; i <= SEGMENTS; i++) {
                const x = (i / SEGMENTS) * (C / w);
                const y = y_func(x);
                if (x >= 0 && y >= 0) {
                    const q_val = Math.pow(x, alpha) * Math.pow(y, beta);
                    if (!isNaN(q_val)) {
                       curvePoints.push(new THREE.Vector3(x, y, q_val));
                    }
                }
            }
            plotObjects.dual.intersectionCurve.geometry.setFromPoints(curvePoints);

            // Optimal Point
            plotObjects.dual.optimalPoint.position.set(x_d, y_d, Q_out);
        }

        function resetToDefaults() {
            for (const key in defaultValues) {
                sliders[key].value = defaultValues[key];
            }
            update();
        }

        function setCFromPrimal() {
            const newC = Math.max(parseFloat(sliders.C.min), Math.min(parseFloat(sliders.C.max), results.primal.C));
            sliders.C.value = newC;
            update();
        }

        function setQFromDual() {
            const newQ = Math.max(parseFloat(sliders.Q.min), Math.min(parseFloat(sliders.Q.max), results.dual.Q));
            sliders.Q.value = newQ;
            update();
        }

        function onWindowResize() {
            for (const key in cameras) {
                const container = plotContainers[key];
                cameras[key].aspect = container.clientWidth / container.clientHeight;
                cameras[key].updateProjectionMatrix();
                renderers[key].setSize(container.clientWidth, container.clientHeight);
            }
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.primal.update();
            controls.dual.update();
            renderers.primal.render(scenes.primal, cameras.primal);
            renderers.dual.render(scenes.dual, cameras.dual);
        }

        init();
    </script>

</body>
</html>
