    </div>

    <script>
        // --- GLOBAL VARIABLES & CONSTANTS ---
        let sliderPressure, sliderHeat;
        let pressureValue, heatValue;
        let flashResults;

        const FEED = {
            F: 10,    // kmol/hr
            T_F: 323.2, // K
            P_F: 1000,  // kPa
            z: [0.25, 0.25, 0.25, 0.25] // n-butane, n-pentane, n-heptane, n-octane
        };

        const COMPONENTS = [
            { name: "n-butane",  A: 6.80165, B: 1005.157, C: -27.159, Cp_L: 132, dH_vap: 22400 },
            { name: "n-pentane", A: 6.86177, B: 1184.249, C: -35.223, Cp_L: 161, dH_vap: 25800 },
            { name: "n-heptane", A: 6.90237, B: 1358.59,  C: -51.303, Cp_L: 225, dH_vap: 31800 },
            { name: "n-octane",  A: 6.92383, B: 1431.149, C: -59.501, Cp_L: 255, dH_vap: 34400 }
        ];

        // --- P5.JS SKETCH ---
        function setup() {
            let canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');

            sliderPressure = select('#slider-pressure');
            sliderHeat = select('#slider-heat');
            pressureValue = select('#pressure-value');
            heatValue = select('#heat-value');

            sliderPressure.input(updateSimulation);
            sliderHeat.input(updateSimulation);

            // Initial calculation and draw
            updateSimulation();
            noLoop();
        }

        function draw() {
            background('#F0F0F0');

            if (!flashResults) return; // Don't draw if results aren't ready

            // --- DRAW DIAGRAM ELEMENTS ---
            // Flash Vessel
            push();
            fill('#E6E6FA');
            stroke(0);
            strokeWeight(2);
            rectMode(CENTER);
            rect(400, 250, 100, 100);
            fill(50);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(20);
            text("Flash", 400, 250);
            pop();

            // Arrows
            drawArrow(100, 250, 350, 250, '#9ACD32', 3); // Feed
            drawArrow(400, 200, 400, 100, '#9370DB', 3); // Vapor (up)
            drawArrow(400, 100, 550, 100, '#9370DB', 3); // Vapor (right)
            drawArrow(400, 300, 400, 400, '#FFA500', 3); // Liquid (down)
            drawArrow(400, 400, 550, 400, '#FFA500', 3); // Liquid (right)

            // Dashed Heat Arrow
            drawDashedArrow(300, 170, 375, 225, '#FF0000', 2);


            // --- DRAW TEXT LABELS ---
            noStroke();
            textSize(12);

            // Feed Info
            fill('#9ACD32');
            textAlign(LEFT, TOP);
            textStyle(BOLD);
            text("feed", 150, 200);
            textStyle(NORMAL);
            text(
`temperature = ${FEED.T_F}
pressure = ${FEED.P_F}
n-butane = ${FEED.z[0].toFixed(3)}
n-pentane = ${FEED.z[1].toFixed(3)}
n-heptane = ${FEED.z[2].toFixed(3)}
n-octane = ${FEED.z[3].toFixed(3)}`, 150, 220);

            // Vapor Info
            fill('#9370DB');
            textAlign(LEFT, TOP);
            textStyle(BOLD);
            text("vapor mole fraction:", 560, 80);
            textStyle(NORMAL);
            text(
`n-butane = ${flashResults.y[0].toFixed(3)}
n-pentane = ${flashResults.y[1].toFixed(3)}
n-heptane = ${flashResults.y[2].toFixed(3)}
n-octane = ${flashResults.y[3].toFixed(3)}`, 560, 100);

            // Liquid Info
            fill('#FFA500');
            textAlign(LEFT, TOP);
            textStyle(BOLD);
            text("liquid mole fraction:", 560, 380);
            textStyle(NORMAL);
            text(
`n-butane = ${flashResults.x[0].toFixed(3)}
n-pentane = ${flashResults.x[1].toFixed(3)}
n-heptane = ${flashResults.x[2].toFixed(3)}
n-octane = ${flashResults.x[3].toFixed(3)}`, 560, 400);

            // Process Info
            fill(50);
            textAlign(LEFT, TOP);
            text(
`vapor fraction = ${flashResults.V_frac.toFixed(3)}
flash temperature = ${flashResults.T_flash.toFixed(1)}
flash pressure = ${sliderPressure.value()}`, 560, 260);

            // Heat Info
            fill('#FF0000');
            text(`Q = ${sliderHeat.value()}`, 270, 150);
        }

        // --- DRAWING HELPER FUNCTIONS ---
        function drawArrow(x1, y1, x2, y2, color, sw) {
            push();
            stroke(color);
            strokeWeight(sw);
            line(x1, y1, x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            translate(x2, y2);
            rotate(angle);
            fill(color);
            triangle(0, 0, -10, -5, -10, 5);
            pop();
        }

        function drawDashedArrow(x1, y1, x2, y2, color, sw) {
             push();
             stroke(color);
             strokeWeight(sw);
             drawingContext.setLineDash([5, 5]);
             line(x1, y1, x2, y2);
             drawingContext.setLineDash([]);
             let angle = atan2(y2 - y1, x2 - x1);
             translate(x2, y2);
             rotate(angle);
             fill(color);
             noStroke();
             triangle(0, 0, -10, -5, -10, 5);
             pop();
        }

        // --- INTERACTION & CALCULATION ---
        function updateSimulation() {
            let P = Number(sliderPressure.value());
            let Q = Number(sliderHeat.value());

            pressureValue.html(P);
            heatValue.html(Q);

            flashResults = calculateFlash(P, Q);
            redraw();
        }

        // --- THERMODYNAMIC CALCULATION ENGINE ---

        // Bisection method root finder
        function bisection(func, a, b, tol = 1e-5, maxIter = 50) {
            let fa = func(a);
            let fb = func(b);
            if (fa * fb >= 0) {
                // Return boundary if root is at the edge or no root in interval
                if (Math.abs(fa) < tol) return a;
                if (Math.abs(fb) < tol) return b;
                return null; 
            }

            let c = a;
            for (let i = 0; i < maxIter; i++) {
                c = (a + b) / 2;
                let fc = func(c);
                if (Math.abs(fc) < tol || (b - a) / 2 < tol) {
                    return c;
                }
                if (fc * fa < 0) {
                    b = c;
                } else {
                    a = c;
                    fa = fc;
                }
            }
            return c;
        }

        // Antoine equation: P_sat (kPa) for T (K)
        function antoine(T, comp) {
            return 10**(comp.A - comp.B / (T + comp.C));
        }

        // Rachford-Rice equation sum
        function rachfordRiceSum(V, T, P_flash) {
            let sum = 0;
            for (let i = 0; i < COMPONENTS.length; i++) {
                const Ki = antoine(T, COMPONENTS[i]) / P_flash;
                sum += FEED.z[i] * (Ki - 1) / (1 + V * (Ki - 1));
            }
            return sum;
        }

        function calculateFlash(P_flash, Q_hr) {
            // Objective function for the outer loop (energy balance)
            const energyBalanceResidual = (T) => {
                // Inner loop: Solve Rachford-Rice for V at a given T
                const rr_func = (V) => rachfordRiceSum(V, T, P_flash);

                let V = 0;
                // Check if a two-phase system is possible at this T
                const f0 = rr_func(0); // sum(z(K-1))
                const f1 = rr_func(1); // sum(z(K-1)/K)
                if (f0 > 0 && f1 < 0) {
                    // Two-phase region, find V by bisection
                    V = bisection(rr_func, 0, 1);
                } else if (f0 <= 0) {
                    // Subcooled liquid
                    V = 0;
                } else { // f1 >= 0
                    // Superheated vapor
                    V = 1;
                }
                if (V === null) V = 0; // handle solver failure

                // With T and V, calculate enthalpy terms
                let K = [], y = [];
                let H_vap_term = 0;
                for (let i = 0; i < COMPONENTS.length; i++) {
                    K[i] = antoine(T, COMPONENTS[i]) / P_flash;
                    let x_i = FEED.z[i] / (1 + V * (K[i] - 1));
                    y[i] = K[i] * x_i;
                }

                // Recalculate y to ensure it sums to 1 (important for energy balance accuracy)
                const y_sum = y.reduce((a, b) => a + b, 0);
                if (y_sum > 0) {
                    y = y.map(val => val / y_sum);
                }

                for (let i = 0; i < COMPONENTS.length; i++) {
                    H_vap_term += y[i] * COMPONENTS[i].dH_vap;
                }

                let H_feed_sensible = 0;
                for (let i = 0; i < COMPONENTS.length; i++) {
                    H_feed_sensible += FEED.z[i] * COMPONENTS[i].Cp_L * (FEED.T_F - T);
                }

                let H_vaporization = V * H_vap_term;

                return (Q_hr / FEED.F) + H_feed_sensible - H_vaporization;
            };

            // Outer loop: Solve energy balance for T
            let T_flash = bisection(energyBalanceResidual, 250, 450);
            if (T_flash === null) { T_flash = FEED.T_F; } // Fallback

            // --- Final calculation with the found T_flash ---
            const rr_func_final = (V) => rachfordRiceSum(V, T_flash, P_flash);
            let V_frac = 0;
            const f0 = rr_func_final(0);
            const f1 = rr_func_final(1);

            if (f0 > 0 && f1 < 0) {
                 V_frac = bisection(rr_func_final, 0, 1);
            } else if (f0 <= 0) {
                 V_frac = 0;
            } else {
                 V_frac = 1;
            }
            if (V_frac === null) V_frac = 0; // Fallback

            let x = [], y = [], K = [];
            for (let i = 0; i < COMPONENTS.length; i++) {
                K[i] = antoine(T_flash, COMPONENTS[i]) / P_flash;
                x[i] = FEED.z[i] / (1 + V_frac * (K[i] - 1));
                y[i] = K[i] * x[i];
            }

            // Normalize mole fractions for display to prevent rounding errors
            const x_sum = x.reduce((a,b) => a+b, 0);
            const y_sum = y.reduce((a,b) => a+b, 0);
            if(x_sum > 0) x = x.map(val => val / x_sum);
            if(y_sum > 0) y = y.map(val => val / y_sum);

            return {
                T_flash: T_flash,
                V_frac: V_frac,
                x: x,
                y: y
            };
        }

    </script>
</body>
