                const x = Math.random() * 1024;
                const y = Math.random() * 1024;
                const r = Math.random() * 4 + 1;
                const c = Math.floor(Math.random() * 50 + 130);
                ctx.fillStyle = `rgb(${c},${c},${c})`;
                ctx.beginPath();
                ctx.arc(x, y, r, 0, Math.PI * 2);
                ctx.fill();
            }
            return new THREE.CanvasTexture(canvas);
        }
