        });

        // --- 6. Helper Functions & Animation Loop ---
        function updateEarthScale() {
            const scale = params.realisticSize ? 0.3 : 1;
            earth.scale.set(scale, scale, scale);
        }

        function updateAnimatedSliders(delta) {
            const speed = 20 * delta;
            const radSpeed = Math.PI * delta / 4;

            if (isAnimating.time) {
                let val = parseFloat(sliderTime.value) + speed;
                if (val > sliderTime.max) val = parseFloat(sliderTime.min);
                sliderTime.value = val;
                sliderTime.dispatchEvent(new Event('input'));
            }
            if (isAnimating.nodalAngle) {
                let val = parseFloat(sliderNodalAngle.value) + radSpeed;
                if (val > sliderNodalAngle.max) val = parseFloat(sliderNodalAngle.min);
                sliderNodalAngle.value = val;
                sliderNodalAngle.dispatchEvent(new Event('input'));
            }
            if (isAnimating.sunEarthAngle) {
                let val = parseFloat(sliderSunEarthAngle.value) + radSpeed;
                if (val > sliderSunEarthAngle.max) val = parseFloat(sliderSunEarthAngle.min);
                sliderSunEarthAngle.value = val;
                sliderSunEarthAngle.dispatchEvent(new Event('input'));
            }
            if (isAnimating.zodiac) {
                let val = parseFloat(sliderZodiac.value) + radSpeed;
                if (val > sliderZodiac.max) val = parseFloat(sliderZodiac.min);
                sliderZodiac.value = val;
                sliderZodiac.dispatchEvent(new Event('input'));
            }
        }

        function animate() {
            requestAnimationFrame(animate);
            const delta = clock.getDelta();

            updateAnimatedSliders(delta);

            // Update scene based on params
            const earthOrbitRadius = 150;
            const earthAngle = (params.time / 27.32) * Math.PI * 2; // Sidereal period

            // Earth Position
            const earthPos = new THREE.Vector3(
                Math.cos(earthAngle) * earthOrbitRadius,
                0,
                -Math.sin(earthAngle) * earthOrbitRadius
            );
            // Apply nodal angle tilt (rotation around Z axis)
            earthPos.applyAxisAngle(new THREE.Vector3(0, 0, 1), params.nodalAngle);
            earth.position.copy(earthPos);

            // Sun Light Position
            const sunAngle = earthAngle + params.sunEarthAngle;
            sunLight.position.set(
                Math.cos(sunAngle) * 2000,
                Math.sin(params.nodalAngle) * 500, // Give some inclination to sun
                -Math.sin(sunAngle) * 2000
            );

            // Zodiac Rotation
            constellationGroup.rotation.y = params.zodiac;

            // Eclipse Check
            const moonPos = new THREE.Vector3(0, 0, 0); // Moon is at origin
            const sunToMoonDir = moonPos.clone().sub(sunLight.position).normalize();
            const earthToMoonDir = moonPos.clone().sub(earth.position).normalize();
            const dot = sunToMoonDir.dot(earthToMoonDir);

            if (dot > 0.998) { // Earth is between Sun and Moon
                earth.material = earthEclipseMat;
                atmosphere.visible = false;
            } else {
                earth.material = earthMat;
                atmosphere.visible = true;
            }

            renderer.render(scene, camera);
        }

        // --- 7. Initial Call ---
        // Manually set initial text content for sliders not starting at 0
        sliderNodalAngleValue.textContent = parseFloat(sliderNodalAngle.value).toFixed(2);
        if (sliderSunEarthAngle.value === "1.571") {
            // keep MathJax
        } else {
             sliderSunEarthAngleValue.textContent = parseFloat(sliderSunEarthAngle.value).toFixed(2);
        }
        sliderZodiacValue.textContent = parseFloat(sliderZodiac.value).toFixed(2);

        setView('land'); // Set initial view
        updateEarthScale();
        animate();
    });
</script>

</body>
</html>
