<body>

    <div id="main-container">
        <div id="control-panel">
            <!-- Slider 1: Initial Angle -->
            <div class="control-row">
                <label for="slider-theta0">θ<sub>0</sub></label>
                <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" value="0.5236" step="0.01">
                <span id="value-theta0" class="value-display">π/6</span>
            </div>

            <!-- Slider 2: Initial Velocity -->
            <div class="control-row">
                <label for="slider-v0">v<sub>0</sub></label>
