            <div class="control-row">
                <label for="slider-length">connector length</label>
                <input type="range" id="slider-length" min="0.5" max="4" value="2" step="0.1">
                <span id="value-length" class="value-display">2</span>
            </div>

            <!-- Animation Controls -->
            <div class="control-row">
                <label>animate</label>
                <div class="button-group">
                    <button id="btn-play-pause">▶</button>
                    <button id="btn-reset">|&lt;</button>
                </div>
            </div>
        </div>
        <div id="visualization-section"></div>
    </div>

    <script>
        // --- p5.js Sketch ---

        // DOM element references
        let sliderTheta0, sliderV0, sliderLength;
        let valueTheta0, valueV0, valueLength;
        let btnPlayPause, btnReset;

        // Physics variables
        let theta, theta_dot, theta_ddot;
        let x, x_dot;
        let t;

        // Initial conditions from sliders
        let theta0, v0, l;

        // Constants
        const g = 9.8; // Gravity
        const m = 1;   // Bob mass
        const M = 1;   // Pivot mass
        const scale = 50; // Pixels per meter
        const dt = 0.05; // Time step for integration

        // Animation state
        let isPlaying = false;

        function setup() {
            const canvas = createCanvas(500, 500);
            canvas.parent('visualization-section');
            rectMode(CENTER);

            // Get references to all HTML controls
            sliderTheta0 = document.getElementById('slider-theta0');
            sliderV0 = document.getElementById('slider-v0');
            sliderLength = document.getElementById('slider-length');

            valueTheta0 = document.getElementById('value-theta0');
            valueV0 = document.getElementById('value-v0');
            valueLength = document.getElementById('value-length');

            btnPlayPause = document.getElementById('btn-play-pause');
            btnReset = document.getElementById('btn-reset');

            // Attach event listeners
            sliderTheta0.addEventListener('input', handleSliderChange);
            sliderV0.addEventListener('input', handleSliderChange);
            sliderLength.addEventListener('input', handleSliderChange);

            btnPlayPause.addEventListener('click', togglePlayPause);
            btnReset.addEventListener('click', resetSimulation);

            // Initialize the simulation with default slider values
            resetSimulation();
        }

        function draw() {
            background('#F0F0F0');

            if (isPlaying) {
                updatePhysics();
            }

            drawVisualization();
        }

        function updatePhysics() {
            // Equation of motion for theta
            let num = -m * l * sin(theta) * cos(theta) * pow(theta_dot, 2) - g * (M + m) * sin(theta);
            let den = l * (M + m * pow(sin(theta), 2));
            theta_ddot = num / den;

            // Numerical integration (Euler-Cromer)
            theta_dot += theta_ddot * dt;
            theta += theta_dot * dt;

            // Conservation of horizontal momentum
            // M*x_dot + m*(x_dot + d/dt(l*sin(theta))) = (M+m)*v0_pivot_initial
            // (M+m)*x_dot + m*l*cos(theta)*theta_dot = (M+m)*v0 
            // x_dot = v0 - (m*l*cos(theta)*theta_dot) / (M+m)
            x_dot = v0 - (m * l * cos(theta) * theta_dot) / (M + m);
            x += x_dot * dt;

            t += dt;
        }

        function drawVisualization() {
            // Calculate screen coordinates from physics units
            const pivotX = width / 2 + x * scale;
            const pivotY = height / 2;

            const bobX = pivotX + (l * scale) * sin(theta);
            const bobY = pivotY + (l * scale) * cos(theta); // Y is down in p5.js

            // Draw connector rod
            stroke('black');
            strokeWeight(2);
            line(pivotX, pivotY, bobX, bobY);

            // Draw pivot
            noStroke();
            fill('red');
            rect(pivotX, pivotY, 40, 20);

            // Draw bob
            stroke('black');
            strokeWeight(2);
            fill('orange');
            circle(bobX, bobY, 25);
        }

        function resetSimulation() {
            // Read initial conditions from sliders
            theta0 = parseFloat(sliderTheta0.value);
            v0 = parseFloat(sliderV0.value);
            l = parseFloat(sliderLength.value);

            // Reset physics state variables
            theta = theta0;
            theta_dot = 0;
            theta_ddot = 0;
            x = 0;
            x_dot = v0; // Initial velocity of the whole system
            t = 0;

            // Reset animation state
            isPlaying = false;
            btnPlayPause.textContent = "▶";

            // Update display values
            updateValueDisplays();
        }

        function handleSliderChange() {
            // When any slider changes, stop and reset the simulation
            resetSimulation();
        }

        function updateValueDisplays() {
            valueTheta0.textContent = formatTheta(parseFloat(sliderTheta0.value));
            valueV0.textContent = parseFloat(sliderV0.value).toFixed(1);
            valueLength.textContent = parseFloat(sliderLength.value).toFixed(1);
        }

        function togglePlayPause() {
            isPlaying = !isPlaying;
            if (isPlaying) {
                btnPlayPause.textContent = "||";
            } else {
                btnPlayPause.textContent = "▶";
            }
        }

        function formatTheta(rad) {
            const tolerance = 0.015;
            const fractions = {
                'π': Math.PI, '-π': -Math.PI,
                '2π/3': 2 * Math.PI / 3, '-2π/3': -2 * Math.PI / 3,
                'π/2': Math.PI / 2, '-π/2': -Math.PI / 2,
                'π/3': Math.PI / 3, '-π/3': -Math.PI / 3,
                'π/6': Math.PI / 6, '-π/6': -Math.PI / 6,
                '0': 0,
            };

            for (const [key, value] of Object.entries(fractions)) {
                if (Math.abs(rad - value) < tolerance) {
                    return key === '0' ? '0.' : key;
                }
            }
            return rad.toFixed(2); // Fallback to decimal
        }

    </script>
</body>
</html>
