    </script>

    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // --- DOM Elements ---
        const sliders = {
            strike: document.getElementById('slider-strike'),
            dip: document.getElementById('slider-dip'),
            slipAngle: document.getElementById('slider-slip-angle'),
            slipAmount: document.getElementById('slider-slip-amount')
        };
        const values = {
            strike: document.getElementById('value-strike'),
            dip: document.getElementById('value-dip'),
            slipAngle: document.getElementById('value-slip-angle'),
            slipAmount: document.getElementById('value-slip-amount')
        };
        const btnReset = document.getElementById('btn-reset');
        const checkboxBox = document.getElementById('checkbox-box');
        const canvas = document.getElementById('webgl-canvas');
        const canvasContainer = document.getElementById('canvas-container');

        // --- Defaults ---
        const DEFAULTS = {
            strike: 180,
            dip: 60,
            slipAngle: 90,
            slipAmount: 0.5
        };

        // --- 3D Scene Setup ---
        let scene, camera, renderer, controls;
        let sphereMesh, boxHelper;
        let originalPositions;
        let pLabel, tLabel, uLabel;

        const DEFORMATION_SCALE = 0.3;
        const COLOR_COMPRESSIVE = new THREE.Color(0xffffff); // White
        const COLOR_DILATATIONAL = new THREE.Color(0xffa500); // Orange

        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Camera
            const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 100);
            camera.position.set(0, 2, 4);
            camera.lookAt(scene.position);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.1;
            controls.screenSpacePanning = false;
            controls.minDistance = 2;
            controls.maxDistance = 10;

            // Lighting
            scene.add(new THREE.AmbientLight(0x888888));
            const dirLight1 = new THREE.DirectionalLight(0xffffff, 1.5);
            dirLight1.position.set(5, 5, 5);
            scene.add(dirLight1);
            const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight2.position.set(-5, -5, -5);
            scene.add(dirLight2);

            // Sphere
            const sphereGeom = new THREE.SphereGeometry(1, 64, 32);
            sphereGeom.setAttribute('color', new THREE.BufferAttribute(new Float32Array(sphereGeom.attributes.position.count * 3), 3));
            originalPositions = sphereGeom.attributes.position.clone();

            const sphereMat = new THREE.MeshStandardMaterial({
                vertexColors: true,
                metalness: 0.1,
                roughness: 0.6
            });
            sphereMesh = new THREE.Mesh(sphereGeom, sphereMat);
            scene.add(sphereMesh);

            // Bounding Box
            boxHelper = new THREE.BoxHelper(sphereMesh, 0x888888);
            boxHelper.visible = false;
            scene.add(boxHelper);

            // Compass Rose
            createCompassRose();

            // P, T, U Labels
            pLabel = makeTextSprite('P', { fontsize: 32, fontface: 'Arial', textColor: { r: 0, g: 0, b: 0, a: 1.0 } });
            tLabel = makeTextSprite('T', { fontsize: 32, fontface: 'Arial', textColor: { r: 0, g: 0, b: 0, a: 1.0 } });
            uLabel = makeTextSprite('U', { fontsize: 32, fontface: 'Arial', textColor: { r: 0, g: 0, b: 0, a: 1.0 } });
            scene.add(pLabel, tLabel, uLabel);

            // Event Listeners
            for (const key in sliders) {
                sliders[key].addEventListener('input', handleSliderChange);
            }
            btnReset.addEventListener('click', resetControls);
            checkboxBox.addEventListener('change', () => {
                boxHelper.visible = checkboxBox.checked;
            });
            window.addEventListener('resize', onWindowResize);

            // Initial State
            resetControls();

            // Start Animation Loop
            animate();
        }

        function createCompassRose() {
            const radius = 2.0;
            const compassGroup = new THREE.Group();

            // Dashed Circle
            const curve = new THREE.EllipseCurve(0, 0, radius, radius, 0, 2 * Math.PI, false, 0);
            const points = curve.getPoints(128);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineDashedMaterial({ color: 0x888888, dashSize: 0.05, gapSize: 0.05 });
            const ellipse = new THREE.Line(geometry, material);
            ellipse.computeLineDistances();
            ellipse.rotation.x = -Math.PI / 2;
            compassGroup.add(ellipse);

            // Labels
            const directions = { 'N': 0, 'E': 90, 'S': 180, 'W': 270 };
            for (const [label, angle] of Object.entries(directions)) {
                const rad = -angle * Math.PI / 180;
                const sprite = makeTextSprite(label, { fontsize: 48, fontWeight: 'bold' });
                sprite.position.set(Math.sin(rad) * radius * 1.15, 0, Math.cos(rad) * radius * 1.15);
                compassGroup.add(sprite);
            }

            for (let angle = 0; angle < 360; angle += 30) {
                if (angle % 90 === 0) continue;
                const rad = -angle * Math.PI / 180;
                const sprite = makeTextSprite(`${angle}°`, { fontsize: 24 });
                sprite.position.set(Math.sin(rad) * radius * 1.1, 0, Math.cos(rad) * radius * 1.1);
                compassGroup.add(sprite);
            }

            scene.add(compassGroup);
        }

        function makeTextSprite(message, parameters) {
            const fontface = parameters.hasOwnProperty("fontface") ? parameters["fontface"] : "Arial";
            const fontsize = parameters.hasOwnProperty("fontsize") ? parameters["fontsize"] : 18;
            const fontWeight = parameters.hasOwnProperty("fontWeight") ? parameters["fontWeight"] : "normal";
            const textColor = parameters.hasOwnProperty("textColor") ? parameters["textColor"] : { r: 0, g: 0, b: 0, a: 1.0 };

            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            context.font = `${fontWeight} ${fontsize}px ${fontface}`;

            const metrics = context.measureText(message);
            const textWidth = metrics.width;
            canvas.width = textWidth + 10;
            canvas.height = fontsize + 10;
            context.font = `${fontWeight} ${fontsize}px ${fontface}`; // re-set font after resize
            context.fillStyle = `rgba(${textColor.r}, ${textColor.g}, ${textColor.b}, ${textColor.a})`;
            context.fillText(message, 5, fontsize);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.scale.set(0.01 * canvas.width, 0.01 * canvas.height, 1.0);
            return sprite;
        }

        function handleSliderChange(event) {
            updateUI(event.target.id);
            updateVisualization();
        }

        function updateUI(sourceId) {
            if (sourceId) {
                const key = sourceId.split('-')[1];
                 values[key].value = parseFloat(sliders[key].value).toFixed(key === 'slipAmount' ? 2 : 0);
            } else { // update all
                 for(const key in sliders) {
                     values[key].value = parseFloat(sliders[key].value).toFixed(key === 'slipAmount' ? 2 : 0);
                 }
            }
        }

        function resetControls() {
            for (const key in DEFAULTS) {
                sliders[key].value = DEFAULTS[key];
            }
            updateUI();
            updateVisualization();
        }

        function updateVisualization() {
            const strike = parseFloat(sliders.strike.value);
            const dip = parseFloat(sliders.dip.value);
            const slipAngle = parseFloat(sliders.slipAngle.value);
            const slipAmount = parseFloat(sliders.slipAmount.value);

            // --- Calculations ---
            const phi = strike * Math.PI / 180; // strike
            const delta = dip * Math.PI / 180; // dip
            const lambda = slipAngle * Math.PI / 180; // slip angle

            const sinP = Math.sin(phi);
            const cosP = Math.cos(phi);
            const sinD = Math.sin(delta);
            const cosD = Math.cos(delta);
            const sinL = Math.sin(lambda);
            const cosL = Math.cos(lambda);

            // Aki & Richards (1980) coordinate system (x=N, y=E, z=Down)
            // Fault normal vector 'n'
            const n_seismo = new THREE.Vector3(-sinD * sinP, sinD * cosP, cosD);
            // Slip vector 'u'
            const u_seismo = new THREE.Vector3(
                cosL * cosP + sinL * cosD * sinP,
                cosL * sinP - sinL * cosD * cosP,
                sinL * sinD
            );

            // Convert to Three.js coordinate system (x=E, y=Up, z=S)
            // three_x = seismo_y (E)
            // three_y = -seismo_z (Down)
            // three_z = -seismo_x (N)
            const n_vec = new THREE.Vector3(n_seismo.y, -n_seismo.z, -n_seismo.x).normalize();
            const u_vec = new THREE.Vector3(u_seismo.y, -u_seismo.z, -u_seismo.x).normalize();

            // P (compression) and T (tension) axes
            const p_vec = new THREE.Vector3().addVectors(u_vec, n_vec).normalize();
            const t_vec = new THREE.Vector3().subVectors(u_vec, n_vec).normalize();

            // --- Update Geometry and Colors ---
            const positions = sphereMesh.geometry.attributes.position;
            const colors = sphereMesh.geometry.attributes.color;
            const v = new THREE.Vector3();

            for (let i = 0; i < originalPositions.count; i++) {
                v.fromBufferAttribute(originalPositions, i);

                const dotP = v.dot(p_vec);
                const dotT = v.dot(t_vec);

                const radiation = dotT * dotT - dotP * dotP;

                // Update Color
                const color = (radiation > 0) ? COLOR_DILATATIONAL : COLOR_COMPRESSIVE;
                colors.setXYZ(i, color.r, color.g, color.b);

                // Update Position (Deformation)
                const displacement = slipAmount * DEFORMATION_SCALE * radiation;
                const newMagnitude = 1 + displacement;
                v.multiplyScalar(newMagnitude);
                positions.setXYZ(i, v.x, v.y, v.z);
            }

            positions.needsUpdate = true;
            colors.needsUpdate = true;
            sphereMesh.geometry.computeVertexNormals();

            // --- Update Labels and Box ---
            const p_pos_mag = 1 + slipAmount * DEFORMATION_SCALE * (p_vec.dot(t_vec)**2 - p_vec.dot(p_vec)**2);
            pLabel.position.copy(p_vec).multiplyScalar(p_pos_mag * 1.15);

            const t_pos_mag = 1 + slipAmount * DEFORMATION_SCALE * (t_vec.dot(t_vec)**2 - t_vec.dot(p_vec)**2);
            tLabel.position.copy(t_vec).multiplyScalar(t_pos_mag * 1.15);

            const u_pos_mag = 1 + slipAmount * DEFORMATION_SCALE * (u_vec.dot(t_vec)**2 - u_vec.dot(p_vec)**2);
            uLabel.position.copy(u_vec).multiplyScalar(u_pos_mag * 1.15);

            boxHelper.update();
        }

        function onWindowResize() {
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        init();
    </script>
</body>
</html>
