<body>

    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database size</label>
                <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
                <div class="value-display">+<span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
                <div class="value-display">+<span id="display-iterations">2</span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display"></div>
    </div>

    <script>
        // --- DOM Elements ---
        let sliderDbSize;
        let sliderIterations;
        let displayDbSize;
        let displayIterations;
        let probabilityDisplay;

        // --- Constants ---
        const TARGET_INDEX = 6; // Fixed target qubit, 0-indexed
        const TARGET_COLOR = '#FFA500';
        const NORMAL_COLOR = '#808080';

        function sketch(p) {
            p.setup = function () {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                canvas.parent('canvas-container');
                p.noLoop();

                // Get DOM element references
                sliderDbSize = document.getElementById('slider-db-size');
                sliderIterations = document.getElementById('slider-iterations');
                displayDbSize = document.getElementById('display-db-size');
                displayIterations = document.getElementById('display-iterations');
                probabilityDisplay = document.getElementById('probability-display');

                // Set initial values from HTML
                displayDbSize.textContent = sliderDbSize.value;
                displayIterations.textContent = sliderIterations.value;

                // Add event listeners
                sliderDbSize.addEventListener('input', () => {
                    displayDbSize.textContent = sliderDbSize.value;
                    p.redraw();
                });
                sliderIterations.addEventListener('input', () => {
                    displayIterations.textContent = sliderIterations.value;
                    p.redraw();
                });

                // Initial draw
                p.redraw();
            };

            p.draw = function () {
                const N = parseInt(sliderDbSize.value, 10);
                const k = parseInt(sliderIterations.value, 10);

                p.background(255);

                const stageHeight = p.height / 4;

                // --- Grover's Algorithm Logic ---

                // 1. Initial State
                let amplitudes = Array(N).fill(1 / Math.sqrt(N));

                // 2. Oracle (Inversion)
                const applyOracle = (amps) => {
                    const newAmps = [...amps];
                    if (TARGET_INDEX < newAmps.length) {
                        newAmps[TARGET_INDEX] *= -1;
                    }
                    return newAmps;
                };

                // 3. Diffusion
                const applyDiffusion = (amps) => {
                    const mean = amps.reduce((sum, val) => sum + val, 0) / amps.length;
                    return amps.map(a => 2 * mean - a);
                };

                // --- STAGE 1: Original Amplitudes ---
                drawStage(p, 'original amplitudes', amplitudes, stageHeight * 0.5, true);

                // --- STAGE 2: After First Inversion ---
                const ampsAfterOracle = applyOracle(amplitudes);
                drawStage(p, 'after first inversion', ampsAfterOracle, stageHeight * 1.5);

                // --- STAGE 3: After First Diffusion ---
                const ampsAfterDiffusion = applyDiffusion(ampsAfterOracle);
                drawStage(p, 'after first diffusion', ampsAfterDiffusion, stageHeight * 2.5);

                // --- STAGE 4: After k Iterations ---
                let finalAmps = [...amplitudes];
                for (let i = 0; i < k; i++) {
                    finalAmps = applyOracle(finalAmps);
                    finalAmps = applyDiffusion(finalAmps);
                }
                const title_k = k === 1 ? 'after 1 iteration' : `after ${k} iterations`;
                drawStage(p, title_k, finalAmps, stageHeight * 3.5);

                // --- Update Probability Display ---
                const probability = Math.pow(Math.sin((2 * k + 1) * Math.asin(1 / Math.sqrt(N))), 2);
                const probPercent = (probability * 100).toFixed(1);
                probabilityDisplay.innerHTML = `${probPercent}% success probability`;
            };

            // --- Drawing Helper Function ---
            function drawStage(p, title, amplitudes, yCenter, drawParentheses = false) {
                const N = amplitudes.length;
                const margin = 40;
                const barWidth = Math.max(1, 250 / N); // Bars get thinner for large N
                const availableWidth = p.width - 2 * margin;
                const spacing = (N > 1) ? availableWidth / (N - 1) : 0;

                // Scaling factor for bar height. Looks good for a wide range of N.
                const barHeightScale = 40;

                // Draw title
                p.fill(100);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(14);
                p.text(title, p.width / 2, yCenter - 50);

                // Draw baseline
                p.stroke(150);
                p.strokeWeight(1);
                p.line(margin, yCenter, p.width - margin, yCenter);

                // Draw amplitude bars
                p.noStroke();
                for (let i = 0; i < N; i++) {
                    const x = margin + i * spacing;
                    const amplitude = amplitudes[i];
                    // The scaling factor from the plan is quite small. Let's make it more dynamic.
                    const scaledAmplitude = amplitude * Math.sqrt(N); 
                    const barHeight = scaledAmplitude * barHeightScale;

                    p.fill(i === TARGET_INDEX ? TARGET_COLOR : NORMAL_COLOR);

                    if (barHeight >= 0) {
                        p.rect(x - barWidth / 2, yCenter - barHeight, barWidth, barHeight);
                    } else {
                        p.rect(x - barWidth / 2, yCenter, barWidth, -barHeight);
                    }
                }

                // Draw parentheses for the first stage
                if (drawParentheses) {
                    p.noFill();
                    p.stroke(150);
                    p.strokeWeight(2);
                    const parenHeight = 40;
                    const firstBarX = margin;
                    const lastBarX = margin + (N-1) * spacing;

                    p.arc(firstBarX - 15, yCenter, 20, parenHeight, p.PI / 2, -p.PI / 2);
                    p.arc(lastBarX + 15, yCenter, 20, parenHeight, -p.PI / 2, p.PI / 2);
                }
            }
        }

        new p5(sketch);
    </script>
</body>
</html>
