<body>

<div id="main-container">
    <!-- Left column: Control Panel -->
    <div id="control-panel">
        <div class="control-group">
            <label for="slider-volatility">relative volatility</label>
            <input type="range" id="slider-volatility" min="1.1" max="10" step="0.1" value="2.5">
            <button id="btn-volatility-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-distillate">distillate specification</label>
            <input type="range" id="slider-distillate" min="0.51" max="0.99" step="0.01" value="0.95">
            <button id="btn-distillate-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-bottom">bottom specification</label>
            <input type="range" id="slider-bottom" min="0.01" max="0.49" step="0.01" value="0.05">
            <button id="btn-bottom-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-feed-comp">feed composition</label>
            <input type="range" id="slider-feed-comp" min="0.02" max="0.98" step="0.01" value="0.50">
            <button id="btn-feed-comp-plus">+</button>
        </div>
        <div class="control-group">
            <label for="slider-feed-quality">feed quality</label>
            <input type="range" id="slider-feed-quality" min="0.0" max="2.0" step="0.05" value="1.0">
            <button id="btn-feed-quality-plus">+</button>
        </div>
    </div>

    <!-- Right column: Visualization Area -->
    <div id="visualization-area">
        <p id="stages-count-display">The number of stages is 8.</p>
        <div id="canvas-container"></div> <!-- p5.js canvas will be attached here -->
    </div>
</div>

<script>
    // Get references to all interactive elements
    const sliders = {
        volatility: document.getElementById('slider-volatility'),
        distillate: document.getElementById('slider-distillate'),
        bottom: document.getElementById('slider-bottom'),
        feedComp: document.getElementById('slider-feed-comp'),
        feedQuality: document.getElementById('slider-feed-quality'),
    };

    const buttons = {
        volatility: document.getElementById('btn-volatility-plus'),
        distillate: document.getElementById('btn-distillate-plus'),
        bottom: document.getElementById('btn-bottom-plus'),
        feedComp: document.getElementById('btn-feed-comp-plus'),
        feedQuality: document.getElementById('btn-feed-quality-plus'),
    };

    const stagesDisplay = document.getElementById('stages-count-display');

    // Main p5.js sketch function
    const sketch = (p) => {
        const margin = 50;
        let canvasWidth, canvasHeight;

        // Coordinate mapping functions
        const mapX = (val) => p.map(val, 0, 1, margin, canvasWidth - margin);
        const mapY = (val) => p.map(val, 0, 1, canvasHeight - margin, margin); // Flipped Y-axis

        p.setup = () => {
            const canvasContainer = document.getElementById('canvas-container');
            canvasWidth = canvasContainer.offsetWidth;
            canvasHeight = canvasContainer.offsetHeight;
            const canvas = p.createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            p.noLoop(); // We will only redraw on input changes

            // Attach event listeners
            Object.values(sliders).forEach(slider => {
                slider.addEventListener('input', onInputChange);
            });

            // Button click listeners
            for (const key in buttons) {
                buttons[key].addEventListener('click', () => {
                    const slider = sliders[key];
                    let currentValue = parseFloat(slider.value);
                    let step = parseFloat(slider.step);
                    let newValue = currentValue + step;
                    if (newValue > parseFloat(slider.max)) newValue = parseFloat(slider.max);

                    // Use toFixed to handle floating point inaccuracies
                    const precision = (step.toString().split('.')[1] || []).length;
                    slider.value = newValue.toFixed(precision);

                    // Dispatch input event to trigger redraw
                    slider.dispatchEvent(new Event('input', { bubbles: true }));
                });
            }

            // Initial draw
            onInputChange();
        };

        // This function is called whenever an input changes
        function onInputChange() {
            handleConstraints();
            drawDiagram();
        }

        // Enforce parameter constraints
        function handleConstraints() {
            let xD = parseFloat(sliders.distillate.value);
            let xB = parseFloat(sliders.bottom.value);
            let xF = parseFloat(sliders.feedComp.value);
            const step = parseFloat(sliders.feedComp.step);

            // Dynamically set min/max for feed composition
            sliders.feedComp.min = (xB + step).toFixed(2);
            sliders.feedComp.max = (xD - step).toFixed(2);

            // Adjust feed composition if it violates the new bounds
            if (xF <= xB) {
                sliders.feedComp.value = (xB + step).toFixed(2);
            }
            if (xF >= xD) {
                sliders.feedComp.value = (xD - step).toFixed(2);
            }
        }

        // The main drawing function
        function drawDiagram() {
            // 1. Read Inputs
            const alpha = parseFloat(sliders.volatility.value);
            const xD = parseFloat(sliders.distillate.value);
            const xB = parseFloat(sliders.bottom.value);
            const xF = parseFloat(sliders.feedComp.value);
            const q = parseFloat(sliders.feedQuality.value);

            // 2. Clear and Prepare Canvas
            p.background(255);
            drawAxes();

            // 3. Draw Equilibrium Curve (Red)
            p.stroke('#FF0000'); // Red
            p.strokeWeight(2);
            p.noFill();
            p.beginShape();
            for (let x = 0; x <= 1.0; x += 0.01) {
                const y = (alpha * x) / (1 + (alpha - 1) * x);
                p.vertex(mapX(x), mapY(y));
            }
            p.endShape();

            // 4. Draw Diagonal Line (Green)
            p.stroke('#228B22'); // ForestGreen
            p.strokeWeight(2);
            p.line(mapX(0), mapY(0), mapX(1), mapY(1));

            // 5. Calculate Intersection Point
            const { x_int, y_int } = calculateIntersection(alpha, xF, q);

            // 6. Draw Operating & Feed Lines
            p.strokeWeight(2);
            // Rectifying Line (Cyan)
            p.stroke('#00FFFF');
            p.line(mapX(xD), mapY(xD), mapX(x_int), mapY(y_int));
            // Stripping Line (Magenta)
            p.stroke('#FF00FF');
            p.line(mapX(xB), mapY(xB), mapX(x_int), mapY(y_int));
            // Feed Line (Dark Blue)
            p.stroke('#0000CD');
            p.line(mapX(xF), mapY(xF), mapX(x_int), mapY(y_int));

            // 7. Calculate and Draw Stages (Black)
            const stageCount = drawStages(alpha, xD, xB, x_int, y_int);

            // 8. Update Stage Count Display
            stagesDisplay.textContent = `The number of stages is ${stageCount}.`;
        }

        function drawAxes() {
            p.stroke('#808080'); // Gray
            p.strokeWeight(1);
            p.fill('#808080');
            p.textAlign(p.CENTER, p.CENTER);

            // Draw axis lines
            p.line(mapX(0), mapY(0), mapX(1), mapY(0)); // X-axis
            p.line(mapX(0), mapY(0), mapX(0), mapY(1)); // Y-axis

            // Draw ticks and labels
            for (let i = 0.2; i <= 1.0; i += 0.2) {
                const tickVal = i.toFixed(1);
                // Y-axis ticks
                p.line(mapX(0), mapY(i), mapX(0) - 5, mapY(i));
                p.noStroke();
                p.text(tickVal, mapX(0) - 20, mapY(i));
                p.stroke('#808080');

                // X-axis ticks
                p.line(mapX(i), mapY(0), mapX(i), mapY(0) + 5);
                p.noStroke();
                p.text(tickVal, mapX(i), mapY(0) + 20);
                p.stroke('#808080');
            }
             p.noStroke();
             p.text("0.0", mapX(0)-15, mapY(0)+15);
             p.stroke('#808080');
        }

        function calculateIntersection(alpha, xF, q) {
            let x_int, y_int;

            if (q === 1.0) { // Saturated liquid, vertical q-line
                x_int = xF;
                y_int = (alpha * x_int) / (1 + (alpha - 1) * x_int);
            } else if (q === 0.0) { // Saturated vapor, horizontal q-line
                y_int = xF;
                x_int = y_int / (alpha - (alpha - 1) * y_int);
            } else { // General case, solve quadratic
                const m_q = q / (q - 1);
                const c_q = xF - m_q * xF;

                const A = m_q * (alpha - 1);
                const B = m_q + c_q * (alpha - 1) - alpha;
                const C = c_q;

                // Using quadratic formula: x = (-B ± sqrt(B^2 - 4AC)) / 2A
                const discriminant = B * B - 4 * A * C;
                if (discriminant >= 0) {
                     // We expect two roots, one is usually physically irrelevant.
                     // The correct root is typically between xB and xD.
                    const root1 = (-B + Math.sqrt(discriminant)) / (2 * A);
                    const root2 = (-B - Math.sqrt(discriminant)) / (2 * A);
                    x_int = (root1 >= 0 && root1 <= 1) ? root1 : root2;
                } else {
                    // Fallback for no real intersection, should not happen in normal range
                    x_int = xF;
                }
                y_int = m_q * x_int + c_q;
            }
            return { x_int, y_int };
        }

        function drawStages(alpha, xD, xB, x_int, y_int) {
            p.stroke('#000000'); // Black
            p.strokeWeight(1);

            let stageCount = 0;
            let currentX = xD;
            let currentY = xD;

            // Slopes and intercepts of operating lines
            const slopeRect = (y_int - xD) / (x_int - xD);
            const interceptRect = xD - slopeRect * xD;
            const slopeStrip = (y_int - xB) / (x_int - xB);
            const interceptStrip = xB - slopeStrip * xB;

            while (currentX > xB && stageCount < 100) { // Safety break at 100 stages
                stageCount++;
                const prevX = currentX;
                const prevY = currentY;

                // Horizontal step to equilibrium curve
                currentX = currentY / (alpha - currentY * (alpha - 1));
                p.line(mapX(prevX), mapY(currentY), mapX(currentX), mapY(currentY));

                if (currentX <= xB) break;

                // Vertical step to operating line
                if (currentX > x_int) { // Above feed, use rectifying line
                    currentY = slopeRect * currentX + interceptRect;
                } else { // Below feed, use stripping line
                    currentY = slopeStrip * currentX + interceptStrip;
                }
                p.line(mapX(currentX), mapY(prevY), mapX(currentX), mapY(currentY));
            }
            return stageCount;
        }

    };

    // Instantiate the p5.js sketch
    new p5(sketch);
</script>

</body>
</html>
