                        }
                    });
                    threeApp.scene = null;
                }
                threeApp = { renderer: null, scene: null, camera: null, controls: null, animationId: null, earthSystem: null, sun: null };
            }

            // --- P5.JS 2D SKETCH ---
            const sketch2D = (p) => {
                p.setup = () => {
                    p.createCanvas(vizArea.clientWidth, vizArea.clientHeight, p.WEBGL);
                    p.angleMode(p.DEGREES);
                    p.noLoop(); // Redraw only on slider change
