                    p.rotateZ(tilt);

                    // Magnetotail (semi-transparent blue)
                    p.noStroke();
                    p.fill(0, 0, 139, 150);
                    p.beginShape();
                    p.vertex(-30, 45);
                    p.curveVertex(100, 100);
                    p.curveVertex(p.width/2, 80);
                    p.curveVertex(p.width/2, -80);
                    p.curveVertex(100, -100);
                    p.vertex(-30, -45);
                    p.endShape(p.CLOSE);

                    // Earth
                    p.fill('#4169E1'); // Blue
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.ellipse(0, 0, 80, 80);
                    p.fill('#228B22'); // Green
                    p.noStroke();
                    p.arc(0, 0, 78, 78, 20, 110);
                    p.arc(0, 0, 78, 78, 180, 220);
