                    // Polar Cusps
                    p.fill('#F0E68C'); // Khaki
                    p.triangle(-20, 38, -20, -38, -120, 0); // Simplified shape
                    p.beginShape();
                    p.vertex(-20, 40); p.vertex(-80, 100); p.vertex(-80, 60);
                    p.endShape(p.CLOSE);
                     p.beginShape();
                    p.vertex(-20, -40); p.vertex(-80, -100); p.vertex(-80, -60);
