                const earthGeom = new THREE.SphereGeometry(1, 32, 32);
                const earthMat = new THREE.MeshStandardMaterial({ color: 0x4169E1, roughness: 0.8 });
                const earth = new THREE.Mesh(earthGeom, earthMat);
