                earth.rotation.x = THREE.MathUtils.degToRad(AXIAL_TILT_DEGREES);
                threeApp.earthSystem.add(earth);

                // Earth's Axis Helper
                const axis = new THREE.ArrowHelper(new THREE.Vector3(0, 1, 0), new THREE.Vector3(0, 0, 0), 2, 0x00FF00);
