                // Magnetosphere
                const profilePoints = [];
                profilePoints.push(new THREE.Vector2(0, 0.2)); // Tip by earth
                profilePoints.push(new THREE.Vector2(2, 2.5)); // Nose
                profilePoints.push(new THREE.Vector2(0, 4));   // Widest point
                profilePoints.push(new THREE.Vector2(-15, 6)); // Tail
                const latheGeom = new THREE.LatheGeometry(profilePoints, 32);
                const latheMat = new THREE.MeshStandardMaterial({
                    color: 0x0000FF,
                    transparent: true,
                    opacity: 0.4,
                    side: THREE.DoubleSide,
                    metalness: 0.2,
                    roughness: 0.5
                });
                const magnetosphere = new THREE.Mesh(latheGeom, latheMat);
                magnetosphere.rotation.y = Math.PI / 2;
                magnetosphere.position.z = -1; // Offset slightly
                threeApp.earthSystem.add(magnetosphere);

                updateThreeScene();
                animateThree();
            }

            function updateThreeScene() {
                if (!threeApp.scene || !threeApp.orbit) return;

                const angle = (currentMonth / 12) * 2 * Math.PI;
                const pointOnOrbit = threeApp.orbit.getPoint(currentMonth / 12);

                // Position Earth system on the orbit
                threeApp.earthSystem.position.set(pointOnOrbit.x, 0, pointOnOrbit.y); // Use y from curve for z

                // Orient the system so the magnetosphere tail points away from the Sun
                threeApp.earthSystem.lookAt(threeApp.sun.position);
            }

            function animateThree() {
                threeApp.animationId = requestAnimationFrame(animateThree);
                threeApp.controls.update();
                threeApp.renderer.render(threeApp.scene, threeApp.camera);
            }

            // --- EVENT HANDLERS & MAIN LOGIC ---
            function switchView(view) {
                if (view === currentView) return;

                currentView = view;
                cleanupP5();
                cleanupThree();

                if (view === '2d') {
                    btn2d.classList.add('active');
                    btn3d.classList.remove('active');
                    p5Instance = new p5(sketch2D, vizArea);
                } else {
                    btn3d.classList.add('active');
                    btn2d.classList.remove('active');
                    init3D();
                }
            }

            function handleSliderChange(event) {
                currentMonth = parseInt(event.target.value, 10);
                if (currentView === '2d' && p5Instance) {
                    p5Instance.redraw();
                } else if (currentView === '3d') {
                    updateThreeScene();
                }
            }

            // --- INITIALIZATION ---
            document.addEventListener('DOMContentLoaded', () => {
                slider.value = currentMonth;
                slider.addEventListener('input', handleSliderChange);
                btn2d.addEventListener('click', () => switchView('2d'));
                btn3d.addEventListener('click', () => switchView('3d'));

                // Initial view setup
                switchView('2d');

                // Handle window resize
                window.addEventListener('resize', () => {
                    if (currentView === '2d' && p5Instance) {
                        p5Instance.windowResized();
                    }
                    if (currentView === '3d' && threeApp.renderer) {
                        threeApp.renderer.setSize(vizArea.clientWidth, vizArea.clientHeight);
                        threeApp.camera.aspect = vizArea.clientWidth / vizArea.clientHeight;
                        threeApp.camera.updateProjectionMatrix();
                    }
                });
            });
        })();
    </script>

