<script>
    document.addEventListener('DOMContentLoaded', () => {
        // --- DOM Element References ---
        const sliderAlpha = document.getElementById('slider-alpha');
        const displayAlpha = document.getElementById('display-alpha');
        const sliderBeta = document.getElementById('slider-beta');
        const displayBeta = document.getElementById('display-beta');
        const plotContainer = document.getElementById('plot-container');
        const meanValue = document.getElementById('mean-value');
        const varianceValue = document.getElementById('variance-value');
        const mgfFormula = document.getElementById('mgf-formula');

        // --- Mathematical Helper Functions ---

        // Lanczos approximation for the Gamma function Γ(z)
        function gamma(z) {
            const g = 7;
            const p = [
                0.99999999999980993, 676.5203681218851, -1259.1392167224028,
                771.32342877765313, -176.61502916214059, 12.507343278686905,
                -0.13857109526572012, 9.9843695780195716e-6, 1.5056327351493116e-7
            ];
            if (z < 0.5) {
                return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
            }
            z -= 1;
            let x = p[0];
            for (let i = 1; i < g + 2; i++) {
                x += p[i] / (z + i);
            }
            const t = z + g + 0.5;
            return Math.sqrt(2 * Math.PI) * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
        }

        // Gamma Probability Density Function (PDF)
        function gammaPDF(x, alpha, beta) {
            if (x < 0) return 0;
            if (x === 0) {
                if (alpha === 1) return 1 / beta;
                if (alpha < 1) return Infinity; // Technically correct, Plotly will handle it
                return 0;
            }
            const numerator = Math.pow(x, alpha - 1) * Math.exp(-x / beta);
            const denominator = Math.pow(beta, alpha) * gamma(alpha);
            return numerator / denominator;
        }

        // --- Main Update Function ---
        function updateVisualization() {
            // 1. Read values from sliders
            const alpha = parseFloat(sliderAlpha.value);
            const beta = parseFloat(sliderBeta.value);

            // 2. Update display spans with formatting seen in snapshots
            displayAlpha.textContent = (alpha % 1 === 0 && alpha.toFixed(3).endsWith('.000')) ? `${alpha.toFixed(0)}.` : alpha.toFixed(3);
            displayBeta.textContent = (beta % 1 === 0 && beta.toFixed(3).endsWith('.000')) ? `${beta.toFixed(0)}.` : beta.toFixed(3);

            // 3. Generate data for plot
            const xValues = [];
            const pdfValues = [];
            const cdfValues = [];
            const step = 0.05;
            const xMax = 25;

            for (let x = 0; x <= xMax; x += step) {
                xValues.push(x);
                pdfValues.push(gammaPDF(x, alpha, beta));
            }

            // Numerically integrate PDF to get CDF using the trapezoidal rule
            let cumulativeArea = 0;
            cdfValues.push(0);
            for (let i = 1; i < pdfValues.length; i++) {
                const trapezoidArea = (pdfValues[i] + pdfValues[i - 1]) / 2 * step;
                cumulativeArea += trapezoidArea;
                cdfValues.push(cumulativeArea);
            }

            // 4. Perform statistical calculations
            const mean = alpha * beta;
            const variance = alpha * Math.pow(beta, 2);
            const yMean = gammaPDF(mean, alpha, beta);

