            const betaStr = (beta % 1 === 0) ? beta.toFixed(0) : beta.toFixed(3);

            mgfFormula.innerHTML = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${betaStr} t)^{${alphaStr}}} $$`;
