<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Interactive Ternary Plot</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            background-color: #f0f0f0;
            font-family: sans-serif;
        }
        #canvas-container {
            border: 1px solid #ccc;
            box-shadow: 0 4px 8px rgba(0,0,0,0.1);
        }
    </style>
</head>
<body>

    <div id="canvas-container">
        <!-- The p5.js canvas will be created here -->
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        const sketch = (p) => {
            // --- Configuration ---
            const canvasSize = 650;
            const margin = 60;
            const plotSize = canvasSize - 2 * margin;

            // --- State ---
            let isDragging = false;
            const locator = {
                x: 0.2,
                y: 0.22
            };

            // --- Constants ---
            const azeotropes = {
                A1: { x: 0.655, y: 0, label: 'A₁' },
                A2: { x: 0, y: 0.795, label: 'A₂' },
                A3: { x: 0.605, y: 0.345, label: 'A₃' },
                A4: { x: 0.26, y: 0.335, label: 'A₄' }
            };

            // Distillation boundary path
            const distillationBoundary = [azeotropes.A2, azeotropes.A4, azeotropes.A1];

            // --- p5.js Lifecycle Methods ---
            p.setup = () => {
                const canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.parent('canvas-container');
                canvas.id('visualization-canvas');
                p.pixelDensity(1); // Ensure consistent sizing
            };

            p.draw = () => {
                p.background(255);
                drawAxesAndLabels();
                drawAzeotropes();
                drawDistillationLine();
                drawLocator();
            };

            p.mousePressed = () => {
                const locPx = dataToPixel(locator.x, locator.y);
                const d = p.dist(p.mouseX, p.mouseY, locPx.px, locPx.py);
                if (d < 15) { // Click tolerance radius
                    isDragging = true;
                }
            };

            p.mouseReleased = () => {
                isDragging = false;
            };

            p.mouseDragged = () => {
                if (isDragging) {
                    const mouseData = pixelToData(p.mouseX, p.mouseY);

                    // Find the closest point on the distillation boundary polyline
                    let closestPoint = null;
                    let minDistanceSq = Infinity;

                    for (let i = 0; i < distillationBoundary.length - 1; i++) {
                        const segmentStart = distillationBoundary[i];
                        const segmentEnd = distillationBoundary[i+1];
                        const pointOnSegment = findClosestPointOnSegment(mouseData, segmentStart, segmentEnd);

                        const distSq = (mouseData.x - pointOnSegment.x)**2 + (mouseData.y - pointOnSegment.y)**2;

                        if (distSq < minDistanceSq) {
                            minDistanceSq = distSq;
                            closestPoint = pointOnSegment;
                        }
                    }

                    if (closestPoint) {
                        locator.x = closestPoint.x;
                        locator.y = closestPoint.y;
                    }
                }
            };

            // --- Coordinate Transformation ---
            const dataToPixel = (x, y) => {
                const px = margin + x * plotSize;
                const py = (canvasSize - margin) - y * plotSize;
                return { px, py };
            };

            const pixelToData = (px, py) => {
                const x = (px - margin) / plotSize;
                const y = ((canvasSize - margin) - py) / plotSize;
                return { x, y };
            };

            // --- Rendering Functions ---
            const drawAxesAndLabels = () => {
                p.push();
                p.stroke(0);
                p.strokeWeight(1.5);
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);

                // Axes lines
                const origin = dataToPixel(0, 0);
                const xEnd = dataToPixel(1, 0);
                const yEnd = dataToPixel(0, 1);
                p.line(origin.px, origin.py, xEnd.px, xEnd.py); // x-axis
                p.line(origin.px, origin.py, yEnd.px, yEnd.py); // y-axis

                // Hypotenuse
                p.line(xEnd.px, xEnd.py, yEnd.px, yEnd.py);

                // Ticks and labels
                p.noStroke();
                p.fill(0);
                for (let i = 0; i <= 5; i++) {
                    const val = i * 0.2;
                    // X-axis ticks
                    let tickPos = dataToPixel(val, 0);
                    p.stroke(0);
                    p.line(tickPos.px, tickPos.py, tickPos.px, tickPos.py + 5);
                    p.noStroke();
                    p.text(val.toFixed(1), tickPos.px, tickPos.py + 15);

                    // Y-axis ticks
                    tickPos = dataToPixel(0, val);
                    p.stroke(0);
                    p.line(tickPos.px, tickPos.py, tickPos.px - 5, tickPos.py);
                    p.noStroke();
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(val.toFixed(1), tickPos.px - 10, tickPos.py);
                }

                // Component Labels
                p.fill('#FF0000');
                p.textSize(14);
                p.textAlign(p.RIGHT, p.TOP);
                p.text("methanol", origin.px - 5, origin.py + 5);
                p.textAlign(p.LEFT, p.TOP);
                p.text("chloroform", xEnd.px + 5, xEnd.py + 5);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text("acetone", yEnd.px, yEnd.py - 5);

                p.pop();
            };

            const drawAzeotropes = () => {
                p.push();
                p.noStroke();
                for (const key in azeotropes) {
                    const a = azeotropes[key];
                    const pos = dataToPixel(a.x, a.y);
                    // Point
                    p.fill('#990000');
                    p.circle(pos.px, pos.py, 12); // radius 6px
                    // Label
                    p.fill(0);
                    p.textSize(14);
                    p.textAlign(p.LEFT, p.BOTTOM);
                    p.text(a.label, pos.px + 8, pos.py - 8);
                }
                p.pop();
            };

            const drawDistillationLine = () => {
                const points = calculateResidueCurve();
                p.push();
                p.noFill();
                p.stroke('#0000FF');
                p.strokeWeight(2);
                p.beginShape();
                if (points.length > 1) {
                    // Duplicate first point for curveVertex
                    let firstPt = dataToPixel(points[0].x, points[0].y);
                    p.curveVertex(firstPt.px, firstPt.py);

                    points.forEach(pt => {
                        const pxPt = dataToPixel(pt.x, pt.y);
                        p.curveVertex(pxPt.px, pxPt.py);
                    });

                    // Duplicate last point for curveVertex
                    let lastPt = dataToPixel(points[points.length-1].x, points[points.length-1].y);
                    p.curveVertex(lastPt.px, lastPt.py);
                }
                p.endShape();
                p.pop();
            };

            const drawLocator = () => {
                const pos = dataToPixel(locator.x, locator.y);
                p.push();
                p.strokeWeight(2);
                p.stroke(0);
                p.fill(255);
                p.circle(pos.px, pos.py, 16); // radius 8px
                p.pop();
            };

            // --- Calculation/Logic Functions ---

            // Vector field model as specified
            const getVector = (x, y) => {
              const A3 = {x: 0.605, y: 0.345}; // Unstable node (repeller)
              const M = {x: 0, y: 0};         // Stable node (attractor)

              // Vector from repeller to current point
              let v_repel_x = x - A3.x;
              let v_repel_y = y - A3.y;
              // Vector from current point to attractor
              let v_attract_x = M.x - x;
              let v_attract_y = M.y - y;

              // Normalize influence by distance squared, add epsilon to avoid division by zero
              let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
              let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

              // Weighted sum of influences
              let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
              let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

              // Add a small rotational field to shape the curves
              dx += -0.1 * y;
              dy += 0.1 * x;

              return { dx, dy };
            }

            const calculateResidueCurve = () => {
                const stepSize = 0.005;
                const numSteps = 250;
                let forwardPoints = [];
                let backwardPoints = [];

                // Forward integration
                let current = { x: locator.x, y: locator.y };
                for (let i = 0; i < numSteps; i++) {
                    if (current.x < 0 || current.y < 0 || current.x + current.y > 1) break;
                    const vec = getVector(current.x, current.y);
                    current = { x: current.x + vec.dx * stepSize, y: current.y + vec.dy * stepSize };
                    forwardPoints.push(current);
                }

                // Backward integration
                current = { x: locator.x, y: locator.y };
                for (let i = 0; i < numSteps; i++) {
                    if (current.x < 0 || current.y < 0 || current.x + current.y > 1) break;
                    const vec = getVector(current.x, current.y);
                    current = { x: current.x - vec.dx * stepSize, y: current.y - vec.dy * stepSize };
                    backwardPoints.push(current);
                }

                return [...backwardPoints.reverse(), {x: locator.x, y: locator.y}, ...forwardPoints];
            };

            const findClosestPointOnSegment = (p, a, b) => {
                const ap = { x: p.x - a.x, y: p.y - a.y };
                const ab = { x: b.x - a.x, y: b.y - a.y };
                const ab2 = ab.x * ab.x + ab.y * ab.y;
                if (ab2 === 0) return a; // segment is a point

                const ap_dot_ab = ap.x * ab.x + ap.y * ab.y;
                const t = p.constrain(ap_dot_ab / ab2, 0, 1);

                return {
                    x: a.x + ab.x * t,
                    y: a.y + ab.y * t
                };
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
