    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
        import { ParametricGeometry } from 'three/addons/geometries/ParametricGeometry.js';

        let scene, camera, renderer, controls, paraboloidGroup;

        // --- UI Elements ---
        const sliderA = document.getElementById('slider-a');
        const inputA = document.getElementById('input-a');
        const sliderB = document.getElementById('slider-b');
        const inputB = document.getElementById('input-b');
        const sliderC = document.getElementById('slider-c');
        const inputC = document.getElementById('input-c');

        // --- Main Functions ---
        function init() {
            const container = document.getElementById('canvas-container');

            // 1. Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            // 2. Camera
            const aspect = container.clientWidth / container.clientHeight;
            camera = new THREE.PerspectiveCamera(45, aspect, 0.1, 100);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // 3. Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            container.appendChild(renderer.domElement);

            // 4. Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // 5. Lighting
            const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(2, 3, 4);
            scene.add(directionalLight);

            // 6. Axes Helper
            drawAxes();

            // 7. Event Listeners
            setupEventListeners();

            // 8. Initial Visualization
            updateVisualization();

            // 9. Animation Loop
            animate();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function drawAxes() {
            const axisLength = 5;
            const axisMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });

            // X-axis
            let points = [];
            points.push(new THREE.Vector3(-axisLength, 0, 0));
            points.push(new THREE.Vector3(axisLength, 0, 0));
            let geometry = new THREE.BufferGeometry().setFromPoints(points);
            const xAxis = new THREE.Line(geometry, axisMaterial);
            scene.add(xAxis);

            // Y-axis
            points = [];
            points.push(new THREE.Vector3(0, -axisLength, 0));
            points.push(new THREE.Vector3(0, axisLength, 0));
            geometry = new THREE.BufferGeometry().setFromPoints(points);
            const yAxis = new THREE.Line(geometry, axisMaterial);
            scene.add(yAxis);

            // Z-axis
            points = [];
            points.push(new THREE.Vector3(0, 0, -axisLength));
            points.push(new THREE.Vector3(0, 0, axisLength));
            geometry = new THREE.BufferGeometry().setFromPoints(points);
            const zAxis = new THREE.Line(geometry, axisMaterial);
            scene.add(zAxis);
        }

        function updateVisualization() {
            // Get current values from controls
            const a = parseFloat(sliderA.value);
            const b = parseFloat(sliderB.value);
            const c = parseFloat(sliderC.value);

            // Remove previous mesh group if it exists
            if (paraboloidGroup) {
                scene.remove(paraboloidGroup);
                paraboloidGroup.traverse(child => {
                    if (child.geometry) child.geometry.dispose();
                    if (child.material) child.material.dispose();
                });
            }

            // Create new geometry, materials, and meshes
            const range = 2.0;
            const slices = 50;
            const stacks = 50;

            // Parametric function for the elliptic paraboloid
            const parametricFunction = (u, v, target) => {
                const x = a * v * range * Math.cos(u * 2 * Math.PI);
                const y = b * v * range * Math.sin(u * 2 * Math.PI);
                const z = c * (v * range) * (v * range);
                target.set(x, y, z);
            };

            const geometry = new ParametricGeometry(parametricFunction, slices, stacks);

            // Add vertex colors for the gradient
            const colors = [];
            const positions = geometry.attributes.position;
            const maxZ = c * range * range;
            const color1 = new THREE.Color(0xFFD700); // Gold/Yellow
            const color2 = new THREE.Color(0xFF4500); // OrangeRed

            for (let i = 0; i < positions.count; i++) {
                const z = positions.getZ(i);
                const normalizedZ = maxZ > 0 ? z / maxZ : 0;
                const vertexColor = color1.clone().lerp(color2, normalizedZ);
                colors.push(vertexColor.r, vertexColor.g, vertexColor.b);
            }
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            // Create a group to hold both solid and wireframe meshes
            paraboloidGroup = new THREE.Group();

            // 1. Solid Mesh with vertex colors
            const solidMaterial = new THREE.MeshPhongMaterial({
                side: THREE.DoubleSide,
                vertexColors: true
            });
            const solidMesh = new THREE.Mesh(geometry, solidMaterial);
            paraboloidGroup.add(solidMesh);

            // 2. Wireframe Overlay
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                color: 0xcccccc,
                wireframe: true,
                transparent: true,
                opacity: 0.5
            });
            const wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);
            paraboloidGroup.add(wireframeMesh);

            scene.add(paraboloidGroup);
        }

        function setupEventListeners() {
            // Sync slider and input for 'a'
            sliderA.addEventListener('input', (e) => {
                inputA.value = e.target.value;
                updateVisualization();
            });
            inputA.addEventListener('input', (e) => {
                sliderA.value = e.target.value;
                updateVisualization();
            });

            // Sync slider and input for 'b'
            sliderB.addEventListener('input', (e) => {
                inputB.value = e.target.value;
                updateVisualization();
            });
            inputB.addEventListener('input', (e) => {
                sliderB.value = e.target.value;
                updateVisualization();
            });

            // Sync slider and input for 'c'
            sliderC.addEventListener('input', (e) => {
                inputC.value = e.target.value;
                updateVisualization();
            });
            inputC.addEventListener('input', (e) => {
                sliderC.value = e.target.value;
                updateVisualization();
            });
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();

            renderer.setSize(width, height);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // --- Run ---
        init();

    </script>
</body>
</html>
