<script type="module">
    import * as THREE from 'three';
    import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

    // --- STATE ---
    let state = {
        j1: 1.5,
        m1: 0.5,
        j2: 1.0,
        m2: 0.0,
        j: 2.5,
        isAnimating: false,
        phi_z: 0,
        phi_j: 0,
    };

    // --- COLORS ---
    const COLORS = {
        J: 0x000000,
        J1: 0xff0000,
        J2: 0x0000ff,
        CONE_J: 0x808080,
        CONE_J1: 0xffaaaa,
        CONE_J2: 0xaaaaff,
    };

    // --- THREE.JS SETUP ---
    let scene, camera, renderer, controls;
    let sceneObjects = new THREE.Group();
    const Z_AXIS = new THREE.Vector3(0, 0, 1);

    // --- UI ELEMENTS ---
    const btnPlayPause = document.getElementById('btn-play-pause');
    const btnReset = document.getElementById('btn-reset');

    function formatHalfInteger(value) {
        if (value === null || value === undefined) return '';
        if (value % 1 === 0) return value.toString();
        const numerator = value * 2;
        return `<sup>${numerator}</sup>&frasl;<sub>2</sub>`;
    }

    // --- UI UPDATE FUNCTIONS ---
    function updateM1Selector() {
        const container = document.querySelector('#m1-selector .button-container');
        container.innerHTML = '';
        for (let m = -state.j1; m <= state.j1; m++) {
            const btn = document.createElement('button');
            btn.dataset.value = m;
            btn.innerHTML = formatHalfInteger(m);
            btn.addEventListener('click', () => {
                state.m1 = m;
                setActiveButton('#m1-selector', m);
                resetAnimation();
                updateScene();
            });
            container.appendChild(btn);
        }
        if (Math.abs(state.m1) > state.j1) {
            state.m1 = 0;
        }
    }

    function updateM2Selector() {
        const container = document.querySelector('#m2-selector .button-container');
        container.innerHTML = '';
        for (let m = -state.j2; m <= state.j2; m++) {
            const btn = document.createElement('button');
            btn.dataset.value = m;
            btn.innerHTML = formatHalfInteger(m);
            btn.addEventListener('click', () => {
                state.m2 = m;
                setActiveButton('#m2-selector', m);
                resetAnimation();
                updateScene();
            });
            container.appendChild(btn);
        }
        if (Math.abs(state.m2) > state.j2) {
            state.m2 = 0;
        }
    }

    function updateJSelector() {
        const container = document.querySelector('#j-selector .button-container');
        container.innerHTML = '';
        const j_min = Math.abs(state.j1 - state.j2);
        const j_max = state.j1 + state.j2;
        for (let j_val = j_min; j_val <= j_max; j_val++) {
            const btn = document.createElement('button');
            btn.dataset.value = j_val;
            btn.innerHTML = formatHalfInteger(j_val);
            btn.addEventListener('click', () => {
                state.j = j_val;
                setActiveButton('#j-selector', j_val);
                resetAnimation();
                updateScene();
            });
            container.appendChild(btn);
        }
        if (state.j < j_min || state.j > j_max) {
             state.j = j_max;
        }
    }

    function setActiveButton(selectorId, value) {
        document.querySelectorAll(`${selectorId} button`).forEach(b => {
            if (parseFloat(b.dataset.value) === value) {
                b.classList.add('active');
            } else {
                b.classList.remove('active');
            }
        });
    }

    function resetAnimation() {
        state.isAnimating = false;
        state.phi_z = 0;
        state.phi_j = 0;
        btnPlayPause.textContent = '▶';
        updateVectorPositions();
    }

    // --- 3D SCENE LOGIC ---

    function getMagnitude(q) {
        if (q < 0) return 0;
        return Math.sqrt(q * (q + 1));
    }

    function createVector(name, color, length, dir) {
        const arrow = new THREE.ArrowHelper(dir, new THREE.Vector3(0,0,0), length, color, 0.2, 0.1);
        arrow.name = name;
        return arrow;
    }

    function createCone(name, color, radius, height, transparent = true) {
        const geometry = new THREE.ConeGeometry(radius, height, 64, 1, true);
        geometry.translate(0, height / 2, 0); // Apex at origin
        geometry.rotateX(Math.PI / 2); // Point along Z

        const material = new THREE.MeshLambertMaterial({
            color: color,
            transparent: transparent,
            opacity: 0.25,
            side: THREE.DoubleSide
        });

        const cone = new THREE.Mesh(geometry, material);
        cone.name = name;
        return cone;
    }

    function updateScene() {
        scene.remove(sceneObjects);
        sceneObjects = new THREE.Group();

        const Lj1 = getMagnitude(state.j1);
        const Lj2 = getMagnitude(state.j2);
        const Lj = getMagnitude(state.j);
        const m_total = state.m1 + state.m2;

        const isCoupled = state.j >= state.j1 && state.j >= state.j2;

        if (isCoupled && Lj > 0) {
            // --- Coupled Model ---
            const cos_theta_J_z = Math.max(-1, Math.min(1, m_total / Lj));
            const theta_J_z = Math.acos(cos_theta_J_z);

            // Vector J
            const dirJ = new THREE.Vector3(Math.sin(theta_J_z), 0, cos_theta_J_z);
            const vecJ = createVector('J', COLORS.J, Lj, dirJ);
            sceneObjects.add(vecJ);

            // Cone for J
            const coneJ = createCone('ConeJ', COLORS.CONE_J, Lj * Math.sin(theta_J_z), Lj);
            sceneObjects.add(coneJ);

            // Angles for J1, J2 relative to J (Law of Cosines)
            const cos_theta_J1_J = (Lj*Lj + Lj1*Lj1 - Lj2*Lj2) / (2 * Lj * Lj1);
            const theta_J1_J = Math.acos(Math.max(-1, Math.min(1, cos_theta_J1_J)));

            // Vectors J1 and J2
            const local_dirJ1 = new THREE.Vector3(Math.sin(theta_J1_J), 0, cos_theta_J1_J);
            const vecJ1 = createVector('J1', COLORS.J1, Lj1, local_dirJ1);

            const tempJ1 = new THREE.Vector3().copy(local_dirJ1).multiplyScalar(Lj1);
            const tempJ = new THREE.Vector3(0,0,Lj);
            const tempJ2 = new THREE.Vector3().subVectors(tempJ, tempJ1).normalize();
            const vecJ2 = createVector('J2', COLORS.J2, Lj2, tempJ2);

            sceneObjects.add(vecJ1, vecJ2);

            // Cones for J1, J2 around J
            const coneJ1 = createCone('ConeJ1', COLORS.CONE_J1, Lj1 * Math.sin(theta_J1_J), Lj1);
            const coneJ2 = createCone('ConeJ2', COLORS.CONE_J2, Lj2 * Math.sin(theta_J1_J), -Lj2); // Note: J1, J2 are opposed
            sceneObjects.add(coneJ1, coneJ2);

        } else {
            // --- Uncoupled Model ---
            if (Lj1 > 0) {
                const cos_theta_J1_z = Math.max(-1, Math.min(1, state.m1 / Lj1));
                const theta_J1_z = Math.acos(cos_theta_J1_z);
                const dirJ1 = new THREE.Vector3(Math.sin(theta_J1_z), 0, cos_theta_J1_z);
                const vecJ1 = createVector('J1', COLORS.J1, Lj1, dirJ1);
                sceneObjects.add(vecJ1);

                const coneJ1 = createCone('ConeJ1', COLORS.CONE_J1, Lj1 * Math.sin(theta_J1_z), Lj1);
                sceneObjects.add(coneJ1);
            }
             if (Lj2 > 0) {
                const cos_theta_J2_z = Math.max(-1, Math.min(1, state.m2 / Lj2));
                const theta_J2_z = Math.acos(cos_theta_J2_z);
                const dirJ2 = new THREE.Vector3(Math.sin(theta_J2_z), 0, cos_theta_J2_z);
                const vecJ2 = createVector('J2', COLORS.J2, Lj2, dirJ2);
                sceneObjects.add(vecJ2);

                const coneJ2 = createCone('ConeJ2', COLORS.CONE_J2, Lj2 * Math.sin(theta_J2_z), Lj2);
                sceneObjects.add(coneJ2);
            }
        }
        scene.add(sceneObjects);
        updateVectorPositions();
    }

    function updateVectorPositions() {
        const Lj1 = getMagnitude(state.j1);
        const Lj2 = getMagnitude(state.j2);
        const Lj = getMagnitude(state.j);
        const m_total = state.m1 + state.m2;

        const isCoupled = state.j >= state.j1 && state.j >= state.j2;

        const vecJ = sceneObjects.getObjectByName('J');
        const vecJ1 = sceneObjects.getObjectByName('J1');
        const vecJ2 = sceneObjects.getObjectByName('J2');
        const coneGroup = new THREE.Group();
        const coneJ1_c = sceneObjects.getObjectByName('ConeJ1');
        const coneJ2_c = sceneObjects.getObjectByName('ConeJ2');
        if(coneJ1_c) coneGroup.add(coneJ1_c);
        if(coneJ2_c) coneGroup.add(coneJ2_c);


        if (isCoupled && Lj > 0 && vecJ && vecJ1 && vecJ2) {
            // Precession around Z-axis
            const cos_theta_J_z = Math.max(-1, Math.min(1, m_total / Lj));
            const theta_J_z = Math.acos(cos_theta_J_z);
            const dirJ = new THREE.Vector3(
                Math.sin(theta_J_z) * Math.cos(state.phi_z),
                Math.sin(theta_J_z) * Math.sin(state.phi_z),
                cos_theta_J_z
            );
            vecJ.setDirection(dirJ);

            // Rotation to align J-frame with J vector
            const jFrameRotation = new THREE.Quaternion().setFromUnitVectors(Z_AXIS, dirJ);

            // Precession around J-axis
            const cos_theta_J1_J = (Lj*Lj + Lj1*Lj1 - Lj2*Lj2) / (2 * Lj * Lj1);
            const theta_J1_J = Math.acos(Math.max(-1, Math.min(1, cos_theta_J1_J)));

            const local_dirJ1 = new THREE.Vector3(
                Math.sin(theta_J1_J) * Math.cos(state.phi_j),
                Math.sin(theta_J1_J) * Math.sin(state.phi_j),
                cos_theta_J1_J
            ).normalize();

            const world_dirJ1 = local_dirJ1.clone().applyQuaternion(jFrameRotation);
            vecJ1.setDirection(world_dirJ1);

            const world_J1_vec = world_dirJ1.clone().multiplyScalar(Lj1);
            const world_J_vec = dirJ.clone().multiplyScalar(Lj);
            const world_J2_vec = new THREE.Vector3().subVectors(world_J_vec, world_J1_vec);
            vecJ2.setDirection(world_J2_vec.normalize());

            // Rotate the cones for J1 and J2
            coneGroup.quaternion.copy(jFrameRotation);

        } else { // Uncoupled
            if (Lj1 > 0 && vecJ1) {
                const cos_theta_J1_z = Math.max(-1, Math.min(1, state.m1 / Lj1));
                const theta_J1_z = Math.acos(cos_theta_J1_z);
                const dirJ1 = new THREE.Vector3(
                    Math.sin(theta_J1_z) * Math.cos(state.phi_z),
                    Math.sin(theta_J1_z) * Math.sin(state.phi_z),
                    cos_theta_J1_z
                );
                vecJ1.setDirection(dirJ1);
            }
             if (Lj2 > 0 && vecJ2) {
                const cos_theta_J2_z = Math.max(-1, Math.min(1, state.m2 / Lj2));
                const theta_J2_z = Math.acos(cos_theta_J2_z);
                const dirJ2 = new THREE.Vector3(
                    Math.sin(theta_J2_z) * Math.cos(-state.phi_z), // Different phase
                    Math.sin(theta_J2_z) * Math.sin(-state.phi_z),
                    cos_theta_J2_z
                );
                vecJ2.setDirection(dirJ2);
            }
        }
    }


    // --- INITIALIZATION and ANIMATION LOOP ---
    function init() {
        const container = document.getElementById('canvas-container');

        // Scene
        scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        // Camera
        camera = new THREE.PerspectiveCamera(50, container.clientWidth / container.clientHeight, 0.1, 100);
        camera.position.set(5, -7, 4);
        camera.up.set(0, 0, 1);

        // Renderer
        renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas'), antialias: true });
        renderer.setSize(container.clientWidth, container.clientHeight);
        renderer.setPixelRatio(window.devicePixelRatio);

        // Controls
        controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.1;

        // Lights
        scene.add(new THREE.AmbientLight(0x808080));
        const light = new THREE.DirectionalLight(0xffffff, 1);
        light.position.set(5, 5, 10);
        scene.add(light);

        // --- UI Initialization ---
        document.querySelectorAll('#j1-selector button').forEach(btn => {
            btn.addEventListener('click', () => {
                state.j1 = parseFloat(btn.dataset.value);
                setActiveButton('#j1-selector', state.j1);
                updateM1Selector();
                updateJSelector();
                state.m1 = 0; // Reset m1
                setActiveButton('#m1-selector', state.m1);
                state.j = state.j1 + state.j2; // Reset j
                setActiveButton('#j-selector', state.j);
                resetAnimation();
                updateScene();
            });
        });

        document.querySelectorAll('#j2-selector button').forEach(btn => {
            btn.addEventListener('click', () => {
                state.j2 = parseFloat(btn.dataset.value);
                setActiveButton('#j2-selector', state.j2);
                updateM2Selector();
                updateJSelector();
                state.m2 = 0; // Reset m2
                setActiveButton('#m2-selector', state.m2);
                state.j = state.j1 + state.j2; // Reset j
                setActiveButton('#j-selector', state.j);
                resetAnimation();
                updateScene();
            });
        });

        btnPlayPause.addEventListener('click', () => {
            state.isAnimating = !state.isAnimating;
            btnPlayPause.textContent = state.isAnimating ? '❚❚' : '▶';
        });

        btnReset.addEventListener('click', () => {
            resetAnimation();
        });

        // Set initial UI state
        updateM1Selector();
        updateM2Selector();
        updateJSelector();

        setActiveButton('#j1-selector', state.j1);
        setActiveButton('#m1-selector', state.m1);
        setActiveButton('#j2-selector', state.j2);
        setActiveButton('#m2-selector', state.m2);
        setActiveButton('#j-selector', state.j);

        // Initial scene draw
        updateScene();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        });

        animate();
    }

    function animate() {
        requestAnimationFrame(animate);

        if (state.isAnimating) {
            state.phi_z += 0.02;
            state.phi_j += 0.05;
            updateVectorPositions();
        }

        controls.update();
        renderer.render(scene, camera);
    }

    init();

</script>

</body>
</html>
