    <script>
        // --- DOM Elements ---
        const stepsSlider = document.getElementById('steps-slider');
        const stepsValue = document.getElementById('steps-value');
        const btnNone = document.getElementById('btn-none');
        const btnBox = document.getElementById('btn-box');
        const btnScattered = document.getElementById('btn-scattered');

        // --- Grid Constants ---
        const GRID_SIZE = 10;
        const CELL_SIZE = 20;
        const CANVAS_SIZE = GRID_SIZE * CELL_SIZE + 1;
        const START_X = 4;
        const START_Y = 4;
        const EMPTY = 0;
        const OBSTACLE = 1;

        // --- State Variables ---
        let initialGrid = [];
        let recursiveHistory = [];
        let iterativeHistory = [];
        let recursiveSketchInstance, iterativeSketchInstance;

        // --- Color Constants ---
        const COLOR_EMPTY = [255, 255, 255];
        const COLOR_OBSTACLE = [0, 0, 0];
        const COLOR_FILLED = [0, 0, 255];
        const COLOR_GRID_LINE = [0, 0, 0];


        // --- Grid Generation Logic ---
        function generateGrid(pattern) {
            const grid = Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(EMPTY));

            switch (pattern) {
                case 'none':
                    // All empty
                    break;
                case 'box':
                    for (let i = 0; i < GRID_SIZE; i++) {
                        grid[0][i] = OBSTACLE;
                        grid[GRID_SIZE - 1][i] = OBSTACLE;
                        grid[i][0] = OBSTACLE;
                        grid[i][GRID_SIZE - 1] = OBSTACLE;
                    }
                    break;
                case 'scattered':
                    // Use a fixed pattern to match the screenshot
                    const obstacles = [
                        {x: 0, y: 1}, {x: 2, y: 2}, {x: 4, y: 3}, {x: 1, y: 4},
                        {x: 2, y: 6}, {x: 8, y: 6}, {x: 5, y: 7}, {x: 0, y: 8},
                        {x: 7, y: 8}, {x: 9, y: 9}
                    ];
                    // Transpose coordinates for (row, col) / (y, x) access
                    obstacles.forEach(o => {
                        if (grid[o.y] && grid[o.y][o.x] !== undefined) {
                            grid[o.y][o.x] = OBSTACLE;
                        }
                    });
                    break;
            }

            // Ensure start cell is never an obstacle
            grid[START_Y][START_X] = EMPTY;
            return grid;
        }

        // --- Flood Fill Algorithms ---
        function runFloodFillAlgorithms() {
            // Recursive (DFS)
            recursiveHistory = [];
            const visitedRecursive = initialGrid.map(row => [...row]);
            function floodFillRecursive(x, y) {
                if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) return;
                if (visitedRecursive[y][x] !== EMPTY) return;

                visitedRecursive[y][x] = 2; // Mark as visited
                recursiveHistory.push({ x, y });

                // Neighbor order: Up, Left, Down, Right to match screenshot pattern
                floodFillRecursive(x, y - 1);
                floodFillRecursive(x - 1, y);
                floodFillRecursive(x, y + 1);
                floodFillRecursive(x + 1, y);
            }
            floodFillRecursive(START_X, START_Y);

            // Iterative (BFS)
            iterativeHistory = [];
            const visitedIterative = initialGrid.map(row => [...row]);
            const queue = [];

            if (visitedIterative[START_Y][START_X] === EMPTY) {
                queue.push({ x: START_X, y: START_Y });
                visitedIterative[START_Y][START_X] = 2; // Mark as visited
            }

            while (queue.length > 0) {
                const { x, y } = queue.shift();
                iterativeHistory.push({ x, y });

                // Neighbor order: Up, Right, Down, Left (standard BFS expansion)
                const neighbors = [
                    {x: x, y: y - 1}, 
                    {x: x + 1, y: y}, 
                    {x: x, y: y + 1}, 
                    {x: x - 1, y: y}
                ];

                for (const n of neighbors) {
                    if (n.x >= 0 && n.x < GRID_SIZE && n.y >= 0 && n.y < GRID_SIZE && visitedIterative[n.y][n.x] === EMPTY) {
                        visitedIterative[n.y][n.x] = 2;
                        queue.push(n);
                    }
                }
            }
        }

        // --- P5.js Sketch Definition ---
        const sketch = (p) => {
            p.setup = () => {
                p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                p.noLoop(); // We will manually trigger redraws
            };

            p.drawGrid = (history, step, grid) => {
                p.background(...COLOR_EMPTY);
                p.stroke(...COLOR_GRID_LINE);
                p.strokeWeight(1);

                const cellsToDraw = history.slice(0, step);
                const filledCells = new Set(cellsToDraw.map(cell => `${cell.x},${cell.y}`));

                for (let y = 0; y < GRID_SIZE; y++) {
                    for (let x = 0; x < GRID_SIZE; x++) {
                        if (grid[y][x] === OBSTACLE) {
                            p.fill(...COLOR_OBSTACLE);
                        } else if (filledCells.has(`${x},${y}`)) {
                            p.fill(...COLOR_FILLED);
                        } else {
                            p.fill(...COLOR_EMPTY);
                        }
                        p.rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
                    }
                }
            };
        };

        // --- Main Control Logic ---
        function resetSimulation(pattern) {
            // Update button styles
            [btnNone, btnBox, btnScattered].forEach(btn => btn.classList.remove('active'));
            if (pattern === 'none') btnNone.classList.add('active');
            if (pattern === 'box') btnBox.classList.add('active');
            if (pattern === 'scattered') btnScattered.classList.add('active');

            // Generate grid and run algorithms
            initialGrid = generateGrid(pattern);
            runFloodFillAlgorithms();

            // Update slider
            const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
            stepsSlider.max = maxSteps;
            stepsSlider.value = 0;
            stepsValue.textContent = '+ 0';

            // Redraw canvases
            drawBothGrids();
        }

        function drawBothGrids() {
            const step = parseInt(stepsSlider.value, 10);
            if (recursiveSketchInstance && recursiveSketchInstance.drawGrid) {
                recursiveSketchInstance.drawGrid(recursiveHistory, step, initialGrid);
            }
            if (iterativeSketchInstance && iterativeSketchInstance.drawGrid) {
                iterativeSketchInstance.drawGrid(iterativeHistory, step, initialGrid);
            }
        }

        // --- Event Listeners ---
        stepsSlider.addEventListener('input', () => {
            stepsValue.textContent = `+ ${stepsSlider.value}`;
            drawBothGrids();
        });

        btnNone.addEventListener('click', () => resetSimulation('none'));
        btnBox.addEventListener('click', () => resetSimulation('box'));
        btnScattered.addEventListener('click', () => resetSimulation('scattered'));

        // --- Initialization ---
        document.addEventListener('DOMContentLoaded', () => {
            recursiveSketchInstance = new p5(sketch, 'canvas-recursive');
            iterativeSketchInstance = new p5(sketch, 'canvas-iterative');

            // Wait a bit for p5 to initialize before the first draw
            setTimeout(() => {
                // Initial state as per requirements
                resetSimulation('scattered');

                // Set to the state shown in the first screenshot
                stepsSlider.value = 61;
                // Manually trigger event to update visuals
                stepsSlider.dispatchEvent(new Event('input')); 
            }, 100);
        });

    </script>
</body>
</html>
