    }
  </style>
</head>
<body>

  <div id="control-panel">
    <div>
      <label for="slider-k">k</label>
      <input type="range" id="slider-k" min="0.6" max="0.95" step="0.01" value="0.9">
    </div>
    <div>
      <label for="slider-time">time</label>
      <input type="range" id="slider-time" min="5" max="25" step="0.5" value="15">
    </div>
    <div>
      <label for="slider-vx">initial velocity in x</label>
      <input type="range" id="slider-vx" min="1" max="8" step="0.1" value="2.5">
    </div>
    <div>
      <label for="slider-dt">Δt</label>
      <input type="range" id="slider-dt" min="0.05" max="0.4" step="0.01" value="0.15">
    </div>
  </div>

  <div id="canvas-container">
    <!-- p5.js canvas will be created here -->
  </div>

  <script>
    let sliderK, sliderTime, sliderVx, sliderDt;

    function setup() {
      const canvas = createCanvas(700, 400);
      canvas.parent('canvas-container');

      sliderK = select('#slider-k');
      sliderTime = select('#slider-time');
      sliderVx = select('#slider-vx');
      sliderDt = select('#slider-dt');

      const sliders = [sliderK, sliderTime, sliderVx, sliderDt];
      sliders.forEach(slider => slider.input(() => redraw()));

      noLoop();
    }

    function draw() {
      // 1. Get current values from sliders
      const k = parseFloat(sliderK.value());
      const totalTime = parseFloat(sliderTime.value());
      const vx = parseFloat(sliderVx.value());
      const dt_strobe = parseFloat(sliderDt.value());

      // 2. Constants and Initialization
      const g = 9.8;
      const y0 = 10;
      const vy0 = 0;
      const x0 = 0;

      let currentTime = 0;
      let currentX = x0;
      let currentY = y0;
      let currentVy = vy0;

      const pathPoints = [];
      const strobePoints = [];

      // 3. Simulation Loop (Bounce-by-Bounce)
      while (currentTime < totalTime) {
        let startX = currentX;
        let startY = currentY;
        let startVy = currentVy;

        // Time of flight for a full parabolic arc
        const t_flight_full = (startVy + Math.sqrt(startVy * startVy + 2 * g * startY)) / g;

        // The actual time for this segment is the smaller of the full flight time or remaining simulation time
        const t_flight_segment = Math.min(t_flight_full, totalTime - currentTime);

        // Generate continuous path points for this arc segment
        const path_dt = 0.005; // small time step for a smooth curve
        for (let t = 0; t <= t_flight_segment; t += path_dt) {
          const pathX = startX + vx * t;
          const pathY = startY + startVy * t - 0.5 * g * t * t;
          if (pathY >= -1e-6) { // Use a small tolerance for floating point errors
            pathPoints.push({ x: pathX, y: pathY });
          }
        }
        // Ensure the final point of the segment is included
        const finalPathX = startX + vx * t_flight_segment;
        const finalPathY = startY + startVy * t_flight_segment - 0.5 * g * t_flight_segment * t_flight_segment;
        if (finalPathY >= -1e-6) {
            pathPoints.push({x: finalPathX, y: Math.max(0, finalPathY)});
        }

        // Generate stroboscopic points for this arc segment
        let nextStrobeTime = Math.ceil(currentTime / dt_strobe) * dt_strobe;
        while (nextStrobeTime < currentTime + t_flight_segment && nextStrobeTime <= totalTime) {
          if (nextStrobeTime >= currentTime) { // Ensure we don't calculate points from the past
            const t_in_arc = nextStrobeTime - currentTime;
            const strobeX = startX + vx * t_in_arc;
            const strobeY = startY + startVy * t_in_arc - 0.5 * g * t_in_arc * t_in_arc;
            if (strobeY >= -1e-6) {
              strobePoints.push({ x: strobeX, y: Math.max(0, strobeY) });
            }
          }
          nextStrobeTime += dt_strobe;
        }

        // If simulation ends mid-arc, stop the loop
        if (currentTime + t_flight_full >= totalTime) {
          break;
        }

        // Update state for the next bounce
        const vy_impact = Math.sqrt(startVy * startVy + 2 * g * startY);
        currentTime += t_flight_full;
        currentX += vx * t_flight_full;
        currentY = 0;
        currentVy = Math.sqrt(k) * vy_impact;
      }

      // 4. Rendering
      background(0, 0, 0);

      const padding = 40;
      const drawableWidth = width - 2 * padding;
      const drawableHeight = height - 2 * padding;

      const maxX = Math.max(1, vx * totalTime); // Prevent division by zero if time is very short
      const maxY = y0;

      const scaleX = drawableWidth / maxX;
      const scaleY = drawableHeight / maxY;

      push();
      translate(padding, height - padding);
      scale(1, -1); // Flip Y-axis to have origin at bottom-left

      // Draw Axes and Ticks
      stroke(100, 100, 150);
      strokeWeight(1);
      line(0, 0, drawableWidth, 0); // X-Axis
      line(0, 0, 0, drawableHeight); // Y-Axis

      // X-Ticks
      const xTickInterval = Math.max(1, Math.round(maxX / 15)); // Dynamic tick interval
      for (let i = 0; i <= maxX; i += xTickInterval) {
        line(i * scaleX, 0, i * scaleX, -8); 
      }
      // Y-Ticks
      const yTickInterval = 1;
      for (let i = 0; i <= maxY; i += yTickInterval) {
        line(0, i * scaleY, -8, i * scaleY);
      }

      // Draw Trajectory Path (Red Line)
      noFill();
      stroke(255, 0, 0);
      strokeWeight(2);
      beginShape();
      for (const p of pathPoints) {
        vertex(p.x * scaleX, p.y * scaleY);
      }
      endShape();

      // Draw Stroboscopic Points (Yellow Dots)
      noStroke();
      fill(255, 255, 0);
      for (const p of strobePoints) {
        ellipse(p.x * scaleX, p.y * scaleY, 8, 8);
      }

      pop();
    }
  </script>

</body>
</html>
