<body>
    <header>
        <h1>Angle Bisectors on the Circumcircle</h1>
        <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    </header>

    <main>
        <div id="canvas-container"></div>
    </main>

    <script>
        let points = {};
        let derivedPoints = {};
        let center;
        const radius = 200;
        let draggedPoint = null;
        const dragTolerance = 15;

        function setup() {
            let canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');
            canvas.id('p5-canvas');

            center = createVector(width / 2, height / 2);

            // Initialize interactive points with initial angular positions
            points = {
                A: { label: 'A', angle: radians(210), pos: createVector(0, 0) },
                B: { label: 'B', angle: radians(330), pos: createVector(0, 0) },
                C: { label: 'C', angle: radians(90), pos: createVector(0, 0) }
            };

            // Initialize derived points (using "p" suffix for prime)
            derivedPoints = {
                Ap: { label: "A'", pos: createVector(0, 0) },
                Bp: { label: "B'", pos: createVector(0, 0) },
                Cp: { label: "C'", pos: createVector(0, 0) }
            };

            // Calculate initial positions
            updateGeometry();
        }

        function draw() {
            background('#F0F0F0');

            // If dragging, update the dragged point's angle based on mouse position
            if (draggedPoint) {
                let mouseAngle = atan2(mouseY - center.y, mouseX - center.y);
                draggedPoint.angle = mouseAngle;
            }

            updateGeometry();
            drawGeometry();
        }

        function updateGeometry() {
            // Update Cartesian coordinates for interactive points A, B, C from their angles
            for (let key in points) {
                let p = points[key];
                p.pos.x = center.x + radius * cos(p.angle);
                p.pos.y = center.y + radius * sin(p.angle);
            }

            const { A, B, C } = points;

            // Calculate angles for derived points A', B', C'
            // A' is the midpoint of arc BC, B' of arc AC, C' of arc AB
            let angleAp = atan2(sin(B.angle) + sin(C.angle), cos(B.angle) + cos(C.angle));
            let angleBp = atan2(sin(A.angle) + sin(C.angle), cos(A.angle) + cos(C.angle));
            let angleCp = atan2(sin(A.angle) + sin(B.angle), cos(A.angle) + cos(B.angle));

            // Update Cartesian coordinates for derived points A', B', C'
            derivedPoints.Ap.pos.x = center.x + radius * cos(angleAp);
            derivedPoints.Ap.pos.y = center.y + radius * sin(angleAp);

            derivedPoints.Bp.pos.x = center.x + radius * cos(angleBp);
            derivedPoints.Bp.pos.y = center.y + radius * sin(angleBp);

            derivedPoints.Cp.pos.x = center.x + radius * cos(angleCp);
            derivedPoints.Cp.pos.y = center.y + radius * sin(angleCp);
        }

        function drawGeometry() {
            const { A, B, C } = points;
            const { Ap, Bp, Cp } = derivedPoints;

            // Draw Circumcircle
            noFill();
            strokeWeight(4);
            stroke('#6A5ACD'); // SlateBlue
            ellipse(center.x, center.y, radius * 2);

            // Draw Triangle ABC
            line(A.pos.x, A.pos.y, B.pos.x, B.pos.y);
            line(B.pos.x, B.pos.y, C.pos.x, C.pos.y);
            line(C.pos.x, C.pos.y, A.pos.x, A.pos.y);

            // Draw extended bisectors & Triangle A'B'C'
            stroke('#FFA500'); // Orange
            // Bisectors
            line(A.pos.x, A.pos.y, Ap.pos.x, Ap.pos.y);
            line(B.pos.x, B.pos.y, Bp.pos.x, Bp.pos.y);
            line(C.pos.x, C.pos.y, Cp.pos.x, Cp.pos.y);
            // Triangle A'B'C'
            line(Ap.pos.x, Ap.pos.y, Bp.pos.x, Bp.pos.y);
            line(Bp.pos.x, Bp.pos.y, Cp.pos.x, Cp.pos.y);
            line(Cp.pos.x, Cp.pos.y, Ap.pos.x, Ap.pos.y);

            // Draw all points and labels
            drawPoint(A, true);
            drawPoint(B, true);
            drawPoint(C, true);

            drawPoint(Ap, false);
            drawPoint(Bp, false);
            drawPoint(Cp, false);
        }

        function drawPoint(p, isInteractive) {
            // Draw the point circle
            strokeWeight(1);
            stroke('#000000');
            fill('#FFA500'); // Orange
            ellipse(p.pos.x, p.pos.y, 12, 12);

            // Draw crosshair if it's an interactive point (A, B, C)
            if (isInteractive) {
                push();
                stroke('#000000');
                strokeWeight(1.5);
                line(p.pos.x - 4, p.pos.y, p.pos.x + 4, p.pos.y);
                line(p.pos.x, p.pos.y - 4, p.pos.x, p.pos.y + 4);
                pop();
            }

            // Draw the label, offset radially outward
            let offsetVector = p5.Vector.sub(p.pos, center).normalize().mult(20);
            let labelPos = p5.Vector.add(p.pos, offsetVector);

            noStroke();
            fill('#000000');
            textSize(16);
            textAlign(CENTER, CENTER);
            text(p.label, labelPos.x, labelPos.y);
        }

        function mousePressed() {
            // Check if mouse is over any interactive point
            for (let key in points) {
                let p = points[key];
                if (dist(mouseX, mouseY, p.pos.x, p.pos.y) < dragTolerance) {
                    draggedPoint = p;
                    return; // Found a point, exit
                }
            }
        }

        function mouseReleased() {
            // Stop dragging
            draggedPoint = null;
        }

        // Prevent default browser behavior (e.g., text selection) when dragging on the canvas
        function mouseDragged() {
            if (draggedPoint) {
                return false;
            }
        }
    </script>
</body>
</html>
