                // Factory
                p.fill('#696969'); p.rect(480, 220, 80, 180); 
                p.fill('#A9A9A9'); p.rect(500, 160, 20, 60); p.ellipse(510, 160, 30, 10); p.ellipse(510, 155, 25, 8); 

