                // Sheep
                p.fill(255); p.ellipse(210, 250, 50, 30); p.ellipse(200, 240, 20, 20); p.ellipse(215, 238, 20, 20); p.ellipse(225, 245, 20, 20); p.ellipse(230, 255, 20, 20); 
                p.fill(0); p.ellipse(233, 253, 2, 2); 

