        <div id="toggle-velocity" class="toggle-switch">
          <input type="radio" id="radio-velocity-off" name="velocity" value="off" checked>
          <label for="radio-velocity-off">off</label>
          <input type="radio" id="radio-velocity-on" name="velocity" value="on">
          <label for="radio-velocity-on">on</label>
        </div>

        <label>acceleration</label>
        <div id="toggle-acceleration" class="toggle-switch">
          <input type="radio" id="radio-acceleration-off" name="acceleration" value="off">
          <label for="radio-acceleration-off">off</label>
          <input type="radio" id="radio-acceleration-on" name="acceleration" value="on" checked>
          <label for="radio-acceleration-on">on</label>
        </div>

        <label for="select-coords">coordinate system</label>
        <select id="select-coords">
          <option value="rectangular">rectangular</option>
          <option value="normal-tangential">normal-tangential</option>
          <option value="polar">polar</option>
        </select>

        <label>instantaneous circle</label>
        <div id="toggle-circle" class="toggle-switch">
          <input type="radio" id="radio-circle-off" name="circle" value="off" checked>
          <label for="radio-circle-off">off</label>
          <input type="radio" id="radio-circle-on" name="circle" value="on">
          <label for="radio-circle-on">on</label>
        </div>
      </div>

      <div id="canvas-container">
        <!-- p5.js canvas will be inserted here -->
      </div>
    </div>

    <script>
        const sketch = (p) => {
            // --- STATE ---
            const state = {
                time: 0.4,
                position: 'off',
                velocity: 'off',
                acceleration: 'on',
                coords: 'rectangular',
                circle: 'off'
            };

            // --- CONTROLS ---
            let sliderTime, btnResetTime, togglePosition, toggleVelocity, 
                toggleAcceleration, selectCoords, toggleCircle;

            // --- DRAWING PARAMS ---
            let scaleFactor = 100;
            const logicalWidth = 5;

            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const w = canvasContainer.offsetWidth - 20;
                const h = canvasContainer.offsetHeight - 20;
                const canvas = p.createCanvas(w, h);
                canvas.parent(canvasContainer);

                scaleFactor = p.min(w, h) / logicalWidth;

                // --- GET HTML ELEMENTS ---
                sliderTime = document.getElementById('slider-time');
                btnResetTime = document.getElementById('btn-reset-time');
                togglePosition = document.getElementById('toggle-position');
                toggleVelocity = document.getElementById('toggle-velocity');
                toggleAcceleration = document.getElementById('toggle-acceleration');
                selectCoords = document.getElementById('select-coords');
                toggleCircle = document.getElementById('toggle-circle');

                // --- SET INITIAL VALUES from state object ---
                sliderTime.value = state.time;
                document.querySelector(`input[name="position"][value="${state.position}"]`).checked = true;
                document.querySelector(`input[name="velocity"][value="${state.velocity}"]`).checked = true;
                document.querySelector(`input[name="acceleration"][value="${state.acceleration}"]`).checked = true;
                selectCoords.value = state.coords;
                document.querySelector(`input[name="circle"][value="${state.circle}"]`).checked = true;

                // --- ADD EVENT LISTENERS ---
                sliderTime.addEventListener('input', e => {
                    state.time = parseFloat(e.target.value);
                    p.redraw();
                });
                btnResetTime.addEventListener('click', () => {
                    state.time = 0.4;
                    sliderTime.value = state.time;
                    p.redraw();
                });
                const addToggleListener = (groupEl, stateKey) => {
                    groupEl.addEventListener('change', e => {
                        state[stateKey] = e.target.value;
                        p.redraw();
                    });
                };
                addToggleListener(togglePosition, 'position');
                addToggleListener(toggleVelocity, 'velocity');
                addToggleListener(toggleAcceleration, 'acceleration');
                addToggleListener(toggleCircle, 'circle');

                selectCoords.addEventListener('change', e => {
                    state.coords = e.target.value;
                    p.redraw();
                });

                p.noLoop();
                p.redraw();
            };

            p.draw = () => {
                p.background(255);
                p.translate(p.width / 2, p.height / 2);
                p.scale(scaleFactor, -scaleFactor); // Flip Y-axis

                drawAxes();
                drawLemniscateCurve();

                // --- KINEMATICS ---
                const t = state.time;
                const r = p.createVector(p.cos(t), p.sin(2 * t)); // Position
                const v = p.createVector(-p.sin(t), 2 * p.cos(2 * t)); // Velocity
                const a = p.createVector(-p.cos(t), -4 * p.sin(2 * t)); // Acceleration

                // Draw point on curve
                p.push();
                p.fill(0);
                p.noStroke();
                p.ellipse(r.x, r.y, 0.08, 0.08);
                p.pop();

                // --- DRAW VECTORS & CIRCLE BASED ON STATE ---
                if (state.position === 'on') {
                    drawArrow(p.createVector(0,0), r, p.color(0, 100, 200), 'r');
                }
                if (state.velocity === 'on') {
                    drawArrow(r, v, p.color(180, 0, 0), 'v');
                }
                if (state.acceleration === 'on') {
                    drawArrow(r, a, p.color(150, 0, 150), 'a');
                }

                drawBasisVectors(r, v, a);

                if (state.circle === 'on') {
                    drawInstantaneousCircle(r, v, a);
                }
            };

            function drawAxes() {
                p.push();
                p.stroke(200);
                p.strokeWeight(1 / scaleFactor);

                // Axes
                p.line(-p.width, 0, p.width, 0);
                p.line(0, -p.height, 0, p.height);

                p.fill(0);
                p.noStroke();
                p.textSize(16 / scaleFactor);
                p.textAlign(p.CENTER, p.CENTER);

                // X Ticks & Labels
                for (let i = -2; i <= 2; i++) {
                    if (i === 0) continue;
                    p.stroke(200);
                    p.line(i, -0.05, i, 0.05);
                    p.noStroke();
                    p.push();
                    p.scale(1, -1);
                    p.text(i, i, -0.2);
                    p.pop();
                }
                p.push();
                p.scale(1,-1);
                p.text('x(t)', 2.2, 0.2);
                p.pop();

                // Y Ticks & Labels
                for (let i = -2; i <= 2; i++) {
                    if (i === 0) continue;
                    p.stroke(200);
                    p.line(-0.05, i, 0.05, i);
                    p.noStroke();
                    p.push();
                    p.scale(1, -1);
                    p.text(i, -0.2, -i);
                    p.pop();
                }
                 p.push();
                p.scale(1,-1);
                p.text('y(t)', 0.2, -2.2);
                p.pop();
                p.pop();
            }

            function drawLemniscateCurve() {
                p.push();
                p.noFill();
                p.stroke(0, 0, 255);
                p.strokeWeight(2 / scaleFactor);
                p.beginShape();
                for (let t = 0; t <= p.TWO_PI; t += 0.01) {
                    p.vertex(p.cos(t), p.sin(2 * t));
                }
                p.endShape();
                p.pop();
            }

            function drawArrow(base, vec, color) {
                p.push();
                p.stroke(color);
                p.strokeWeight(2 / scaleFactor);
                p.fill(color);
                p.translate(base.x, base.y);
                p.line(0, 0, vec.x, vec.y);
                p.rotate(vec.heading());
                let arrowSize = 0.1;
                p.translate(vec.mag() - arrowSize, 0);
                p.triangle(0, arrowSize / 2.5, 0, -arrowSize / 2.5, arrowSize, 0);
                p.pop();
            }

            function drawDashedArrow(base, vec, label) {
                p.push();
                p.translate(base.x, base.y);
                p.stroke(0);
                p.strokeWeight(1.5 / scaleFactor);
                p.drawingContext.setLineDash([5, 5]);
                p.line(0, 0, vec.x, vec.y);
                p.drawingContext.setLineDash([]);

                // Arrowhead
                p.push();
                p.noStroke();
                p.fill(0);
                p.translate(vec.x, vec.y);
                p.rotate(vec.heading());
                let arrowSize = 0.08;
                p.triangle(0, arrowSize / 2, 0, -arrowSize / 2, arrowSize, 0);
                p.pop();

                // Label
                p.noStroke();
                p.fill(0);
                p.textSize(20 / scaleFactor);
                p.textAlign(p.CENTER, p.CENTER);
                p.push();
                p.translate(vec.x * 1.3, vec.y * 1.3);
                p.scale(1, -1);
                p.text(label, 0, 0);
                p.pop();

                p.pop();
            }

            function drawBasisVectors(r, v, a) {
                const basisLength = 0.5;
                switch (state.coords) {
                    case 'rectangular': {
                        const i_hat = p.createVector(basisLength, 0);
                        const j_hat = p.createVector(0, basisLength);
                        drawDashedArrow(r, i_hat, '\u00EE'); // î
                        drawDashedArrow(r, j_hat, '\u0135'); // ĵ
                        break;
                    }
                    case 'normal-tangential': {
                        if (v.mag() < 1e-6) return; // Avoid division by zero
                        const e_T = v.copy().normalize().mult(basisLength);

                        // Use acceleration to find normal vector direction
                        const v_mag_sq = v.magSq();
                        const a_dot_v = a.dot(v);
                        const a_T_vec = v.copy().mult(a_dot_v / v_mag_sq);
                        const a_N_vec = p5.Vector.sub(a, a_T_vec);
                        if (a_N_vec.mag() < 1e-6) return; // Happens at inflection points
                        const e_N = a_N_vec.normalize().mult(basisLength);

                        drawDashedArrow(r, e_T, 'ê\u209C'); // ê_T
                        drawDashedArrow(r, e_N, 'ê\u2099'); // ê_N
                        break;
                    }
                    case 'polar': {
                        if (r.mag() < 1e-6) return; // Avoid division by zero at origin
                        const e_r = r.copy().normalize().mult(basisLength);
                        const e_theta = e_r.copy().rotate(p.HALF_PI);
                        drawDashedArrow(r, e_r, 'ê\u1D63'); // ê_r
                        drawDashedArrow(r, e_theta, 'ê\u2097'); // ê_θ
                        break;
                    }
                }
            }

            function drawInstantaneousCircle(r, v, a) {
                const v_mag_sq = v.magSq();
                const v_mag = p.sqrt(v_mag_sq);
                if (v_mag < 1e-6) return;

                const cross_prod_z = v.x * a.y - v.y * a.x;
                if (p.abs(cross_prod_z) < 1e-6) return; // No curvature

                const rho = p.pow(v_mag, 3) / p.abs(cross_prod_z); // Radius of curvature

                // Normal vector calculation
                const a_dot_v = a.dot(v);
                const a_T_vec = v.copy().mult(a_dot_v / v_mag_sq);
                const a_N_vec = p5.Vector.sub(a, a_T_vec);
                if (a_N_vec.mag() < 1e-6) return; // Inflection point

                const e_N = a_N_vec.normalize();

                const center = p5.Vector.add(r, e_N.mult(rho));

                p.push();
                p.noFill();
                p.stroke(150);
                p.strokeWeight(1 / scaleFactor);
                p.drawingContext.setLineDash([5, 5]);
                p.ellipse(center.x, center.y, rho * 2, rho * 2);
                p.drawingContext.setLineDash([]);
                p.pop();
            }
        };

        new p5(sketch, 'canvas-container');
    </script>
</body>
</html>
