             <div class="control-group" style="grid-column: 2 / span 2; display: contents;">
                <input type="range" id="slider-stiffness" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="stiffness-value">1.00</span>
            </div>

            <label for="slider-mass">mass</label>
             <div class="control-group" style="grid-column: 2 / span 2; display: contents;">
                <input type="range" id="slider-mass" min="0.1" max="10.0" step="0.01" value="1.0">
                <span id="mass-value">1.00</span>
            </div>

            <label>time</label>
            <div id="time-controls">
                <button id="btn-step-back">◀</button>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-forward">▶|</button>
            </div>

            <button id="btn-reset" title="Reset Simulation">&times;</button>
        </div>
        <div id="visualization-container">
            <div id="plot-canvas-container"></div>
            <div id="animation-canvas-container"></div>
        </div>
    </div>

    <script>
        // --- DOM Elements ---
        const sliderAmplitude = document.getElementById('slider-amplitude');
        const sliderStiffness = document.getElementById('slider-stiffness');
        const sliderMass = document.getElementById('slider-mass');
        const amplitudeValue = document.getElementById('amplitude-value');
        const stiffnessValue = document.getElementById('stiffness-value');
        const massValue = document.getElementById('mass-value');
        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnStepBack = document.getElementById('btn-step-back');
        const btnStepForward = document.getElementById('btn-step-forward');
        const btnReset = document.getElementById('btn-reset');

        // --- State Variables ---
        let amplitude, stiffness, mass, angularFrequency;
        let time;
        let isPlaying;
        let positionHistory;

        // --- Simulation Constants ---
        const TIME_STEP = 0.05;
        const PLOT_COLOR = '#6363B2';
        const DEFAULTS = {
            amplitude: 0.3,
            stiffness: 1.0,
            mass: 1.0,
        };

        // --- p5.js Sketch Instances ---
        let plotSketchInstance;
        let animSketchInstance;

        // --- Plot Sketch ---
        const plotSketch = (p) => {
            let xMax = 25;

            p.setup = () => {
                const container = document.getElementById('plot-canvas-container');
                p.createCanvas(container.offsetWidth, container.offsetHeight);
                p.noLoop(); // Start paused
            };

            p.draw = () => {
                // Main animation loop logic
                if (isPlaying) {
                    time += TIME_STEP;
                    const position = amplitude * Math.sin(angularFrequency * time);
                    positionHistory.push({ t: time, y: position });
                }

                // Adjust plot x-axis range if time exceeds it
                if (time > xMax) {
                    xMax *= 1.5;
                }

                // --- Drawing ---
                p.background(255);
                p.stroke(220);
                p.strokeWeight(1);

                // Map coordinates
                const mapX = val => p.map(val, 0, xMax, 50, p.width - 20);
                const mapY = val => p.map(val, -1, 1, p.height - 30, 30);

                // Draw Axes
                p.stroke(0);
                p.line(mapX(0), mapY(-1.1), mapX(0), mapY(1.1)); // Y-axis
                p.line(mapX(-0.1), mapY(0), mapX(xMax), mapY(0)); // X-axis

                // Draw Grid & Labels
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(12);
                p.noStroke();
                p.fill(150);

                // Y-axis labels and grid
                for (let yVal of [-1, -0.5, 0, 0.5, 1]) {
                    const yPos = mapY(yVal);
                    p.text(yVal.toFixed(0), 25, yPos);
                    p.stroke(240);
                    p.line(mapX(0), yPos, mapX(xMax), yPos);
                }

                // X-axis labels and grid
                const xTickStep = Math.pow(10, Math.floor(Math.log10(xMax))) / 2;
                for (let xVal = 0; xVal <= xMax; xVal += xTickStep) {
                     if (xVal === 0) continue;
                     const xPos = mapX(xVal);
                     p.text(Math.round(xVal), xPos, p.height - 15);
                     p.stroke(240);
                     p.line(xPos, mapY(-1), xPos, mapY(1));
                }

                // Draw Waveform
                p.noFill();
                p.stroke(PLOT_COLOR);
                p.strokeWeight(2);
                p.beginShape();
                if (positionHistory.length > 0) {
                    positionHistory.forEach(point => {
                        p.vertex(mapX(point.t), mapY(point.y));
                    });
                } else {
                     p.vertex(mapX(0), mapY(0));
                     p.vertex(mapX(xMax), mapY(0));
                }
                p.endShape();

                // Redraw the animation sketch
                if (animSketchInstance) {
                    animSketchInstance.redraw();
                }
            };

            p.windowResized = () => {
                const container = document.getElementById('plot-canvas-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
            }
        };

        // --- Animation Sketch ---
        const animSketch = (p) => {
            p.setup = () => {
                const container = document.getElementById('animation-canvas-container');
                p.createCanvas(container.offsetWidth, container.offsetHeight);
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                // Map coordinates
                const mapY = val => p.map(val, -1.2, 1.2, p.height - 20, 20);

                // Draw axis and labels
                p.stroke(150);
                p.line(p.width / 2, mapY(-1.2), p.width / 2, mapY(1.2));
                p.textAlign(p.RIGHT, p.CENTER);
                p.textSize(12);
                p.noStroke();
                p.fill(150);
                for(let yVal of [-1, 0, 1]) {
                    p.text(yVal, p.width/2 - 10, mapY(yVal));
                }

                // Calculate current position
                const currentY = amplitude * Math.sin(angularFrequency * time);

                // Draw anchor
                const anchorY = mapY(1.2);
                p.stroke(0);
                p.strokeWeight(3);
                p.line(p.width * 0.2, anchorY, p.width * 0.8, anchorY);

                // Draw mass
                const massSize = 40;
                const massTopY = mapY(currentY);
                p.fill(PLOT_COLOR);
                p.noStroke();
                p.rectMode(p.CORNERS);
                p.rect(p.width/2 - massSize/2, massTopY, p.width/2 + massSize/2, massTopY + massSize);

                // Draw spring
                p.stroke(0);
                p.strokeWeight(1.5);
                p.noFill();
                const springTopY = anchorY + 2;
                const springBottomY = massTopY;
                const springWidth = 15;
                const numCoils = 10;

                p.beginShape();
                p.vertex(p.width / 2, springTopY);
                for (let i = 1; i < numCoils * 2; i++) {
                    const y = p.lerp(springTopY, springBottomY, i / (numCoils * 2));
                    const x = p.width / 2 + springWidth * (i % 2 === 0 ? -1 : 1);
                    p.vertex(x, y);
                }
                p.vertex(p.width / 2, springBottomY);
                p.endShape();
            };

             p.windowResized = () => {
                const container = document.getElementById('animation-canvas-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
            }
        };

        // --- Control Logic ---
        function updateStateFromSliders() {
            amplitude = parseFloat(sliderAmplitude.value);
            stiffness = parseFloat(sliderStiffness.value);
            mass = parseFloat(sliderMass.value);

            amplitudeValue.textContent = amplitude.toFixed(3);
            stiffnessValue.textContent = stiffness.toFixed(2);
            massValue.textContent = mass.toFixed(2);

            angularFrequency = Math.sqrt(stiffness / mass);
        }

        function resetSimulation() {
            sliderAmplitude.value = DEFAULTS.amplitude;
            sliderStiffness.value = DEFAULTS.stiffness;
            sliderMass.value = DEFAULTS.mass;

            isPlaying = false;
            btnPlayPause.textContent = '▶';
            if (plotSketchInstance) plotSketchInstance.noLoop();

            time = 0;
            positionHistory = [];

            updateStateFromSliders();

            if (plotSketchInstance) {
                plotSketchInstance.xMax = 25; // Reset zoom
                plotSketchInstance.redraw();
            }
            if (animSketchInstance) animSketchInstance.redraw();
        }

        function handleParameterChange() {
            isPlaying = false;
            btnPlayPause.textContent = '▶';
            plotSketchInstance.noLoop();

            time = 0;
            positionHistory = [];

            updateStateFromSliders();

            plotSketchInstance.xMax = 25; // Reset zoom
            plotSketchInstance.redraw();
            animSketchInstance.redraw();
        }

        // --- Event Listeners ---
        sliderAmplitude.addEventListener('input', handleParameterChange);
        sliderStiffness.addEventListener('input', handleParameterChange);
        sliderMass.addEventListener('input', handleParameterChange);

        btnPlayPause.addEventListener('click', () => {
            isPlaying = !isPlaying;
            if (isPlaying) {
                btnPlayPause.textContent = '||';
                plotSketchInstance.loop();
            } else {
                btnPlayPause.textContent = '▶';
                plotSketchInstance.noLoop();
            }
        });

        btnStepForward.addEventListener('click', () => {
            if (!isPlaying) {
                time += TIME_STEP;
                const position = amplitude * Math.sin(angularFrequency * time);
                positionHistory.push({ t: time, y: position });
                plotSketchInstance.redraw();
            }
        });

        btnStepBack.addEventListener('click', () => {
            if (!isPlaying && time > 0) {
                positionHistory.pop();
                time = Math.max(0, time - TIME_STEP);
                plotSketchInstance.redraw();
            }
        });

        btnReset.addEventListener('click', resetSimulation);

        window.addEventListener('resize', () => {
           if(plotSketchInstance) plotSketchInstance.windowResized();
           if(animSketchInstance) animSketchInstance.windowResized();
        });


        // --- Initialization ---
        document.addEventListener('DOMContentLoaded', () => {
            plotSketchInstance = new p5(plotSketch, 'plot-canvas-container');
            animSketchInstance = new p5(animSketch, 'animation-canvas-container');
            resetSimulation();
        });

    </script>

</body>
</html>
