            width: 40px;
            height: 40px;
            border: 1px solid #333;
            display: flex;
            justify-content: center;
            align-items: center;
            cursor: pointer;
            user-select: none;
            transition: background-color 0.2s;
        }

        .grid-cell span {
            font-size: 16px;
            font-weight: bold;
            color: #333;
        }

        .cell-on {
            background-color: #FFD700; /* Gold/Yellow */
        }

        .cell-off {
            background-color: #D3D3D3; /* Light Gray */
        }
    </style>
</head>
<body>

    <div class="main-container">
        <div class="control-panel">
            <span>distance function</span>
            <button id="btn-manhattan">Manhattan</button>
            <button id="btn-euclidean">squared Euclidean</button>
            <button id="btn-chebyshev">Chebyshev</button>
        </div>
        <div id="grid-container">
            <!-- Grid will be generated by JavaScript -->
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- STATE VARIABLES ---
            const ROWS = 9;
            const COLS = 7;
            let currentDistanceFunction = 'manhattan';
            let gridState = Array(ROWS).fill(null).map(() => Array(COLS).fill(false));

            // --- DOM ELEMENTS ---
            const gridContainer = document.getElementById('grid-container');
            const buttons = {
                manhattan: document.getElementById('btn-manhattan'),
                euclidean: document.getElementById('btn-euclidean'),
                chebyshev: document.getElementById('btn-chebyshev'),
            };

            // --- INITIALIZATION ---
            function init() {
                // Set initial "on" cells to match the first snapshot
                gridState[2][1] = true;
                gridState[6][4] = true;

                // Create grid cells
                for (let r = 0; r < ROWS; r++) {
                    for (let c = 0; c < COLS; c++) {
                        const cell = document.createElement('div');
                        cell.classList.add('grid-cell');
                        cell.dataset.row = r;
                        cell.dataset.col = c;

                        const span = document.createElement('span');
                        cell.appendChild(span);

                        cell.addEventListener('click', handleCellClick);
                        gridContainer.appendChild(cell);
                    }
                }

                // Setup button listeners
                Object.keys(buttons).forEach(key => {
                    buttons[key].addEventListener('click', () => handleDistanceChange(key));
                });

                // Initial render
                updateActiveButton();
                redrawGrid();
            }

            // --- EVENT HANDLERS ---
            function handleCellClick(event) {
                const cell = event.currentTarget;
                const r = parseInt(cell.dataset.row, 10);
                const c = parseInt(cell.dataset.col, 10);

                // Toggle the state
                gridState[r][c] = !gridState[r][c];

                // Recalculate and redraw
                redrawGrid();
            }

            function handleDistanceChange(newFunction) {
                if (currentDistanceFunction !== newFunction) {
                    currentDistanceFunction = newFunction;
                    updateActiveButton();
                    redrawGrid();
                }
            }

            // --- CALCULATION LOGIC ---
            function calculateDistances() {
                const distanceGrid = Array(ROWS).fill(null).map(() => Array(COLS).fill(Infinity));
                const sourcePoints = [];

                // Find all "on" cells (source points)
                for (let r = 0; r < ROWS; r++) {
                    for (let c = 0; c < COLS; c++) {
                        if (gridState[r][c]) {
                            sourcePoints.push({ r, c });
                        }
                    }
                }

                if (sourcePoints.length === 0) {
                    return distanceGrid; // All distances are Infinity
                }

                // Calculate the minimum distance for each cell
                for (let r = 0; r < ROWS; r++) {
                    for (let c = 0; c < COLS; c++) {
                        let minDistance = Infinity;
                        for (const source of sourcePoints) {
                            let distance;
                            switch (currentDistanceFunction) {
                                case 'manhattan':
                                    distance = Math.abs(r - source.r) + Math.abs(c - source.c);
                                    break;
                                case 'euclidean':
                                    distance = Math.pow(r - source.r, 2) + Math.pow(c - source.c, 2);
                                    break;
                                case 'chebyshev':
                                    distance = Math.max(Math.abs(r - source.r), Math.abs(c - source.c));
                                    break;
                            }
                            minDistance = Math.min(minDistance, distance);
                        }
                        distanceGrid[r][c] = minDistance;
                    }
                }

                return distanceGrid;
            }

            // --- UI UPDATE FUNCTIONS ---
            function redrawGrid() {
                const distanceGrid = calculateDistances();
                const cells = gridContainer.children;

                for (let i = 0; i < cells.length; i++) {
                    const cell = cells[i];
                    const r = parseInt(cell.dataset.row, 10);
                    const c = parseInt(cell.dataset.col, 10);
                    const span = cell.querySelector('span');

                    const distance = distanceGrid[r][c];
                    span.textContent = isFinite(distance) ? distance : '';

                    // Update cell style
                    if (gridState[r][c]) {
                        cell.classList.add('cell-on');
                        cell.classList.remove('cell-off');
                    } else {
                        cell.classList.add('cell-off');
                        cell.classList.remove('cell-on');
                    }
                }
            }

            function updateActiveButton() {
                Object.values(buttons).forEach(button => button.classList.remove('active'));
                buttons[currentDistanceFunction].classList.add('active');
            }

            // --- START THE APPLICATION ---
            init();
        });
    </script>
</body>
</html>
