<script>
document.addEventListener('DOMContentLoaded', function () {
    const KELVIN_OFFSET = 273.15;

    // --- DOM Elements ---
    const btnModelEp = document.getElementById('btn-model-ep');
    const btnModelAr = document.getElementById('btn-model-ar');
    const btnTmaxLow = document.getElementById('btn-tmax-low');
    const btnTmaxHigh = document.getElementById('btn-tmax-high');

    const sliderN = document.getElementById('slider-n');
    const sliderTmin = document.getElementById('slider-tmin');
    const sliderTmax = document.getElementById('slider-tmax');
    const sliderTref = document.getElementById('slider-tref');
    const sliderParam = document.getElementById('slider-param');

    const labelN = document.getElementById('label-n');
    const labelTmin = document.getElementById('label-tmin');
    const labelTmax = document.getElementById('label-tmax');
    const labelTref = document.getElementById('label-tref');
    const labelParamName = document.getElementById('label-param-name');
    const labelParamValue = document.getElementById('label-param-value');

    const fitResultsDiv = document.getElementById('fit-results');
    const plotContainer = document.getElementById('plot-container');

    // --- Event Listeners ---
    [btnModelEp, btnModelAr, btnTmaxLow, btnTmaxHigh].forEach(btn => {
        btn.addEventListener('click', () => {
            if (btn === btnModelEp || btn === btnModelAr) {
                btnModelEp.classList.toggle('active', btn === btnModelEp);
                btnModelAr.classList.toggle('active', btn === btnModelAr);
            } else if (btn === btnTmaxLow || btn === btnTmaxHigh) {
                btnTmaxLow.classList.toggle('active', btn === btnTmaxLow);
                btnTmaxHigh.classList.toggle('active', btn === btnTmaxHigh);
            }
            update();
        });
    });

    [sliderN, sliderTmin, sliderTmax, sliderTref, sliderParam].forEach(slider => {
        slider.addEventListener('input', update);
    });

    // --- Core Functions ---
    function update() {
        // --- Read State & Apply Constraints ---
        const genModel = btnModelEp.classList.contains('active') ? 'ep' : 'ar';
        const tmaxLimitHigh = btnTmaxHigh.classList.contains('active');

        // Update Tmax slider's max attribute
        sliderTmax.max = tmaxLimitHigh ? 6000 : 1200;
        if (parseInt(sliderTmax.value) > parseInt(sliderTmax.max)) {
            sliderTmax.value = sliderTmax.max;
        }

        // Enforce Tmin < Tmax
        if (parseInt(sliderTmin.value) >= parseInt(sliderTmax.value)) {
            sliderTmin.value = parseInt(sliderTmax.value) - 1;
        }

        // Enforce Tmin <= Tref <= Tmax
        if (parseInt(sliderTref.value) < parseInt(sliderTmin.value)) {
            sliderTref.value = sliderTmin.value;
        }
        if (parseInt(sliderTref.value) > parseInt(sliderTmax.value)) {
            sliderTref.value = sliderTmax.value;
        }

        // Get final values after constraints
        const n = parseInt(sliderN.value);
        const tMin = parseInt(sliderTmin.value);
        const tMax = parseInt(sliderTmax.value);
        const tRef = parseInt(sliderTref.value);
        const aGen = parseInt(sliderParam.value);

        // --- Update UI Labels ---
        labelN.textContent = n;
        labelTmin.textContent = tMin + ".";
        labelTmax.textContent = tMax + ".";
        labelTref.textContent = tRef + ".";
        labelParamValue.textContent = aGen + ".";
        labelParamName.innerHTML = genModel === 'ep' ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';

        // --- Data Generation ---
        const genData = generateData(n, tMin, tMax, tRef, aGen, genModel);

        // --- Data Fitting ---
        const fitModel = (genModel === 'ep') ? 'ar' : 'ep';
        const fitResult = fitData(genData, tRef, fitModel);

        // --- Display Fit Results ---
        const fitParamLabel = fitModel === 'ep' ? 'a_{EP}' : 'a_{Ar}';
        fitResultsDiv.innerHTML = `\\(${fitParamLabel} = ${fitResult.aFit.toFixed(0)}.\\) \\(r^2 = ${fitResult.r2.toFixed(3)}\\)`;

        // --- Re-render MathJax ---
        if (window.MathJax && window.MathJax.typesetPromise) {
            window.MathJax.typesetPromise([labelParamName, fitResultsDiv]);
        }

        // --- Redraw Plot ---
        drawPlot(genData, fitResult, tMin, tMax, tRef, genModel, fitModel);
    }

    function generateData(n, tMin, tMax, tRef, aGen, genModel) {
        const tRefK = tRef + KELVIN_OFFSET;
        const temperatures = [];
        const rates = [];

        const step = n > 1 ? (tMax - tMin) / (n - 1) : 0;

        for (let i = 0; i < n; i++) {
            const tempC = tMin + i * step;
            const tempK = tempC + KELVIN_OFFSET;
            let rate;

            if (genModel === 'ep') {
                rate = (tempK / tRefK) * Math.exp(aGen * (1 / tRefK - 1 / tempK));
            } else { // Arrhenius
                rate = Math.exp(aGen * (1 / tRefK - 1 / tempK));
            }
            temperatures.push(tempC);
            rates.push(rate);
        }
        return { temperatures, rates };
    }

    function fitData(data, tRef, fitModel) {
        const { temperatures, rates } = data;
        const tRefK = tRef + KELVIN_OFFSET;
        let bestAFit = 0;
        let minRss = Infinity;

        // Calculate total sum of squares (for r^2)
        const meanRate = rates.reduce((sum, val) => sum + val, 0) / rates.length;
        const ssTot = rates.reduce((sum, val) => sum + Math.pow(val - meanRate, 2), 0);

        // Grid search for the best 'a' parameter
        for (let aFit = 0; aFit <= 10000; aFit += 1) {
            let currentRss = 0;
            for (let i = 0; i < temperatures.length; i++) {
                const tempK = temperatures[i] + KELVIN_OFFSET;
                let predictedRate;

                if (fitModel === 'ep') {
                    predictedRate = (tempK / tRefK) * Math.exp(aFit * (1 / tRefK - 1 / tempK));
                } else { // Arrhenius
                    predictedRate = Math.exp(aFit * (1 / tRefK - 1 / tempK));
                }
                currentRss += Math.pow(rates[i] - predictedRate, 2);
            }

            if (currentRss < minRss) {
                minRss = currentRss;
                bestAFit = aFit;
            }
        }

        const r2 = 1 - (minRss / ssTot);
        return { aFit: bestAFit, r2: r2 };
    }

    function drawPlot(genData, fitResult, tMin, tMax, tRef, genModel, fitModel) {
        // Trace 1: Generated data points
        const dataTrace = {
            x: genData.temperatures,
            y: genData.rates,
            mode: 'markers',
            type: 'scatter',
            name: 'Generated Data',
            marker: {
                color: 'lightgreen',
                size: 8,
                line: {
                    color: 'blue',
                    width: 2
                }
            }
        };

        // Trace 2: Fitted model curve
        const fitCurveX = [];
        const fitCurveY = [];
        const tRefK = tRef + KELVIN_OFFSET;
        const aFit = fitResult.aFit;
        const numFitPoints = 200;

        for (let i = 0; i <= numFitPoints; i++) {
            const tempC = tMin + (i / numFitPoints) * (tMax - tMin);
            const tempK = tempC + KELVIN_OFFSET;
            let rate;

            if (fitModel === 'ep') {
                rate = (tempK / tRefK) * Math.exp(aFit * (1 / tRefK - 1 / tempK));
            } else { // Arrhenius
                rate = Math.exp(aFit * (1 / tRefK - 1 / tempK));
            }
            fitCurveX.push(tempC);
            fitCurveY.push(rate);
        }

        const fitTrace = {
            x: fitCurveX,
            y: fitCurveY,
            mode: 'lines',
            type: 'scatter',
            name: 'Fitted Model',
            line: {
                color: 'green',
                width: 2
            }
        };

        // Plot Layout
        const genModelName = genModel === 'ep' ? 'Eyring–Polanyi' : 'Arrhenius';
        const fitModelName = fitModel === 'ep' ? 'Eyring–Polanyi' : 'Arrhenius';

        const layout = {
            title: {
                text: `${fitModelName} model fit to ${genModelName} data`,
                y: 0.95
            },
            xaxis: {
                title: '<i>T</i> °C'
            },
            yaxis: {
                title: '<i>k</i>(<i>T</i>) / <i>k</i>(<i>T</i><sub>ref</sub>)',
                rangemode: 'tozero'
            },
            margin: { t: 50, b: 40, l: 60, r: 20 },
            showlegend: false,
            hovermode: 'closest'
        };

        Plotly.newPlot(plotContainer, [dataTrace, fitTrace], layout, {responsive: true});
    }

    // --- Initial Call ---
    update();
});
</script>

</body>
</html>
