            display: flex;
            align-items: center;
            gap: 5px;
            padding: 8px;
            border: 1px solid #ccc;
            background-color: #f1f1f1;
            border-radius: 5px 5px 0 0;
            width: 900px;
            box-sizing: border-box;
        }

        #n-display {
            padding: 5px 10px;
            font-weight: bold;
            font-size: 14px;
            min-width: 20px;
            text-align: center;
        }

        #controls-container button {
            padding: 5px 10px;
            border: 1px solid #ccc;
            background-color: #f0f0f0;
            cursor: pointer;
            border-radius: 3px;
            font-size: 14px;
            transition: background-color 0.2s;
        }

        #controls-container button:hover {
            background-color: #e0e0e0;
        }

        #controls-container button.active {
            background-color: #d0d0d0;
            font-weight: bold;
            border-color: #999;
        }

        #canvas-container {
            border: 1px solid #ccc;
            border-top: none;
            width: fit-content;
            /* p5.js will create the canvas inside this div */
        }
    </style>
</head>
<body>

    <div id="controls-container">
        <span id="n-display"></span>
        <button id="btn-n-3">3</button>
        <button id="btn-n-4">4</button>
        <button id="btn-n-5">5</button>
        <button id="btn-n-6">6</button>
        <button id="btn-n-7">7</button>
        <button id="btn-n-8">8</button>
        <button id="btn-n-9">9</button>
        <button id="btn-n-10">10</button>
    </div>
    <div id="canvas-container"></div>

    <script>
        let n; // The current number of sides for the polygon calculation

        const CANVAS_WIDTH = 900;
        const CANVAS_HEIGHT = 450;

        const colors = {
            yellow: '#fefd00',
            red: '#ee1f24',
            blue: '#0048a1',
            black: '#000000',
            white: '#FFFFFF'
        };

        const polyColors = [colors.yellow, colors.red, colors.blue];

        // p5.js setup function
        function setup() {
            let canvas = createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
            canvas.parent('canvas-container');
            noLoop(); // We only redraw when 'n' changes
        }

        // p5.js draw function
        function draw() {
            if (typeof n === 'undefined') return; // Don't draw until n is initialized

            background(colors.white);
            stroke(colors.black);
            strokeWeight(1.5);

            drawScene(n);
        }

        function drawScene(currentN) {
            // --- Left Side: 2n-gon ---
            const R = 150; // Radius of the circumcircle
            const cx = CANVAS_WIDTH / 4;
            const cy = CANVAS_HEIGHT / 2;
            const numVertices = 2 * currentN;
            const angleStep = PI / currentN;

            for (let j = 0; j < numVertices; j++) {
                const angle1 = j * angleStep - HALF_PI;
                const angle2 = (j + 1) * angleStep - HALF_PI;

                const x1 = cx + R * cos(angle1);
                const y1 = cy + R * sin(angle1);
                const x2 = cx + R * cos(angle2);
                const y2 = cy + R * sin(angle2);

                fill(polyColors[j % 3]);
                triangle(cx, cy, x1, y1, x2, y2);
            }

            // --- Right Side: Rectangles ---
            // Calculate base dimensions from the polygon's triangles
            const s = 2 * R * sin(angleStep / 2); // side length of the 2n-gon
            const a = R * cos(angleStep / 2); // apothem
            const spacing = 20;

            const numFullRects = floor(currentN / 2);
            let totalWidth = numFullRects * s + (numFullRects - 1) * spacing;
            const isOdd = currentN % 2 !== 0;
            if (isOdd) {
                totalWidth += (s + spacing);
            }

            // Center the block of rectangles in the right half of the canvas
            const rightHalfWidth = CANVAS_WIDTH / 2;
            let startX = (CANVAS_WIDTH / 2) + (rightHalfWidth - totalWidth) / 2;
            const startY = CANVAS_HEIGHT / 2;

            // Draw full rectangles
            for (let k = 0; k < numFullRects; k++) {
                const rectX = startX + k * (s + spacing);
                const rectCenterY = startY;

                // A full rectangle is composed of 4 right-angled triangles
                const pTop = { x: rectX + s / 2, y: rectCenterY };
                const pTopLeft = { x: rectX, y: rectCenterY - a };
                const pTopRight = { x: rectX + s, y: rectCenterY - a };
                const pBottomLeft = { x: rectX, y: rectCenterY + a };
                const pBottomRight = { x: rectX + s, y: rectCenterY + a };

                // Top triangle: Yellow
                fill(colors.yellow);
                triangle(pTopLeft.x, pTopLeft.y, pTopRight.x, pTopRight.y, pTop.x, pTop.y);

                // Bottom triangle: Red
                fill(colors.red);
                triangle(pBottomLeft.x, pBottomLeft.y, pBottomRight.x, pBottomRight.y, pTop.x, pTop.y);

                // Left triangle: Blue
                fill(colors.blue);
                triangle(pTopLeft.x, pTopLeft.y, pBottomLeft.x, pBottomLeft.y, pTop.x, pTop.y);

                // Right triangle: Yellow
                fill(colors.yellow);
                triangle(pTopRight.x, pTopRight.y, pBottomRight.x, pBottomRight.y, pTop.x, pTop.y);
            }

            // Draw half-rectangle if n is odd
            if (isOdd) {
                const halfRectX = startX + numFullRects * (s + spacing);
                const halfRectY = startY - a / 2;

                const TL = { x: halfRectX, y: halfRectY };
                const TR = { x: halfRectX + s, y: halfRectY };
                const BL = { x: halfRectX, y: halfRectY + a };
                const BR = { x: halfRectX + s, y: halfRectY + a };

                // Top-left triangle: Blue
                fill(colors.blue);
                triangle(TL.x, TL.y, TR.x, TR.y, BL.x, BL.y);

                // Bottom-right triangle: Red
                fill(colors.red);
                triangle(TR.x, TR.y, BL.x, BL.y, BR.x, BR.y);
            }
        }

        // --- Interaction Logic ---
        function updateN(newN) {
            n = newN;

            // Update display
            document.getElementById('n-display').textContent = n;

            // Update button styles
            document.querySelectorAll('#controls-container button').forEach(button => {
                button.classList.remove('active');
            });
            document.getElementById(`btn-n-${n}`).classList.add('active');

            // Redraw the p5.js canvas
            if (typeof redraw === 'function') {
                 redraw();
            }
        }

        document.addEventListener('DOMContentLoaded', () => {
            // Attach event listeners to all buttons
            for (let i = 3; i <= 10; i++) {
                const button = document.getElementById(`btn-n-${i}`);
                if (button) {
                    button.addEventListener('click', () => updateN(i));
                }
            }

            // Set initial state
            updateN(6);
        });

    </script>

</body>
</html>
