        }
        #workspace-panel, #phase-space-panel {
            background-color: #fff;
            border: 1px solid #ddd;
            padding: 15px;
            border-radius: 8px;
        }
        h3 {
            margin-top: 0;
            text-align: center;
            font-weight: normal;
            color: #555;
        }
        #canvas-workspace, #canvas-phase-space {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>

    <div id="controls-container">
        <div class="slider-group">
            <label for="slider-q">Q</label>
            <input type="range" id="slider-q" min="1" max="7" value="2" step="1">
            <span id="value-q">2</span>
        </div>
        <div class="slider-group">
            <label for="slider-p">P</label>
            <input type="range" id="slider-p" min="0" max="1" value="1" step="0.01">
            <span id="value-p">1.00</span>
        </div>
        <button id="btn-reset">×</button>
    </div>

    <div id="main-container">
        <div id="workspace-panel">
            <h3>robot workspace</h3>
            <div id="canvas-workspace"></div>
        </div>
        <div id="phase-space-panel">
            <h3 id="phase-space-title">robot phase space, path length = 0.00 rads</h3>
            <div id="canvas-phase-space"></div>
        </div>
    </div>

<script>
    // --- GLOBAL STATE ---
    // DOM Elements
    const sliderQ = document.getElementById('slider-q');
    const valueQ = document.getElementById('value-q');
    const sliderP = document.getElementById('slider-p');
    const valueP = document.getElementById('value-p');
    const btnReset = document.getElementById('btn-reset');
    const phaseSpaceTitle = document.getElementById('phase-space-title');

    // Canvas Dimensions
    const canvasSize = 400;

    // Robot Arm Parameters
    const link1Length = 75;
    const link2Length = 50;
    const linkRadius = 10;

    // Initial State Definitions
    const initialObs1 = { x: 200, y: 100, radius: 15 };
    const initialObs2 = { x: 200, y: 300, radius: 15 };
    const initialStartConfig = { q1: -1.5, q2: 0.5 };
    const initialGoalConfig = { q1: 1.5, q2: 1.0 };
    const initialQ = 2;
    const initialP = 1;

    // Simulation State
    let obs1 = { ...initialObs1 };
    let obs2 = { ...initialObs2 };
    let startConfig = { ...initialStartConfig };
    let goalConfig = { ...initialGoalConfig };

    // Interaction Flags
    let isDraggingObstacle1 = false;
    let isDraggingObstacle2 = false;
    let isDraggingStart = false;
    let isDraggingGoal = false;

    // Pathfinding Data
    let N; // Grid resolution
    let cSpace; // Collision grid
    let distanceField; // BFS distance from goal
    let path = []; // Array of {q1, q2} points
    let needsRecalculation = true;

    // --- UTILITY FUNCTIONS ---
    // Map angle (-PI, PI) to grid index (0, N-1)
    const angleToIndex = (q) => Math.floor(((q + Math.PI) / (2 * Math.PI)) * N);
    // Map grid index (0, N-1) to angle (-PI, PI)
    const indexToAngle = (i) => ((i + 0.5) / N) * (2 * Math.PI) - Math.PI;

    // Distance squared from a point p to a line segment vw
    function distSqToSegment(p, v, w) {
        const l2 = (v.x - w.x)**2 + (v.y - w.y)**2;
        if (l2 === 0) return (p.x - v.x)**2 + (p.y - v.y)**2;
        let t = ((p.x - v.x) * (w.x - v.x) + (p.y - v.y) * (w.y - v.y)) / l2;
        t = Math.max(0, Math.min(1, t));
        const proj = { x: v.x + t * (w.x - v.x), y: v.y + t * (w.y - v.y) };
        return (p.x - proj.x)**2 + (p.y - proj.y)**2;
    }

    // Check collision for a given configuration (q1, q2)
    function checkCollision(q1, q2) {
        const base = { x: canvasSize / 2, y: canvasSize / 2 };
        const p1 = { x: base.x + link1Length * Math.cos(q1), y: base.y + link1Length * Math.sin(q1) };
        const p2 = { x: p1.x + link2Length * Math.cos(q1 + q2), y: p1.y + link2Length * Math.sin(q1 + q2) };

        // Check link 1
        const distSq1_obs1 = distSqToSegment(obs1, base, p1);
        if (distSq1_obs1 < (linkRadius + obs1.radius)**2) return true;
        const distSq1_obs2 = distSqToSegment(obs1, base, p1);
        if (distSq1_obs2 < (linkRadius + obs2.radius)**2) return true;


        // Check link 2
        const distSq2_obs1 = distSqToSegment(obs2, p1, p2);
        if (distSq2_obs1 < (linkRadius + obs1.radius)**2) return true;
        const distSq2_obs2 = distSqToSegment(obs2, p1, p2);
        if (distSq2_obs2 < (linkRadius + obs2.radius)**2) return true;

        return false;
    }

    // --- CORE LOGIC FUNCTIONS ---
    function calculateCSpace() {
        cSpace = Array(N).fill(0).map(() => Array(N).fill(false));
        for (let i = 0; i < N; i++) {
            for (let j = 0; j < N; j++) {
                const q1 = indexToAngle(i);
                const q2 = indexToAngle(j);
                cSpace[i][j] = checkCollision(q1, q2);
            }
        }
    }

    function runBFS() {
        distanceField = Array(N).fill(0).map(() => Array(N).fill(Infinity));
        const queue = [];

        const goalI = angleToIndex(goalConfig.q1);
        const goalJ = angleToIndex(goalConfig.q2);

        if (cSpace[goalI][goalJ]) { // Goal is in collision
            return;
        }

        distanceField[goalI][goalJ] = 0;
        queue.push([goalI, goalJ, 0]);

        let head = 0;
        while (head < queue.length) {
            const [ci, cj, dist] = queue[head++];

            for (let di = -1; di <= 1; di++) {
                for (let dj = -1; dj <= 1; dj++) {
                    if (di === 0 && dj === 0) continue;

                    const ni = (ci + di + N) % N; // Wrap around for toroidal space
                    const nj = (cj + dj + N) % N;

                    if (distanceField[ni][nj] === Infinity && !cSpace[ni][nj]) {
                        distanceField[ni][nj] = dist + 1;
                        queue.push([ni, nj, dist + 1]);
                    }
                }
            }
        }
    }

    function findPath() {
        path = [];
        const startI = angleToIndex(startConfig.q1);
        const startJ = angleToIndex(startConfig.q2);

        if (distanceField[startI][startJ] === Infinity || cSpace[startI][startJ]) {
            phaseSpaceTitle.textContent = "robot phase space, path not found";
            return;
        }

        let ci = startI;
        let cj = startJ;
        path.push({ q1: indexToAngle(ci), q2: indexToAngle(cj) });

        while (distanceField[ci][cj] > 0) {
            let bestNi = -1, bestNj = -1, minDist = Infinity;

            for (let di = -1; di <= 1; di++) {
                for (let dj = -1; dj <= 1; dj++) {
                    if (di === 0 && dj === 0) continue;

                    const ni = (ci + di + N) % N;
                    const nj = (cj + dj + N) % N;

                    if (distanceField[ni][nj] < minDist) {
                        minDist = distanceField[ni][nj];
                        bestNi = ni;
                        bestNj = nj;
                    }
                }
            }
            ci = bestNi;
            cj = bestNj;
            path.push({ q1: indexToAngle(ci), q2: indexToAngle(cj) });
        }

        // Calculate path length
        let pathLength = 0;
        for (let i = 0; i < path.length - 1; i++) {
            const p1 = path[i];
            const p2 = path[i+1];
            pathLength += Math.sqrt((p1.q1 - p2.q1)**2 + (p1.q2 - p2.q2)**2);
        }
        phaseSpaceTitle.textContent = `robot phase space, path length = ${pathLength.toFixed(2)} rads`;
    }

    function fullRecalculation() {
        N = 10 * (2 ** parseInt(sliderQ.value));
        calculateCSpace();
        runBFS();
        findPath();
        needsRecalculation = false;
    }

    function updateUI() {
        valueQ.textContent = sliderQ.value;
        valueP.textContent = parseFloat(sliderP.value).toFixed(2);
    }

    function resetSimulation() {
        sliderQ.value = initialQ;
        sliderP.value = initialP;

        obs1 = { ...initialObs1 };
        obs2 = { ...initialObs2 };
        startConfig = { ...initialStartConfig };
        goalConfig = { ...initialGoalConfig };

        updateUI();
        fullRecalculation();
    }


    // --- P5 SKETCH DEFINITIONS ---

    const workspaceSketch = (p) => {
        p.setup = () => {
            p.createCanvas(canvasSize, canvasSize).parent('canvas-workspace');
            p.rectMode(p.CENTER);
            p.textAlign(p.CENTER, p.CENTER);
        };

        p.draw = () => {
            p.background(240);

            // Get current configuration from path
            let currentConfig = startConfig;
            if (path.length > 1) {
                const pVal = parseFloat(sliderP.value);
                const totalSegments = path.length - 1;
                const pathIndexFloat = pVal * totalSegments;
                const segmentIndex = p.constrain(p.floor(pathIndexFloat), 0, totalSegments - 1);
                const t = pathIndexFloat - segmentIndex;

                const p1 = path[segmentIndex];
                const p2 = path[segmentIndex + 1];

                // Check for p2 undefined (can happen at pVal=1)
                if (p2) {
                    currentConfig = {
                        q1: p.lerp(p1.q1, p2.q1, t),
                        q2: p.lerp(p1.q2, p2.q2, t)
                    };
                } else {
                    currentConfig = path[path.length - 1]; // Use last point
                }
            } else if(path.length === 1) {
                currentConfig = path[0];
            } else { // No path found
                const pVal = parseFloat(sliderP.value);
                if (pVal === 0) currentConfig = startConfig;
                else if (pVal === 1) currentConfig = goalConfig;
                else currentConfig = {
                    q1: p.lerp(startConfig.q1, goalConfig.q1, pVal),
                    q2: p.lerp(startConfig.q2, goalConfig.q2, pVal)
                };
            }

            const q1 = currentConfig.q1;
            const q2 = currentConfig.q2;

            p.push();
            p.translate(canvasSize / 2, canvasSize / 2);

            // Draw links
            p.rotate(q1);
            p.stroke(0);
            p.fill(150);
            p.rect(link1Length / 2, 0, link1Length, linkRadius * 2, linkRadius);

            p.translate(link1Length, 0);
            p.rotate(q2);
            p.fill(180);
            p.rect(link2Length / 2, 0, link2Length, linkRadius * 2, linkRadius);

            // Draw text on link
            p.fill(0);
            p.noStroke();
            p.push();
            p.translate(link2Length/2, 0);
            p.rotate(-q1-q2);
            p.textSize(10);
            p.text("Robot Arm", 0, 0);
            p.pop();


            p.pop(); // Back to origin

            // Draw angle indicators
            p.push();
            p.translate(canvasSize / 2, canvasSize / 2);
            p.noFill();
            p.stroke(0);
            // theta1
            p.arc(0, 0, 50, 50, 0, q1);
            p.fill(0);
            p.text('θ₁', 35 * p.cos(q1/2), 35 * p.sin(q1/2));

            // theta2
            p.translate(link1Length * p.cos(q1), link1Length * p.sin(q1));
            p.noFill();
            p.stroke(0);
            p.arc(0, 0, 40, 40, q1, q1 + q2);
            p.fill(0);
            const midAngle2 = q1 + q2/2;
            p.text('θ₂', 28 * p.cos(midAngle2), 28 * p.sin(midAngle2));
            p.pop();

            // Draw obstacles
            const drawObstacle = (obs) => {
                p.push();
                p.translate(obs.x, obs.y);
                p.fill(165, 114, 42); // Brown
                p.stroke(80, 50, 20);
                p.ellipse(0, 0, obs.radius * 2, obs.radius * 2);
                p.noFill();
                p.stroke(80, 50, 20, 150);
                for (let i = 0; i < 5; i++) {
                    p.line(-obs.radius, (i - 2) * 5, obs.radius, (i - 2) * 5);
                }
                p.pop();
            };
            drawObstacle(obs1);
            drawObstacle(obs2);
        };

        p.mousePressed = () => {
            if (p.dist(p.mouseX, p.mouseY, obs1.x, obs1.y) < obs1.radius) {
                isDraggingObstacle1 = true;
            } else if (p.dist(p.mouseX, p.mouseY, obs2.x, obs2.y) < obs2.radius) {
                isDraggingObstacle2 = true;
            }
        };

        p.mouseDragged = () => {
            if (isDraggingObstacle1) {
                obs1.x = p.constrain(p.mouseX, 0, canvasSize);
                obs1.y = p.constrain(p.mouseY, 0, canvasSize);
                needsRecalculation = true;
            } else if (isDraggingObstacle2) {
                obs2.x = p.constrain(p.mouseX, 0, canvasSize);
                obs2.y = p.constrain(p.mouseY, 0, canvasSize);
                needsRecalculation = true;
            }
            if (needsRecalculation) {
                fullRecalculation();
            }
        };

        p.mouseReleased = () => {
            isDraggingObstacle1 = false;
            isDraggingObstacle2 = false;
        };
    };

    const phaseSpaceSketch = (p) => {
        let cellSize;

        p.setup = () => {
            p.createCanvas(canvasSize, canvasSize).parent('canvas-phase-space');
        };

        p.draw = () => {
            p.background(255);
            if (!cSpace) return;

            cellSize = canvasSize / N;

            // Draw grid
            let maxDist = 0;
            for(let i=0; i<N; i++) {
                for(let j=0; j<N; j++) {
                    if (distanceField[i][j] !== Infinity && distanceField[i][j] > maxDist) {
                        maxDist = distanceField[i][j];
                    }
                }
            }

            p.noStroke();
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    if (cSpace[i][j]) {
                        p.fill(204, 0, 0); // Red
                    } else {
                        if (distanceField[i][j] === Infinity) {
                            p.fill(50); // Dark gray for unreachable
                        } else {
                            const gray = p.map(distanceField[i][j], 0, maxDist, 240, 80);
                            p.fill(gray);
                        }
                    }
                    p.rect(i * cellSize, j * cellSize, cellSize, cellSize);
                }
            }

            // Draw Path
            if (path.length > 1) {
                p.stroke(0, 255, 0);
                p.strokeWeight(3);
                p.noFill();
                p.beginShape();
                for (const point of path) {
                    const i = angleToIndex(point.q1);
                    const j = angleToIndex(point.q2);
                    p.vertex((i + 0.5) * cellSize, (j + 0.5) * cellSize);
                }
                p.endShape();
            }

            // Draw Start/Goal markers
            const drawMarker = (config, isStart) => {
                const i = angleToIndex(config.q1);
                const j = angleToIndex(config.q2);
                const x = (i + 0.5) * cellSize;
                const y = (j + 0.5) * cellSize;

                let markerColor = p.color(0, 255, 0);
                if (cSpace[i][j]) {
                    markerColor = p.color(255,0,0);
                }

                p.strokeWeight(2);
                p.stroke(markerColor);
                p.noFill();
                p.ellipse(x, y, 14, 14);
                p.stroke(255);
                p.line(x - 4, y, x + 4, y);
                p.line(x, y - 4, x, y + 4);
            };

            drawMarker(startConfig, true);
            drawMarker(goalConfig, false);

            // Draw current position marker on path
            if (path.length > 1) {
                const pVal = parseFloat(sliderP.value);
                const totalSegments = path.length - 1;
                const pathIndexFloat = pVal * totalSegments;
                const segmentIndex = p.constrain(p.floor(pathIndexFloat), 0, totalSegments - 1);
                const t = pathIndexFloat - segmentIndex;

                const p1_idx = {i: angleToIndex(path[segmentIndex].q1), j: angleToIndex(path[segmentIndex].q2)};
                const p2_idx = {i: angleToIndex(path[segmentIndex+1].q1), j: angleToIndex(path[segmentIndex+1].q2)};

                const x = p.lerp((p1_idx.i + 0.5) * cellSize, (p2_idx.i + 0.5) * cellSize, t);
                const y = p.lerp((p1_idx.j + 0.5) * cellSize, (p2_idx.j + 0.5) * cellSize, t);

                p.fill(0, 150, 255);
                p.stroke(255);
                p.strokeWeight(1);
                p.ellipse(x, y, 8, 8);
            }

            // Draw axis labels
            p.fill(0);
            p.noStroke();
            p.textSize(14);
            p.textAlign(p.CENTER, p.TOP);
            p.text('θ₁', canvasSize / 2, canvasSize - 15);
            p.textAlign(p.LEFT, p.CENTER);
            p.text('θ₂', 5, canvasSize / 2);

        };

        const getMouseGridPos = () => {
            cellSize = canvasSize / N;
            const i = p.floor(p.mouseX / cellSize);
            const j = p.floor(p.mouseY / cellSize);
            return { i: p.constrain(i, 0, N - 1), j: p.constrain(j, 0, N - 1) };
        };

        p.mousePressed = () => {
            if (!cSpace) return;
            cellSize = canvasSize / N;
            const startI = angleToIndex(startConfig.q1);
            const startJ = angleToIndex(startConfig.q2);
            const goalI = angleToIndex(goalConfig.q1);
            const goalJ = angleToIndex(goalConfig.q2);

            const startX = (startI + 0.5) * cellSize;
            const startY = (startJ + 0.5) * cellSize;
            const goalX = (goalI + 0.5) * cellSize;
            const goalY = (goalJ + 0.5) * cellSize;

            if (p.dist(p.mouseX, p.mouseY, startX, startY) < 10) {
                isDraggingStart = true;
            } else if (p.dist(p.mouseX, p.mouseY, goalX, goalY) < 10) {
                isDraggingGoal = true;
            }
        };

        p.mouseDragged = () => {
            if (!isDraggingStart && !isDraggingGoal) return;

            const {i, j} = getMouseGridPos();

            if (cSpace[i][j]) return; // Don't move into obstacles

            if (isDraggingStart) {
                startConfig = { q1: indexToAngle(i), q2: indexToAngle(j) };
                findPath(); // Only need to re-find path
            } else if (isDraggingGoal) {
                goalConfig = { q1: indexToAngle(i), q2: indexToAngle(j) };
                runBFS(); // Goal changed, need to re-run BFS
                findPath();
            }
        };

        p.mouseReleased = () => {
            isDraggingStart = false;
            isDraggingGoal = false;
        };
    };

    // --- INITIALIZATION and EVENT LISTENERS ---
    document.addEventListener('DOMContentLoaded', () => {
        // Event Listeners
        sliderQ.addEventListener('input', () => {
            updateUI();
            fullRecalculation();
        });

        sliderP.addEventListener('input', updateUI);

        btnReset.addEventListener('click', resetSimulation);

        // Initial setup
        updateUI();
        fullRecalculation();

        // Instantiate p5 sketches
        new p5(workspaceSketch, 'canvas-workspace');
        new p5(phaseSpaceSketch, 'canvas-phase-space');
    });

</script>
</body>
</html>
