    <script>
        const sketch = (p) => {
            // State variables
            let dimension = '2D';
            let randomSeedVal = 525;
            let numPoints = 40;
            let algorithm = 'prim';
            let animationState = 'stopped'; // 'stopped', 'playing', 'paused'

            // Data structures
            let points = [];
            let animationSteps = [];
            let currentStep = 0;

            // UI Elements
            let btn2d, btn3d, sliderSeed, seedValue, sliderNumber, numberValue;
            let btnPrim, btnKruskal, btnPlay, btnPause, btnReset;
            let canvasContainer, p5Canvas;

            // Animation control
            const ANIMATION_FRAME_INTERVAL = 5; // Update every 5 frames

            p.setup = function() {
                canvasContainer = document.getElementById('canvas-container');
                const canvasSize = canvasContainer.offsetWidth;
                p5Canvas = p.createCanvas(canvasSize, canvasSize);
                p5Canvas.parent('canvas-container');

                p.frameRate(60);

                // Initialize UI elements
                btn2d = document.getElementById('btn-2d');
                btn3d = document.getElementById('btn-3d');
                sliderSeed = document.getElementById('slider-seed');
                seedValue = document.getElementById('seed-value');
                sliderNumber = document.getElementById('slider-number');
                numberValue = document.getElementById('number-value');
                btnPrim = document.getElementById('btn-prim');
                btnKruskal = document.getElementById('btn-kruskal');
                btnPlay = document.getElementById('btn-play');
                btnPause = document.getElementById('btn-pause');
                btnReset = document.getElementById('btn-reset');

                // Add event listeners
                btn2d.onclick = () => handleDimensionChange('2D');
                btn3d.onclick = () => handleDimensionChange('3D');
                sliderSeed.oninput = handleSeedChange;
                sliderNumber.oninput = handleNumberChange;
                btnPrim.onclick = () => handleAlgorithmChange('prim');
                btnKruskal.onclick = () => handleAlgorithmChange('kruskal');
                btnPlay.onclick = handlePlay;
                btnPause.onclick = handlePause;
                btnReset.onclick = handleReset;

                // Initial setup
                resetSketch(true);
            };

            p.draw = function() {
                p.background(224); // #E0E0E0

                if (dimension === '3D') {
                    p.orbitControl();
                    p.push();
                    p.stroke(150);
                    p.noFill();
                    p.box(p.width);
                    p.pop();
                }

                drawPoints();
                drawMST();

                if (animationState === 'playing' && p.frameCount % ANIMATION_FRAME_INTERVAL === 0) {
                    if (currentStep < animationSteps.length) {
                        currentStep++;
                    } else {
                        animationState = 'stopped';
                        updateControlStates();
                    }
                }
            };

            // --- Control Handlers ---

            function handleDimensionChange(newDim) {
                if (dimension === newDim) return;
                dimension = newDim;
                resetSketch(true);
            }

            function handleAlgorithmChange(newAlgo) {
                if (algorithm === newAlgo) return;
                algorithm = newAlgo;
                resetSketch(false);
            }

            function handleSeedChange() {
                randomSeedVal = parseInt(sliderSeed.value);
                seedValue.textContent = randomSeedVal;
                resetSketch(true);
            }

            function handleNumberChange() {
                numPoints = parseInt(sliderNumber.value);
                numberValue.textContent = numPoints;
                resetSketch(true);
            }

            function handlePlay() {
                if (currentStep >= animationSteps.length) {
                   resetSketch(false);
                }
                animationState = 'playing';
                updateControlStates();
            }

            function handlePause() {
                animationState = 'paused';
                updateControlStates();
            }

            function handleReset() {
                resetSketch(false);
            }

            // --- Core Logic ---

            function resetSketch(isFullReset) {
                animationState = 'stopped';
                currentStep = 0;
                animationSteps = [];

                if (isFullReset) {
                    // Re-create canvas if dimension changed
                    if ((dimension === '3D' && !p5Canvas.isP3D) || (dimension === '2D' && p5Canvas.isP3D)) {
                       p5Canvas.remove();
                       const canvasSize = canvasContainer.offsetWidth;
                       p5Canvas = p.createCanvas(canvasSize, canvasSize, dimension === '3D' ? p.WEBGL : p.P2D);
                       p5Canvas.parent('canvas-container');
                    }
                    generatePoints();
                }

                precalculateMST();
                updateControlStates();
            }

            function generatePoints() {
                points = [];
                p.randomSeed(randomSeedVal);
                const margin = 50;
                for (let i = 0; i < numPoints; i++) {
                    if (dimension === '2D') {
                        points.push(p.createVector(
                            p.random(margin, p.width - margin),
                            p.random(margin, p.height - margin)
                        ));
                    } else { // 3D
                        points.push(p.createVector(
                            p.random(-p.width/2 + margin, p.width/2 - margin),
                            p.random(-p.height/2 + margin, p.height/2 - margin),
                            p.random(-p.width/2 + margin, p.width/2 - margin)
                        ));
                    }
                }
            }

            function precalculateMST() {
                if (points.length < 2) return;
                if (algorithm === 'prim') {
                    calculatePrimSteps();
                } else {
                    calculateKruskalSteps();
                }
            }

            function updateControlStates() {
                // Dimension buttons
                btn2d.classList.toggle('active', dimension === '2D');
                btn3d.classList.toggle('active', dimension === '3D');

                // Algorithm buttons
                btnPrim.classList.toggle('active', algorithm === 'prim');
                btnKruskal.classList.toggle('active', algorithm === 'kruskal');

                // Process buttons
                const isFinished = currentStep >= animationSteps.length;

                btnPlay.disabled = (animationState === 'playing') || (isFinished && animationState !== 'paused');
                btnPause.disabled = (animationState !== 'playing');
                btnReset.disabled = false;
            }

            // --- Drawing Functions ---

            function drawPoints() {
                p.push();
                if (dimension === '2D') {
                    p.noStroke();
                    p.fill('blue');
                    points.forEach(pt => p.circle(pt.x, pt.y, 8));
                } else { // 3D
                    p.noStroke();
                    p.ambientMaterial(0, 0, 255); // Blue
                    points.forEach(pt => {
                        p.push();
                        p.translate(pt.x, pt.y, pt.z);
                        p.sphere(8);
                        p.pop();
                    });
                }
                p.pop();
            }

            function drawMST() {
                if (animationSteps.length === 0) return;

                p.push();
                p.stroke('red');
                p.strokeWeight(2);
                if (dimension === '3D') p.ambientMaterial(255, 0, 0); // Red for lines/spheres

                for (let i = 0; i < currentStep; i++) {
                    const edge = animationSteps[i];
                    const p1 = points[edge.u];
                    const p2 = points[edge.v];
                    p.line(p1.x, p1.y, p1.z || 0, p2.x, p2.y, p2.z || 0);
                }
                p.pop();
            }

            // --- Algorithm Implementations ---

            function calculatePrimSteps() {
                const n = points.length;
                let parent = new Array(n).fill(-1);
                let key = new Array(n).fill(Infinity);
                let mstSet = new Array(n).fill(false);

                key[0] = 0;
                animationSteps = [];

                for (let count = 0; count < n; count++) {
                    let min = Infinity, u = -1;
                    for (let v = 0; v < n; v++) {
                        if (!mstSet[v] && key[v] < min) {
                            min = key[v];
                            u = v;
                        }
                    }

                    if (u === -1) continue;
                    mstSet[u] = true;
                    if(parent[u] !== -1) {
                        animationSteps.push({ u: u, v: parent[u] });
                    }

                    for (let v = 0; v < n; v++) {
                        const dist = p5.Vector.dist(points[u], points[v]);
                        if (!mstSet[v] && dist < key[v]) {
                            parent[v] = u;
                            key[v] = dist;
                        }
                    }
                }
            }

            function calculateKruskalSteps() {
                let edges = [];
                for (let i = 0; i < points.length; i++) {
                    for (let j = i + 1; j < points.length; j++) {
                        const dist = p5.Vector.dist(points[i], points[j]);
                        edges.push({ u: i, v: j, weight: dist });
                    }
                }

                edges.sort((a, b) => a.weight - b.weight);

                let parent = Array.from(Array(points.length).keys());
                const find = (i) => {
                    if (parent[i] === i) return i;
                    return parent[i] = find(parent[i]);
                };
                const union = (i, j) => {
                    let rootI = find(i);
                    let rootJ = find(j);
                    if (rootI !== rootJ) {
                        parent[rootI] = rootJ;
                        return true;
                    }
                    return false;
                };

                animationSteps = [];
                for (const edge of edges) {
                    if (union(edge.u, edge.v)) {
                        animationSteps.push(edge);
                    }
                }
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
