    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // Get DOM elements
            const selectMethod = document.getElementById('select-method');
            const sliderNum1 = document.getElementById('slider-num1');
            const sliderNum2 = document.getElementById('slider-num2');
            const btnIntYes = document.getElementById('btn-int-yes');
            const btnIntNo = document.getElementById('btn-int-no');
            const outputDisplay = document.getElementById('output-display');

            // --- Helper Functions ---

            /**
             * Analyzes a number and returns its integer representation and decimal factor.
             * E.g., 16.1 -> { integer: 161, factor: 10 }
             * E.g., 15 -> { integer: 15, factor: 1 }
             */
            function getIntegerAndFactor(num) {
                if (Number.isInteger(num)) {
                    return { integer: num, factor: 1 };
                }
                const numStr = String(num);
                const parts = numStr.split('.');
                const factor = Math.pow(10, parts[1].length);
                const integer = Number(numStr.replace('.', ''));
                return { integer, factor };
            }

            /**
             * Formats a number for display in the "convert to" step.
             * Adds a trailing dot if it was a decimal.
             */
            function formatIntForDisplay(data) {
                return data.factor === 1 ? String(data.integer) : `${data.integer}.`;
            }

            /**
             * Formats a number for the "rewrite as" step in the place values method.
             */
            function formatRewritePart(data) {
                if (data.factor === 1) {
                    return String(data.integer);
                }
                return `(${data.integer}. \u00F7 ${data.factor})`;
            }

            // --- Calculation Logic & HTML Generation ---

            function generateEstimationHTML(num1, num2) {
                const data1 = getIntegerAndFactor(num1);
                const data2 = getIntegerAndFactor(num2);

                const productOfInts = data1.integer * data2.integer;
                let productStr = String(productOfInts);
                if (data1.factor > 1 || data2.factor > 1) {
                    productStr += ".";
                }

                // Special case for "convert to" when one is integer.
                const convertToStr = (num1, data1) => (data1.factor === 1) ? String(num1) : `${data1.integer}.`;
                const num1Str = convertToStr(num1, data1);
                const num2Str = convertToStr(num2, data2);

                const finalResult = num1 * num2;

                return [
                    `<p>start with: ${num1} \u00D7 ${num2}</p>`,
                    `<p>convert to: ${num1Str} \u00D7 ${num2Str} = ${productStr}</p>`,
                    `<p>estimate as: ${Math.round(num1)} \u00D7 ${Math.round(num2)} = ${Math.round(num1) * Math.round(num2)}</p>`,
                    `<p>result is: ${Number(finalResult.toPrecision(15))}</p>` // toPrecision to avoid floating point issues
                ].join('');
            }

            function generatePlaceValuesHTML(num1, num2) {
                const data1 = getIntegerAndFactor(num1);
                const data2 = getIntegerAndFactor(num2);

                const productOfInts = data1.integer * data2.integer;
                const productOfFactors = data1.factor * data2.factor;
                const finalResult = num1 * num2;

                const rewriteLine = `<p>rewrite as: ${formatRewritePart(data1)} \u00D7 ${formatRewritePart(data2)}</p>`;

                let lines = [
                    `<p>start with: ${num1} \u00D7 ${num2}</p>`
                ];

                // If both are integers, the rewrite line is redundant but shown in snapshot.
                // However, let's only show rewrite if at least one is a decimal, which is more logical.
                // The snapshot 4 logic is a bit odd, so we will follow the clearer snapshot 2 logic.
                if (data1.factor > 1 || data2.factor > 1 || (data1.factor === 1 && data2.factor === 1)) {
                    // Let's stick to the logic implied by screenshot 4, which always shows all steps
                    lines.push(rewriteLine);
                    lines.push(`<p>= (${data1.integer}. \u00D7 ${data2.integer}.) \u00F7 (${data1.factor} \u00D7 ${data2.factor})</p>`);
                    lines.push(`<p>= ${productOfInts}. \u00F7 ${productOfFactors}</p>`);
                } else {
                    // This case is for when both numbers are integers and we want to simplify the view
                    lines.push(`<p>= ${productOfInts}</p>`);
                }

                lines.push(`<p>= ${Number(finalResult.toPrecision(15))}</p>`);

                return lines.join('');
            }

            // --- Main Update Function ---
            function updateDisplay() {
                const method = selectMethod.value;
                const num1 = parseFloat(sliderNum1.value);
                const num2 = parseFloat(sliderNum2.value);

                let htmlContent = '';
                if (method === 'estimation') {
                    htmlContent = generateEstimationHTML(num1, num2);
                } else {
                    htmlContent = generatePlaceValuesHTML(num1, num2);
                }
                outputDisplay.innerHTML = htmlContent;
            }

            // --- Event Listeners ---
            selectMethod.addEventListener('change', updateDisplay);
            sliderNum1.addEventListener('input', updateDisplay);
            sliderNum2.addEventListener('input', updateDisplay);

            btnIntYes.addEventListener('click', () => {
                btnIntYes.classList.add('active');
                btnIntNo.classList.remove('active');
                sliderNum2.step = '1';
                // Round current value to the nearest integer and update slider
                sliderNum2.value = Math.round(sliderNum2.value);
                updateDisplay();
            });

            btnIntNo.addEventListener('click', () => {
                btnIntNo.classList.add('active');
                btnIntYes.classList.remove('active');
                sliderNum2.step = '0.1';
                updateDisplay(); // No need to change value, just allow decimals
            });

            // --- Initial State ---
            function initialize() {
                selectMethod.value = 'estimation';
                sliderNum1.value = 1.1;
                sliderNum2.value = 2;
                sliderNum2.step = '1';
                btnIntYes.classList.add('active');
                btnIntNo.classList.remove('active');
                updateDisplay();
            }

            initialize();
        });
    </script>

</body>
