            <p id="label-right"></p>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/three@0.132.2/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.132.2/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Elements ---
            const selectMode = document.getElementById('select-vibration-mode');
            const btnPlay = document.getElementById('btn-play');
            const btnPause = document.getElementById('btn-pause');
            const btnReset = document.getElementById('btn-reset');
            const canvasContainer = document.getElementById('three-js-canvas-container');
            const plotlyContainer = document.getElementById('plotly-chart-container');
            const descriptionArea = document.getElementById('description-area');
            const labelLeft = document.getElementById('label-left');
            const labelRight = document.getElementById('label-right');
            const controlButtons = [btnPlay, btnPause, btnReset];

            // --- State ---
            let currentMode = selectMode.value;
            let isPlaying = false;
            let animationFrameId;
            let time = 0;
            let plotRendered = false;

            // --- Three.js Setup ---
            let scene, renderer, cameraMolecule, cameraVectors, controlsMolecule, controlsVectors;
            const objects = {
                carbon: null,
                hydrogens: [],
                bonds: [],
                dipoleArrows: [],
                netDipoleArrow: null
            };

            const BOND_LENGTH = 1.09;
            const H_POSITIONS = [
                new THREE.Vector3(1, 1, 1),
                new THREE.Vector3(1, -1, -1),
                new THREE.Vector3(-1, 1, -1),
                new THREE.Vector3(-1, -1, 1)
            ].map(v => v.normalize().multiplyScalar(BOND_LENGTH));

            // --- Displacement vectors for each mode ---
            const DISPLACEMENTS = {
                'symmetric-stretch': H_POSITIONS.map(p => p.clone().normalize()),
                'asymmetric-stretch': [
                    H_POSITIONS[0].clone().normalize(),
                    H_POSITIONS[1].clone().normalize().negate(),
                    new THREE.Vector3(0, 0, 1).cross(H_POSITIONS[2]).normalize(),
                    new THREE.Vector3(0, 0, -1).cross(H_POSITIONS[3]).normalize(),
                ],
                'bending-e': [
                    new THREE.Vector3(0,0,1), new THREE.Vector3(0,0,1),
                    new THREE.Vector3(0,0,-1), new THREE.Vector3(0,0,-1)
                ],
                'bending-f2': Array(4).fill(new THREE.Vector3(1, 0, 0)),
            };


            function initThree() {
                // Scene
                scene = new THREE.Scene();

                // Renderer
                renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
                renderer.setPixelRatio(window.devicePixelRatio);
                renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
                renderer.setScissorTest(true);
                canvasContainer.appendChild(renderer.domElement);

                // Cameras
                const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
                cameraMolecule = new THREE.PerspectiveCamera(50, 0.5 * aspect, 0.1, 100);
                cameraMolecule.position.set(0, 0, 5);
                cameraVectors = new THREE.PerspectiveCamera(50, 0.5 * aspect, 0.1, 100);
                cameraVectors.position.set(0, 0, 5);

                // Controls
                controlsMolecule = new THREE.OrbitControls(cameraMolecule, renderer.domElement);
                controlsMolecule.enablePan = false;
                controlsMolecule.minDistance = 3;
                controlsMolecule.maxDistance = 10;

                controlsVectors = new THREE.OrbitControls(cameraVectors, renderer.domElement);
                controlsVectors.enablePan = false;
                controlsVectors.minDistance = 3;
                controlsVectors.maxDistance = 10;

                // Lights
                scene.add(new THREE.AmbientLight(0x888888));
                const light = new THREE.DirectionalLight(0xffffff, 1);
                light.position.set(1, 1, 5);
                scene.add(light);

                // Materials
                const carbonMat = new THREE.MeshLambertMaterial({ color: 0x111111 });
                const hydrogenMat = new THREE.MeshLambertMaterial({ color: 0xe0e0ff, emissive: 0x222233 });
                const bondMat = new THREE.MeshBasicMaterial({ color: 0x888888 });

                // Carbon atom
                objects.carbon = new THREE.Mesh(new THREE.SphereGeometry(0.3, 32, 32), carbonMat);
                scene.add(objects.carbon);

                // Hydrogens and bonds
                H_POSITIONS.forEach((pos, i) => {
                    const hydrogen = new THREE.Mesh(new THREE.SphereGeometry(0.2, 32, 32), hydrogenMat);
                    hydrogen.position.copy(pos);
                    objects.hydrogens.push(hydrogen);
                    scene.add(hydrogen);

                    const bond = new THREE.Mesh(new THREE.CylinderGeometry(0.05, 0.05, 1, 8), bondMat);
                    objects.bonds.push(bond);
                    scene.add(bond);

                    const arrow = new THREE.ArrowHelper(pos.clone().normalize(), new THREE.Vector3(0,0,0), 1, 0x000000);
                    objects.dipoleArrows.push(arrow);
                    scene.add(arrow);
                });

                // Net dipole arrow
                objects.netDipoleArrow = new THREE.ArrowHelper(new THREE.Vector3(1,0,0), new THREE.Vector3(0,0,0), 1, 0xff0000);
                objects.netDipoleArrow.visible = false;
                scene.add(objects.netDipoleArrow);

                updateBonds();
                animate();
            }

            function updateBonds() {
                const carbonPos = objects.carbon.position;
                objects.hydrogens.forEach((h, i) => {
                    const bond = objects.bonds[i];
                    const hPos = h.position;

                    const bondVec = new THREE.Vector3().subVectors(hPos, carbonPos);
                    bond.scale.y = bondVec.length();
                    bond.position.copy(carbonPos).addScaledVector(bondVec, 0.5);

                    const axis = new THREE.Vector3(0, 1, 0);
                    bond.quaternion.setFromUnitVectors(axis, bondVec.clone().normalize());

                    const dipoleArrow = objects.dipoleArrows[i];
                    dipoleArrow.setDirection(bondVec.clone().normalize());
                    dipoleArrow.setLength(bondVec.length() * 0.8, 0.1, 0.05);
                });
            }

            function animate() {
                animationFrameId = requestAnimationFrame(animate);

                if (isPlaying) {
                    time += 0.05;
                    updateVibration(time);
                }

                controlsMolecule.update();
                controlsVectors.update();
                render();
            }

            function render() {
                const { clientWidth, clientHeight } = canvasContainer;
                const halfWidth = clientWidth / 2;

                // Left viewport (Molecule)
                renderer.setViewport(0, 0, halfWidth, clientHeight);
                renderer.setScissor(0, 0, halfWidth, clientHeight);
                cameraMolecule.aspect = halfWidth / clientHeight;
                cameraMolecule.updateProjectionMatrix();

                objects.dipoleArrows.forEach(a => a.visible = false);
                objects.netDipoleArrow.visible = false;
                renderer.render(scene, cameraMolecule);

                // Right viewport (Vectors)
                renderer.setViewport(halfWidth, 0, halfWidth, clientHeight);
                renderer.setScissor(halfWidth, 0, halfWidth, clientHeight);
                cameraVectors.aspect = halfWidth / clientHeight;
                cameraVectors.updateProjectionMatrix();

                objects.dipoleArrows.forEach(a => a.visible = true);
                if (objects.netDipoleArrow.scale.y > 0.01) { // Only show if it has length
                    objects.netDipoleArrow.visible = true;
                }
                renderer.render(scene, cameraVectors);
            }

            function updateVibration(t) {
                const amplitude = 0.1;
                const frequency = 2.0;
                const displacementFactor = amplitude * Math.sin(frequency * t);
                const modeDisplacements = DISPLACEMENTS[currentMode];

                if (!modeDisplacements) return;

                let netDipole = new THREE.Vector3();

                // Carbon displacement for F2 bend
                if(currentMode === 'bending-f2') {
                    const cDisp = new THREE.Vector3(-1, 0, 0).multiplyScalar(displacementFactor * 0.25);
                    objects.carbon.position.copy(cDisp);
                } else {
                    objects.carbon.position.set(0,0,0);
                }

                objects.hydrogens.forEach((h, i) => {
                    const initialPos = H_POSITIONS[i];
                    const dispVec = modeDisplacements[i];
                    h.position.copy(initialPos).addScaledVector(dispVec, displacementFactor);
                });

                updateBonds();

                objects.dipoleArrows.forEach(arrow => {
                    netDipole.add(arrow.cone.position);
                });

                if (currentMode === 'asymmetric-stretch' || currentMode === 'bending-f2') {
                    objects.netDipoleArrow.setDirection(netDipole.clone().normalize());
                    objects.netDipoleArrow.setLength(netDipole.length() * 2, 0.2, 0.1);
                } else {
                    objects.netDipoleArrow.setLength(0,0,0);
                }
            }

            function resetAnimation() {
                isPlaying = false;
                if (animationFrameId) {
                    cancelAnimationFrame(animationFrameId);
                    animationFrameId = null;
                }
                time = 0;
                updateVibration(0);
                // Re-enable main animation loop for controls
                if(!animationFrameId) animate();
            }

            function updateView() {
                currentMode = selectMode.value;
                const isSpectrum = currentMode === 'ir-spectrum';

                canvasContainer.style.display = isSpectrum ? 'none' : 'block';
                descriptionArea.style.display = isSpectrum ? 'none' : 'flex';
                plotlyContainer.style.display = isSpectrum ? 'block' : 'none';

                controlButtons.forEach(btn => btn.disabled = isSpectrum);

                resetAnimation();

                let leftText = '', rightText = '', colorClass = 'text-blue';

                switch(currentMode) {
                    case 'symmetric-stretch':
                        leftText = "stretching A₁ Raman active 2916 cm⁻¹";
                        rightText = "no change in the molecular dipole moment";
                        colorClass = "text-blue";
                        break;
                    case 'asymmetric-stretch':
                        leftText = "stretching F₂ IR active 3019 cm⁻¹";
                        rightText = "change in the molecular dipole moment";
                        colorClass = "text-red";
                        break;
                    case 'bending-e':
                        leftText = "bending E Raman active 1534 cm⁻¹";
                        rightText = "no change in the molecular dipole moment";
                        colorClass = "text-blue";
                        break;
                    case 'bending-f2':
                        leftText = "bending F₂ IR active 1306 cm⁻¹";
                        rightText = "change in the molecular dipole moment";
                        colorClass = "text-red";
                        break;
                    case 'ir-spectrum':
                        if (!plotRendered) {
                            renderSpectrumPlot();
                            plotRendered = true;
                        }
                        break;
                }

                labelLeft.textContent = leftText;
                labelRight.textContent = rightText;
                labelLeft.className = labelRight.className = colorClass;
            }

            async function renderSpectrumPlot() {
                // Generate data
                const x = [];
                const y = [];
                for (let i = 400; i <= 4000; i+=2) {
                    x.push(i);
                    let val = 0.95 + (Math.random() - 0.5) * 0.03;
                    y.push(val);
                }

                const gaussian = (x, mean, sigma, amp) => amp * Math.exp(-0.5 * Math.pow((x - mean) / sigma, 2));

                const addPeak = (mean, sigma, amp, fineStructure) => {
                    for(let i=0; i<x.length; i++) {
                        y[i] -= gaussian(x[i], mean, sigma, amp);
                        if(fineStructure){
                             // Add rotational fine structure
                            for(let j = -5; j <= 5; j++){
                                if(j === 0) continue;
                                y[i] -= gaussian(x[i], mean + j * 15, 3, 0.1 + Math.random() * 0.2);
                            }
                        }
                    }
                }

                addPeak(1306, 20, 0.8, true);
                addPeak(3019, 25, 0.85, true);

                // Clamp y values
                for (let i = 0; i < y.length; i++) {
                    y[i] = Math.max(0, Math.min(1, y[i]));
                }

                const img1306 = generateMoleculeImage('bending-f2');
                const img3019 = generateMoleculeImage('asymmetric-stretch');

                const trace = { x, y, type: 'scatter', mode: 'lines', line: { color: '#4a6da7', width: 1.5 } };

                const layout = {
                    xaxis: { title: "wavenumber (cm⁻¹)", range: [400, 4000] },
                    yaxis: { title: "Transmittance", range: [0, 1.1] },
                    margin: { t: 20, r: 20, b: 40, l: 50 },
                    showlegend: false,
                    annotations: [
                        { x: 1306, y: 0.1, xref: 'x', yref: 'y', text: 'ν F₂ 1306 cm⁻¹', showarrow: true, arrowhead: 2, ax: 60, ay: -40, font: { color: 'red' } },
                        { x: 3019, y: 0.3, xref: 'x', yref: 'y', text: 'ν F₂ 3019 cm⁻¹', showarrow: true, arrowhead: 2, ax: 60, ay: -40, font: { color: 'red' } }
                    ],
                    images: [
                        { source: img1306, xref: 'x', yref: 'y', x: 1800, y: 0.55, sizex: 600, sizey: 0.3, xanchor: 'center', yanchor: 'middle' },
                        { source: img3019, xref: 'x', yref: 'y', x: 3400, y: 0.7, sizex: 600, sizey: 0.3, xanchor: 'center', yanchor: 'middle' }
                    ]
                };

                Plotly.newPlot('plotly-chart-container', [trace], layout, {staticPlot: true});
            }

            function generateMoleculeImage(mode) {
                const width = 150, height = 150;
                const offscreenCanvas = document.createElement('canvas');
                offscreenCanvas.width = width;
                offscreenCanvas.height = height;

                const imgRenderer = new THREE.WebGLRenderer({ canvas: offscreenCanvas, antialias: true, alpha: true });
                imgRenderer.setClearColor(0x000000, 0);

                const imgScene = new THREE.Scene();
                const imgCamera = new THREE.PerspectiveCamera(50, width/height, 0.1, 100);
                imgCamera.position.z = 4;

                imgScene.add(new THREE.AmbientLight(0xcccccc));
                const imgLight = new THREE.DirectionalLight(0xffffff, 0.8);
                imgLight.position.set(1, 1, 3);
                imgScene.add(imgLight);

                const carbon = new THREE.Mesh(new THREE.SphereGeometry(0.3, 16, 16), new THREE.MeshLambertMaterial({ color: 0x111111 }));
                imgScene.add(carbon);

                const displacements = DISPLACEMENTS[mode];
                const displacementFactor = 0.3; // static displacement

                if(mode === 'bending-f2') {
                    carbon.position.copy(new THREE.Vector3(-1, 0, 0).multiplyScalar(displacementFactor * 0.25));
                }

                H_POSITIONS.forEach((pos, i) => {
                    const hydrogen = new THREE.Mesh(new THREE.SphereGeometry(0.2, 16, 16), new THREE.MeshLambertMaterial({ color: 0xe0e0ff, emissive: 0x222233 }));
                    hydrogen.position.copy(pos).addScaledVector(displacements[i], displacementFactor);
                    imgScene.add(hydrogen);

                    const bond = new THREE.Mesh(new THREE.CylinderGeometry(0.05, 0.05, 1, 8), new THREE.MeshBasicMaterial({ color: 0x888888 }));
                    const bondVec = new THREE.Vector3().subVectors(hydrogen.position, carbon.position);
                    bond.scale.y = bondVec.length();
                    bond.position.copy(carbon.position).addScaledVector(bondVec, 0.5);
                    bond.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), bondVec.clone().normalize());
                    imgScene.add(bond);
                });

                imgRenderer.render(imgScene, imgCamera);
                return imgRenderer.domElement.toDataURL();
            }

            // --- Event Listeners ---
            selectMode.addEventListener('change', updateView);
            btnPlay.addEventListener('click', () => { isPlaying = true; });
            btnPause.addEventListener('click', () => { isPlaying = false; });
            btnReset.addEventListener('click', resetAnimation);
            window.addEventListener('resize', () => {
                if(renderer) {
                    renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
                }
                 if(plotRendered) {
                    Plotly.Plots.resize(plotlyContainer);
                 }
            });

            // --- Initial Call ---
            initThree();
            updateView();
        });
    </script>

</body>
</html>
