            text-align: right;
            padding-right: 5px;
            border-left: 1px solid #ccc;
            padding-left: 15px;
        }

        #btn-reset {
            width: 28px;
            height: 28px;
            border-radius: 50%;
            border: 1px solid #ccc;
            background-color: #f0f0f0;
            cursor: pointer;
            font-size: 24px;
            line-height: 26px;
            text-align: center;
            color: #666;
            padding: 0;
            transform: rotate(45deg);
            transition: background-color 0.2s, color 0.2s;
            flex-shrink: 0;
        }

