            background-color: #e0e0e0;
            color: #333;
        }

        .visualization-section {
            width: 100%;
        }

        #canvas-material-bar {
            width: 100%;
            height: 80px; /* Intrinsic size is set in JS */
        }

        #div-plot {
            width: 100%;
            height: 350px;
        }
    </style>
</head>
<body>

    <div class="main-container">
        <div class="control-panel">
            <label for="slider-time">time (seconds)</label>
            <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
            <span id="span-time-value">9.8</span>
            <button id="btn-reset" title="Reset to t=0">+</button>
        </div>

        <div class="visualization-section">
            <canvas id="canvas-material-bar"></canvas>
        </div>

        <div class="visualization-section">
            <div id="div-plot"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function () {
            // --- DOM Element References ---
            const slider = document.getElementById('slider-time');
            const timeValueSpan = document.getElementById('span-time-value');
            const resetButton = document.getElementById('btn-reset');
            const canvas = document.getElementById('canvas-material-bar');
            const plotDiv = document.getElementById('div-plot');
            const ctx = canvas.getContext('2d');

            // --- Constants and Configuration ---
            const CANVAS_WIDTH = 600;
            const CANVAS_HEIGHT = 80;
            const TOTAL_LENGTH_MM = 16;
            const PIXELS_PER_MM = CANVAS_WIDTH / TOTAL_LENGTH_MM;
            const BAR_HEIGHT = 50; // Height of the material bar in pixels
            const GLASS_END_MM = 6;
            const GLASS_COLOR_START = '#FF0000';
            const GLASS_COLOR_END = '#FFFFFF';
            const WATER_COLOR = '#00BFFF';
            const ICE_COLOR = '#003366';

            // Set canvas intrinsic size for high-DPI rendering
            canvas.width = CANVAS_WIDTH;
            canvas.height = CANVAS_HEIGHT;

            // --- Core Logic Function ---
            const calculateStefanDistance = (t) => 0.155 * Math.sqrt(t);

            // --- Visualization Functions ---

            /**
             * Draws the material bar on the canvas for a given time t.
             * @param {number} t - Time in seconds.
             */
            function drawMaterialBar(t) {
                const s = calculateStefanDistance(t);

                // Clear canvas
                ctx.clearRect(0, 0, canvas.width, canvas.height);

                // Calculate pixel positions
                const glassEndPx = GLASS_END_MM * PIXELS_PER_MM;
                const waterEndPx = (GLASS_END_MM + s) * PIXELS_PER_MM;
                const totalEndPx = TOTAL_LENGTH_MM * PIXELS_PER_MM;

                // 1. Draw Glass Block (Hot Plate)
                const gradient = ctx.createLinearGradient(0, 0, glassEndPx, 0);
                gradient.addColorStop(0, GLASS_COLOR_START);
                gradient.addColorStop(1, GLASS_COLOR_END);
                ctx.fillStyle = gradient;
                ctx.fillRect(0, 0, glassEndPx, BAR_HEIGHT);

                // 2. Draw Melted Water Layer
                ctx.fillStyle = WATER_COLOR;
                ctx.fillRect(glassEndPx, 0, waterEndPx - glassEndPx, BAR_HEIGHT);

                // 3. Draw Ice Block
                ctx.fillStyle = ICE_COLOR;
                ctx.fillRect(waterEndPx, 0, totalEndPx - waterEndPx, BAR_HEIGHT);

                // 4. Draw Axis and Labels
                const axisY = BAR_HEIGHT + 10;
                ctx.beginPath();
                ctx.moveTo(0, axisY);
                ctx.lineTo(totalEndPx, axisY);
                ctx.strokeStyle = '#333';
                ctx.stroke();

                ctx.fillStyle = '#333';
                ctx.font = '12px sans-serif';
                ctx.textAlign = 'center';

                [0, 5, 10, 15].forEach(label => {
                    const xPos = label * PIXELS_PER_MM;
                    ctx.fillText(label.toString(), xPos, axisY + 15);
                    // Tick mark
                    ctx.beginPath();
                    ctx.moveTo(xPos, axisY);
                    ctx.lineTo(xPos, axisY - 4);
                    ctx.stroke();
                });
            }

            /**
             * Initializes the Plotly chart.
             */
            function initPlot() {
                // Generate data for the curve
                const timePoints = [];
                const distancePoints = [];
                for (let t = 0; t <= 100; t += 0.1) {
                    timePoints.push(t);
                    distancePoints.push(calculateStefanDistance(t));
                }

                const trace = {
                    x: timePoints,
                    y: distancePoints,
                    type: 'scatter',
                    mode: 'lines',
                    name: 's(t)',
                    line: {
                        color: '#FFA500',
                        width: 2.5
                    }
                };

                const layout = {
                    title: 'Stefan distance versus time',
                    xaxis: {
                        title: 'time (s)',
                        range: [0, 100],
                        zeroline: false
                    },
                    yaxis: {
                        title: 'distance (mm)',
                        range: [0, 1.6],
                        zeroline: false
                    },
                    margin: { l: 50, r: 20, b: 40, t: 40, pad: 4 },
                    showlegend: false,
                    shapes: [] // To be updated with the indicator
                };

                const config = {responsive: true, displayModeBar: false};

                Plotly.newPlot(plotDiv, [trace], layout, config);
            }

            /**
             * Updates the vertical indicator line on the plot.
             * @param {number} t - Time in seconds.
             */
            function updatePlotIndicator(t) {
                const s = calculateStefanDistance(t);
                const indicatorShape = {
                    type: 'line',
                    x0: t,
                    y0: 0,
                    x1: t,
                    y1: s,
                    line: {
                        color: 'black',
                        width: 1.5,
                    }
                };
                Plotly.relayout(plotDiv, { shapes: [indicatorShape] });
            }

            /**
             * Master update function called on any interaction.
             */
            function updateAllVisualizations() {
                const t = parseFloat(slider.value);
                timeValueSpan.textContent = t.toFixed(1);
                drawMaterialBar(t);
                updatePlotIndicator(t);
            }


            // --- Event Listeners ---
            slider.addEventListener('input', updateAllVisualizations);

            resetButton.addEventListener('click', () => {
                slider.value = 0;
                // Programmatically trigger the input event to update everything
                slider.dispatchEvent(new Event('input', { 'bubbles': true }));
            });


            // --- Initial Load ---
            initPlot();
            updateAllVisualizations(); // Render initial state based on slider's default value

        });
    </script>

</body>
</html>
