<body>

    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input type="range" id="date-slider" min="0" max="364" step="1" value="340">
            <span id="date-display"></span>
            <button id="today-button">today</button>
        </div>

        <div id="visualization-panel">
            <div id="clocks-container">
                <div id="local-clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper">
                        <!-- Canvas will be created here by p5.js -->
                        <div id="local-time-digital"></div>
                    </div>
                </div>
                <div id="solar-clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper">
                        <!-- Canvas will be created here by p5.js -->
                        <div id="solar-time-digital"></div>
                    </div>
                </div>
            </div>
            <div id="offset-display"></div>
        </div>
    </div>

<script>
    // --- DOM Elements ---
    const dateSlider = document.getElementById('date-slider');
    const dateDisplay = document.getElementById('date-display');
    const todayButton = document.getElementById('today-button');
    const localTimeDigital = document.getElementById('local-time-digital');
    const solarTimeDigital = document.getElementById('solar-time-digital');
    const offsetDisplay = document.getElementById('offset-display');
    const localClockWrapper = document.getElementById('local-clock-wrapper');
    const solarClockWrapper = document.getElementById('solar-clock-wrapper');

    // --- State ---
    let userLongitude = 0; // Default: Greenwich
    let userLatitude = 51.5; // Default: London
    let localTime = new Date();
    let solarTime = new Date();

    // --- P5.js Drawing Function ---
    const drawClock = (p, time, faceColor) => {
        p.background(255); // Clear canvas
        p.translate(p.width / 2, p.height / 2);

        // Clock face
        p.stroke(0);
        p.strokeWeight(4);
        p.fill(faceColor);
        p.ellipse(0, 0, 230, 230);

        // Tick marks
        for (let i = 0; i < 60; i++) {
            p.push();
            p.rotate(p.map(i, 0, 60, 0, 360));
            p.strokeWeight(i % 5 === 0 ? 3 : 1);
            p.line(95, 0, 105, 0);
            p.pop();
        }

        // Numbers
        p.noStroke();
        p.fill(0);
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(24);
        for (let i = 1; i <= 12; i++) {
            const angle = p.map(i, 0, 12, -60, 300);
            const x = 80 * p.cos(angle);
            const y = 80 * p.sin(angle);
            p.text(i, x, y);
        }

        const s = time.getSeconds();
        const m = time.getMinutes();
        const h = time.getHours();

        // Hands
        p.strokeCap(p.ROUND);

        // Second hand
        p.push();
        p.rotate(p.map(s, 0, 60, -90, 270));
        p.stroke(255, 0, 0); // Red
        p.strokeWeight(2);
        p.line(-20, 0, 95, 0);
        p.pop();

        // Minute hand
        p.push();
        p.rotate(p.map(m + s / 60, 0, 60, -90, 270));
        p.stroke(0);
        p.strokeWeight(4);
        p.line(0, 0, 85, 0);
        p.pop();

        // Hour hand
        p.push();
        p.rotate(p.map(h % 12 + m / 60, 0, 12, -90, 270));
        p.stroke(0);
        p.strokeWeight(6);
        p.line(0, 0, 60, 0);
        p.pop();

        // Center pin
        p.fill(0);
        p.noStroke();
        p.ellipse(0, 0, 8, 8);
    };

    // --- P5.js Sketch Definitions ---
    const localClockSketch = (p) => {
        p.setup = () => {
            const canvas = p.createCanvas(250, 250);
            canvas.id('local-clock-canvas');
            canvas.parent(localClockWrapper);
            p.angleMode(p.DEGREES);
        };
        p.draw = () => {
            drawClock(p, localTime, '#E6E6FA'); // Lavender
        };
    };

    const solarClockSketch = (p) => {
        p.setup = () => {
            const canvas = p.createCanvas(250, 250);
            canvas.id('solar-clock-canvas');
            canvas.parent(solarClockWrapper);
            p.angleMode(p.DEGREES);
        };
        p.draw = () => {
            drawClock(p, solarTime, '#FAFAD2'); // LightGoldenRodYellow
        };
    };

    // --- Helper Functions ---
    const formatTime = (date) => {
        const h = date.getHours().toString().padStart(2, '0');
        const m = date.getMinutes().toString().padStart(2, '0');
        const s = date.getSeconds().toString().padStart(2, '0');
        return `${h}:${m}:${s}`;
    };

    const getDayOfYear = (date) => {
        const start = new Date(date.getFullYear(), 0, 0);
        const diff = date - start;
        const oneDay = 1000 * 60 * 60 * 24;
        return Math.floor(diff / oneDay) - 1; // 0-indexed for slider
    };

    const updateDateDisplay = () => {
        const dayOfYear = parseInt(dateSlider.value);
        const year = new Date().getFullYear();
        const date = new Date(year, 0, 1);
        date.setDate(dayOfYear + 1);

        const options = { weekday: 'long', day: '2-digit', month: 'long' };
        dateDisplay.textContent = date.toLocaleDateString('en-GB', options);
    };

    const setSliderToToday = () => {
        const today = new Date();
        const day = getDayOfYear(today);
        dateSlider.value = day;
        updateDateDisplay();
    };

    // --- Main Update Logic ---
    const updateTimes = () => {
        localTime = new Date();

        // Calculate Solar Time
        const dayOfYear = parseInt(dateSlider.value); // d from 0-364

        // Equation of Time (EoT) calculation
        // d needs to be 1-indexed for the formula (d-81)
        const B = (360 / 365.24) * ((dayOfYear + 1) - 81); // in degrees
        const B_rad = B * Math.PI / 180;
        const eot = 9.87 * Math.sin(2 * B_rad) - 7.53 * Math.cos(B_rad) - 1.5 * Math.sin(B_rad);

        // Time correction for longitude (minutes)
        const timeCorrectionMinutes = 4 * userLongitude;

        // Total offset from UTC to local solar time (minutes)
        const totalOffsetMinutes = eot + timeCorrectionMinutes;

        // Get UTC time in milliseconds
        const utcTimeMillis = localTime.getTime() + (localTime.getTimezoneOffset() * 60000);

        // Calculate solar time in milliseconds
        const solarTimeMillis = utcTimeMillis + (totalOffsetMinutes * 60000);
        solarTime = new Date(solarTimeMillis);

        // Update digital displays
        localTimeDigital.textContent = formatTime(localTime);
        solarTimeDigital.textContent = formatTime(solarTime);

        // Update offset display
        const offsetMinutes = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
        offsetDisplay.textContent = `offset of solar time from local time: ${offsetMinutes} minutes`;
    };


    // --- Initialization ---
    const init = () => {
        // Set up event listeners
        dateSlider.addEventListener('input', updateDateDisplay);
        todayButton.addEventListener('click', setSliderToToday);

        // Initial display setup
        updateDateDisplay();

        // Start p5 sketches
        new p5(localClockSketch);
        new p5(solarClockSketch);

        // Run initial update and start interval
        updateTimes();
        setInterval(updateTimes, 1000);
    };

    // Get user location, then initialize the application
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
            (position) => {
                userLatitude = position.coords.latitude;
                userLongitude = position.coords.longitude;
                init();
            },
            (error) => {
                console.warn(`Geolocation error (${error.code}): ${error.message}. Using default location.`);
                init(); // Initialize with default location
            }
        );
    } else {
        console.warn("Geolocation is not supported by this browser. Using default location.");
        init(); // Initialize with default location
    }

</script>

</body>
</html>
