            border: 1px solid #ddd;
            display: inline-block; /* To make the border wrap the canvas */
        }
    </style>
</head>
<body>

<div id="demo-container">
    <div id="control-panel">
        <div>
            <label for="input-steps">steps</label>
            <input type="number" id="input-steps" min="0" max="6" step="1" value="2">
        </div>
        <div>
            <label for="checkbox-zoom">zoom</label>
            <input type="checkbox" id="checkbox-zoom">
        </div>
        <div>
            <label for="select-color">color</label>
            <select id="select-color">
                <option value="DarkRainbow">DarkRainbow</option>
                <option value="SiennaTones">SiennaTones</option>
                <option value="RustTones">RustTones</option>
                <option value="FuchsiaTones">FuchsiaTones</option>
                <option value="Grays">Grays</option>
            </select>
        </div>
    </div>
    <div id="canvas-container"></div>
</div>

<script>
    // --- p5.js Sketch ---

    let inputSteps, checkboxZoom, selectColor;
    let canvas;

    const SQRT7 = Math.sqrt(7);
    const THETA = Math.atan(Math.sqrt(3) / 5) * (180 / Math.PI); // Approx 19.1066 degrees

    const colorPalettes = {
        "DarkRainbow": ['#1E4363', '#2E6341'],
        "SiennaTones": ['#A0522D', '#CD853F', '#D2B48C'],
        "RustTones": ['#B7410E', '#000033', '#FFFFFF'],
        "FuchsiaTones": ['#C71585', '#000000', '#FFFFFF'],
        "Grays": ['#404040', '#808080', '#C0C0C0']
    };

    // Pre-calculate subdivision transformations for a rhombus of size 1 at angle 0
    const childOffsets = [];
    const childAngleMods = [];

    function setup() {
        canvas = createCanvas(600, 600);
        canvas.parent('canvas-container');
        angleMode(DEGREES);

        // --- Setup Controls ---
        inputSteps = select('#input-steps');
        checkboxZoom = select('#checkbox-zoom');
        selectColor = select('#select-color');

        inputSteps.value(2);
        checkboxZoom.elt.checked = false;
        selectColor.value("DarkRainbow");

        inputSteps.changed(redraw);
        checkboxZoom.changed(redraw);
        selectColor.changed(redraw);

        // --- Pre-calculate subdivision geometry ---
        const u = createVector(1, 0);
        const v = createVector(cos(60), sin(60));
        const s = 1 / SQRT7;

        // Offsets relative to parent center for children
        childOffsets.push(createVector(0,0)); // Central child
        childOffsets.push(u.copy().mult(3).add(v).mult(s));
        childOffsets.push(u.copy().mult(2).sub(v).mult(s));
        childOffsets.push(u.copy().mult(-1).add(v.copy().mult(2)).mult(s));
        childOffsets.push(u.copy().mult(-3).sub(v).mult(s));
        childOffsets.push(u.copy().mult(-2).add(v).mult(s));
        childOffsets.push(u.copy().mult(1).sub(v.copy().mult(2)).mult(s));

        // Angle modifications for children
        childAngleMods.push(0);
        childAngleMods.push(THETA);
        childAngleMods.push(-THETA);
        childAngleMods.push(120 - THETA);
        childAngleMods.push(120 + THETA);
        childAngleMods.push(240 - THETA);
        childAngleMods.push(240 + THETA);

        noLoop();
        redraw();
    }

    function draw() {
        background(255);

        const steps = Number(inputSteps.value());
        const isZoomed = checkboxZoom.checked();
        const colorScheme = selectColor.value();
        const palette = colorPalettes[colorScheme];

        push();
        translate(width / 2, height / 2);

        // --- Handle Scaling ---
        const baseSize = 100; // Arbitrary size for step=0 rhombi
        let scaleFactor = 1.0;
        if (isZoomed) {
             // Zoom so that the structure at the current step level fills the view
            scaleFactor = pow(SQRT7, steps) * 0.8;
        } else {
            // Scale to fit the entire snowflake in the canvas
            const expectedRadius = baseSize * pow(SQRT7, steps);
            scaleFactor = (width / 2.5) / expectedRadius;
        }
        scale(scaleFactor);

        // --- Start Drawing ---
        // The initial shape is a hexagon of 6 rhombi
        for (let i = 0; i < 6; i++) {
            const angle = 60 * i;
            // The 3 base orientations are 0, 120, 240 degrees.
            // We alternate between them for the 6 initial rhombi.
            const orientationIndex = i % 3;
            // Rhombus center position to form a hexagon
            const r = baseSize * sqrt(3) / 2;
            const cx = r * cos(angle + 30);
            const cy = r * sin(angle + 30);

            drawTiling(steps, createVector(cx, cy), baseSize, angle, orientationIndex, palette);
        }

        // --- Special Case: Central Hexagon ---
        if (steps > 0 && (colorScheme === 'RustTones' || colorScheme === 'FuchsiaTones' || colorScheme === 'SiennaTones')) {
            const hexColor = palette[2];
            const hexRadius = baseSize / (2 * pow(SQRT7, steps -1));
            fill(hexColor);
            stroke(200);
            strokeWeight(1/scaleFactor);
            drawHexagon(0, 0, hexRadius);
        }

        pop();
    }

    function drawTiling(level, center, size, angle, orientation, palette) {
        if (level === 0) {
            drawRhombus(center.x, center.y, size, angle, orientation, palette);
            return;
        }

        // Recursive step: replace one rhombus with 7 smaller ones
        const newSize = size / SQRT7;
        const newLevel = level - 1;

        // The central child keeps the same orientation index
        drawTiling(newLevel, center, newSize, angle, orientation, palette);

        // The 6 outer children
        const u = createVector(size * cos(angle), size * sin(angle));
        const v = createVector(size * cos(angle + 60), size * sin(angle + 60));

        for (let i = 1; i < 7; i++) {
            // This subdivision rule creates a mirrored pattern compared to some definitions,
            // but generates a valid Gosper tiling.
            const offset = p5.Vector.add(u.copy().mult(childOffsets[i].x), v.copy().mult(childOffsets[i].y));
            const newCenter = p5.Vector.add(center, offset);
            const newAngle = angle + childAngleMods[i];

            // The new orientation depends on the original and the rotation
            // This is a simplification to cycle colors. A more rigorous approach
            // would track the absolute orientation.
            const newOrientation = (orientation + floor(childAngleMods[i] / 120) + 3) % 3;

            drawTiling(newLevel, newCenter, newSize, newAngle, newOrientation, palette);
        }
    }

    function drawRhombus(cx, cy, size, angle, orientation, palette) {
        const color = palette[orientation % palette.length];

        push();
        translate(cx, cy);
        rotate(angle);

        // Vertices for a rhombus with side `size` and internal angle 60/120 degrees
        const x1 = 0, y1 = 0;
        const x2 = size, y2 = 0;
        const x3 = size + size * cos(60), y3 = size * sin(60);
        const x4 = size * cos(60), y4 = size * sin(60);

        // Center the shape before drawing
        const centerOffsetX = (x1 + x3) / 2;
        const centerOffsetY = (y1 + y3) / 2;
        translate(-centerOffsetX, -centerOffsetY);

        fill(color);
        noStroke();
        quad(x1, y1, x2, y2, x3, y3, x4, y4);

        // Draw faint internal line
        stroke(255, 255, 255, 50); // Semi-transparent white
        strokeWeight(1.5 / drawingContext.getTransform().a); // Keep stroke width constant regardless of zoom
        line(x2, y2, x4, y4); // Line between obtuse vertices

        pop();
    }

    function drawHexagon(cx, cy, radius) {
        push();
        translate(cx, cy);
        beginShape();
        for (let i = 0; i < 6; i++) {
            const angle = 60 * i;
            const x = radius * cos(angle);
            const y = radius * sin(angle);
            vertex(x, y);
        }
        endShape(CLOSE);
        pop();
    }

</script>

</body>
</html>
