    // --- P5.JS SKETCH ---
    const sketch = (p) => {
        const CANVAS_WIDTH = 660;
        const CANVAS_HEIGHT = 500;
        const PIXELS_PER_CM = 50; 
        const MAGNET_SIZE_CM = 1.2;
        const MAGNET_SPACING_CM = 1.3;

        const magnetPositions = [];
        const orientationVectors = [
            p.createVector(1, 0, 0), // →
