            p.createVector(0, -1, 0), // ↓
            p.createVector(0, 0, 1), // ⊙
            p.createVector(0, 0, -1), // ⊗
        ];

        const colorMin = p.color(255, 253, 231); // light yellow/cream
        const colorMid = p.color(255, 109, 0);   // orange
        const colorMax = p.color(26, 35, 126);   // dark blue

        p.setup = () => {
            const canvas = p.createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
            canvas.parent(visualizationContainer);
            p.pixelDensity(1);
            p.noLoop();

            for (let i = 0; i < 5; i++) {
                magnetPositions.push(p.createVector((i - 2) * MAGNET_SPACING_CM, 0, 0));
            }

            redrawVisualization();
        };

        const redrawVisualization = () => {
            p.background(255);
            if (state.plotType === 'stream') {
                drawStreamPlot();
            } else if (state.plotType === 'density') {
                drawDensityPlot();
            }
            drawMagnets();
        };

        // Make redrawVisualization globally accessible
        window.redrawVisualization = redrawVisualization;

        function worldToPixel(v) {
            return p.createVector(v.x * PIXELS_PER_CM + p.width / 2, v.y * PIXELS_PER_CM + p.height / 2);
        }

        function pixelToWorld(px, py) {
            return p.createVector((px - p.width / 2) / PIXELS_PER_CM, (py - p.height / 2) / PIXELS_PER_CM, 0);
        }

        function calculateField(x_px, y_px) {
            const p_world = pixelToWorld(x_px, y_px);
            const totalField = p.createVector(0, 0, 0);

            for (let i = 0; i < 5; i++) {
                const magnetPos = magnetPositions[i];
                const moment = orientationVectors[state.magnetOrientations[i]].copy().mult(state.magnetStrength);
                const r = p5.Vector.sub(p_world, magnetPos);
                const rMag = r.mag();

                if (rMag < 0.1) continue; // Avoid singularity

                const rMag3 = rMag * rMag * rMag;
                const rMag5 = rMag3 * rMag * rMag;

                const term1 = r.copy().mult(3 * moment.dot(r) / rMag5);
                const term2 = moment.copy().div(rMag3);
                const field = p5.Vector.sub(term1, term2);

                totalField.add(field);
            }
            return totalField;
        }

        function drawStreamPlot() {
            p.stroke(0, 0, 200, 180);
            p.strokeWeight(1);

            const stepLength = 5;
            const numSteps = 80;
            const arrowInterval = 15;

            for (let y = 0; y < p.height; y += 35) {
                for (let x = 0; x < p.width; x += 35) {
                    // Trace forward
                    let currentPos = p.createVector(x, y);
                    for (let s=0; s < numSteps; s++) {
                        if (currentPos.x < 0 || currentPos.x > p.width || currentPos.y < 0 || currentPos.y > p.height) break;
                        const field = calculateField(currentPos.x, currentPos.y);
                        if (field.magSq() === 0) break;

                        const dir = field.normalize();
                        const nextPos = p5.Vector.add(currentPos, dir.mult(stepLength));
                        p.line(currentPos.x, currentPos.y, nextPos.x, nextPos.y);

                        if (s > 0 && s % arrowInterval === 0) {
                            p.push();
                            p.translate(currentPos.x, currentPos.y);
                            p.rotate(dir.heading());
                            p.line(0, 0, -5, -3);
                            p.line(0, 0, -5, 3);
                            p.pop();
                        }
                        currentPos = nextPos;
                    }

                    // Trace backward
                    currentPos = p.createVector(x, y);
                    for (let s=0; s < numSteps; s++) {
                         if (currentPos.x < 0 || currentPos.x > p.width || currentPos.y < 0 || currentPos.y > p.height) break;
                        const field = calculateField(currentPos.x, currentPos.y);
                        if (field.magSq() === 0) break;

                        const dir = field.normalize().mult(-1); // Go backward
                        const nextPos = p5.Vector.add(currentPos, dir.mult(stepLength));
                        p.line(currentPos.x, currentPos.y, nextPos.x, nextPos.y);

                        currentPos = nextPos;
                    }
                }
            }
        }

        function drawDensityPlot() {
            const gridSize = 5;
            let minMag = Infinity;
            let maxMag = -Infinity;
            const magnitudes = [];

            // Pass 1: Calculate magnitudes and find min/max
            for (let y = 0; y < p.height; y += gridSize) {
                const row = [];
                for (let x = 0; x < p.width; x += gridSize) {
                    const field = calculateField(x, y);
                    const mag = field.mag();
                    row.push(mag);
                    if (mag < minMag) minMag = mag;
                    if (mag > maxMag) maxMag = mag;
                }
                magnitudes.push(row);
            }

            // Avoid division by zero if field is uniform
            if (maxMag === minMag) maxMag = minMag + 1;

            // Pass 2: Draw colored rectangles
            p.noStroke();
            for (let i = 0; i < magnitudes.length; i++) {
                for (let j = 0; j < magnitudes[i].length; j++) {
                    const mag = magnitudes[i][j];
                    const normMag = p.constrain(p.map(mag, minMag, maxMag, 0, 1), 0, 1);

                    let c;
                    if (normMag < 0.5) {
                        c = p.lerpColor(colorMin, colorMid, normMag * 2);
                    } else {
                        c = p.lerpColor(colorMid, colorMax, (normMag - 0.5) * 2);
                    }

                    p.fill(c);
                    p.rect(j * gridSize, i * gridSize, gridSize, gridSize);
                }
            }
        }

        function drawMagnets() {
            const w = MAGNET_SIZE_CM * PIXELS_PER_CM;
            const h = MAGNET_SIZE_CM * PIXELS_PER_CM;
            const depth = w * 0.4;

            p.push();
            p.translate(p.width / 2, p.height / 2);
            p.strokeWeight(1.5);

            for (let i = 0; i < 5; i++) {
                p.push();
                const center_x = (i - 2) * MAGNET_SPACING_CM * PIXELS_PER_CM;
                p.translate(center_x, 0);

                // Draw pseudo-3D box
                p.fill(180);
                p.stroke(0);
                const x1 = -w / 2, y1 = -h / 2;
                const x2 = w / 2, y2 = h / 2;
                const dx = depth * 0.5, dy = -depth * 0.5;

                // Top face
                p.quad(x1, y1, x1 + dx, y1 + dy, x2 + dx, y1 + dy, x2, y1);
                // Left face
                p.quad(x1, y1, x1, y2, x1 + dx, y2 + dy, x1 + dx, y1 + dy);
                // Front face
                p.fill(220);
                p.rect(x1, y1, w, h);

                // Draw symbol
                p.noStroke();
                p.fill(0, 102, 255); // Blue
                p.textAlign(p.CENTER, p.CENTER);
                p.textFont('Arial');

                const orientation = state.magnetOrientations[i];
                if (orientation <= 3) { // Arrows
                    p.textSize(w * 0.6);
                    p.text(ORIENTATION_SYMBOLS[orientation], 0, 0);
                } else { // Circles
                    p.stroke(0, 102, 255);
                    p.strokeWeight(3);
                    p.noFill();
                    p.ellipse(0, 0, w * 0.6, h * 0.6);
                    if (orientation === 4) { // ⊙ (out)
                        p.fill(0, 102, 255);
                        p.noStroke();
                        p.ellipse(0, 0, w * 0.15, h * 0.15);
                    } else { // ⊗ (in)
                         p.line(-w*0.15, -h*0.15, w*0.15, h*0.15);
                         p.line(w*0.15, -h*0.15, -w*0.15, h*0.15);
                    }
                }
                p.pop();
            }
            p.pop();
        }
    };

    // --- CONTROL INITIALIZATION AND EVENT LISTENERS ---

    function setupControls() {
        // Generate magnet orientation controls
        for (let i = 0; i < 5; i++) {
            const controlDiv = document.createElement('div');
            controlDiv.className = 'magnet-control';
            controlDiv.id = `magnet-control-${i}`;

            const displaySpan = document.createElement('span');
            displaySpan.className = 'magnet-orientation-display';
            displaySpan.id = `magnet-orientation-display-${i}`;

            const buttonContainer = document.createElement('div');
            buttonContainer.className = 'orientation-buttons';

            const upButton = document.createElement('button');
            upButton.id = `magnet-orientation-up-${i}`;
            upButton.textContent = '🔼';
            upButton.onclick = () => updateOrientation(i, 1);

            const downButton = document.createElement('button');
            downButton.id = `magnet-orientation-down-${i}`;
            downButton.textContent = '🔽';
            downButton.onclick = () => updateOrientation(i, -1);

            buttonContainer.appendChild(upButton);
            buttonContainer.appendChild(downButton);
            controlDiv.appendChild(displaySpan);
            controlDiv.appendChild(buttonContainer);
            orientationControlsContainer.appendChild(controlDiv);
        }

        // Strength slider
        strengthSlider.addEventListener('input', (e) => {
            state.magnetStrength = parseInt(e.target.value);
            updateUI();
            window.redrawVisualization();
        });

        // Plot type buttons
        streamPlotBtn.addEventListener('click', () => {
            state.plotType = 'stream';
            updateUI();
            window.redrawVisualization();
        });

        densityPlotBtn.addEventListener('click', () => {
            state.plotType = 'density';
            updateUI();
            window.redrawVisualization();
        });

        updateUI();
    }

    function updateOrientation(index, direction) {
        state.magnetOrientations[index] = (state.magnetOrientations[index] + direction + 6) % 6;
        updateUI();
        window.redrawVisualization();
    }

    function updateUI() {
        // Update orientation displays
        for (let i = 0; i < 5; i++) {
            document.getElementById(`magnet-orientation-display-${i}`).textContent = ORIENTATION_SYMBOLS[state.magnetOrientations[i]];
        }

        // Update strength display
        strengthValueSpan.textContent = state.magnetStrength;
        strengthSlider.value = state.magnetStrength;

        // Update plot type buttons
        streamPlotBtn.classList.toggle('active', state.plotType === 'stream');
        densityPlotBtn.classList.toggle('active', state.plotType === 'density');
    }

    // --- START ---
    setupControls();
    new p5(sketch);
});
</script>

</body>
