    <script>
        // --- DATA (Hardcoded) ---
        // These points define the phase envelope
        const alphaCurvePoints = [
            { xA: 0.00, xB: 0.85, xC: 0.15 }, { xA: 0.10, xB: 0.80, xC: 0.10 },
            { xA: 0.20, xB: 0.73, xC: 0.07 }, { xA: 0.30, xB: 0.64, xC: 0.06 },
            { xA: 0.40, xB: 0.54, xC: 0.06 }, { xA: 0.45, xB: 0.48, xC: 0.07 },
            { xA: 0.50, xB: 0.41, xC: 0.09 }, { xA: 0.52, xB: 0.37, xC: 0.11 } // Plait point
        ];
        const betaCurvePoints = [
            { xA: 0.00, xB: 0.10, xC: 0.90 }, { xA: 0.10, xB: 0.10, xC: 0.80 },
            { xA: 0.20, xB: 0.11, xC: 0.69 }, { xA: 0.30, xB: 0.13, xC: 0.57 },
            { xA: 0.40, xB: 0.18, xC: 0.42 }, { xA: 0.45, xB: 0.23, xC: 0.32 },
            { xA: 0.50, xB: 0.29, xC: 0.21 }, { xA: 0.52, xB: 0.37, xC: 0.11 } // Plait point
        ].reverse();
        const phaseEnvelopePolygon = [...alphaCurvePoints, ...betaCurvePoints];

        // These define the tie lines
        const tieLinesData = [
            { alpha: { xA: 0.03, xB: 0.83, xC: 0.14 }, beta: { xA: 0.02, xB: 0.11, xC: 0.87 } },
            { alpha: { xA: 0.08, xB: 0.81, xC: 0.11 }, beta: { xA: 0.06, xB: 0.11, xC: 0.83 } },
            { alpha: { xA: 0.14, xB: 0.77, xC: 0.09 }, beta: { xA: 0.12, xB: 0.11, xC: 0.77 } },
            { alpha: { xA: 0.21, xB: 0.72, xC: 0.07 }, beta: { xA: 0.19, xB: 0.11, xC: 0.70 } },
            { alpha: { xA: 0.29, xB: 0.66, xC: 0.05 }, beta: { xA: 0.27, xB: 0.12, xC: 0.61 } },
            { alpha: { xA: 0.38, xB: 0.58, xC: 0.04 }, beta: { xA: 0.36, xB: 0.15, xC: 0.49 } },
            { alpha: { xA: 0.46, xB: 0.47, xC: 0.07 }, beta: { xA: 0.44, xB: 0.24, xC: 0.32 } },
            { alpha: { xA: 0.50, xB: 0.41, xC: 0.09 }, beta: { xA: 0.49, xB: 0.30, xC: 0.21 } }
        ];


        // --- UI STATE ---
        let currentView = 'diagram';
        let showGridLines = false;
        let showTieLines = true;
        let showAlphaPhase = true;
        let showBetaPhase = true;
        let userPoint = { xA: 0.42, xB: 0.47, xC: 0.11 };

        // --- P5.js Sketch ---
        const sketch = (p) => {
            let canvas;
            const margin = 80;
            let pA, pB, pC; // Triangle vertices
            let sideLength;
            const colorA = '#008800', colorB = '#0000FF', colorC = '#FF0000';
            const colorAlpha = '#FFA500', colorBeta = '#FF00FF';

            p.setup = () => {
                const container = document.getElementById('canvas-container');
                const w = container.offsetWidth;
                const h = container.offsetHeight;
                canvas = p.createCanvas(w, h);
                canvas.parent('canvas-container');

                p.noLoop();
                setupDOMListeners();
                updateUIState();
            };

            p.draw = () => {
                p.background(255);
                calculateTriangleGeometry();

                if (currentView === 'diagram') {
                    drawDiagramView();
                } else if (currentView === 'phases') {
                    drawPhasesView();
                }
            };

            function calculateTriangleGeometry() {
                const w = p.width;
                const h = p.height;
                sideLength = Math.min(w, h / (Math.sqrt(3)/2)) - 2 * margin;
                const triangleHeight = sideLength * Math.sqrt(3) / 2;
                const centerX = w / 2;
                const centerY = h / 2;

                pA = p.createVector(centerX, centerY - triangleHeight / 2);
                pB = p.createVector(centerX - sideLength / 2, centerY + triangleHeight / 2);
                pC = p.createVector(centerX + sideLength / 2, centerY + triangleHeight / 2);
            }

            // --- MAIN DRAWING LOGIC ---

            function drawDiagramView() {
                if (showGridLines) drawGridLinesFunc();
                drawPhaseEnvelope();
                if (showTieLines) drawTieLinesFunc();

                const userPos = barycentricToCartesian(userPoint.xA, userPoint.xB, userPoint.xC);

                drawProjectionLines(userPos, userPoint, [colorA, colorB, colorC], 2, 'solid');

                const inEnvelope = isInsideEnvelope(userPoint);

                if (inEnvelope) {
                    const { alpha, beta } = getPhaseCompositions(userPoint);

                    if (alpha && beta) {
                        const alphaPos = barycentricToCartesian(alpha.xA, alpha.xB, alpha.xC);
                        const betaPos = barycentricToCartesian(beta.xA, beta.xB, beta.xC);

                        p.stroke(0);
                        p.strokeWeight(1.5);
                        p.drawingContext.setLineDash([5, 5]);
                        p.line(alphaPos.x, alphaPos.y, betaPos.x, betaPos.y);
                        p.drawingContext.setLineDash([]);

                        if (showAlphaPhase) {
                            drawProjectionLines(alphaPos, alpha, [colorA, colorB, colorC], 1, 'dashed');
                            p.fill(colorAlpha);
                            p.noStroke();
                            p.ellipse(alphaPos.x, alphaPos.y, 10, 10);
                        }
                        if (showBetaPhase) {
                             drawProjectionLines(betaPos, beta, [colorA, colorB, colorC], 1, 'dashed');
                            p.fill(colorBeta);
                            p.noStroke();
                            p.ellipse(betaPos.x, betaPos.y, 10, 10);
                        }
                    }
                }

                // Draw user point on top
                p.fill(0);
                p.noStroke();
                p.ellipse(userPos.x, userPos.y, 12, 12);

                drawTriangleFrame(); // Draw frame last to be on top of fills
            }

            function drawPhasesView() {
                // Fill regions
                p.noStroke();

                // One Phase Region
                p.fill('#fbe5e6'); // light red
                p.beginShape();
                p.vertex(pA.x, pA.y); p.vertex(pB.x, pB.y); p.vertex(pC.x, pC.y);
                p.beginContour();
                for (const pt of phaseEnvelopePolygon) {
                    const pos = barycentricToCartesian(pt.xA, pt.xB, pt.xC);
                    p.vertex(pos.x, pos.y);
                }
                p.endContour();
                p.endShape(p.CLOSE);

                // Two Phase Region
                p.fill('#d4eafc'); // light blue
                p.beginShape();
                for (const pt of phaseEnvelopePolygon) {
                    const pos = barycentricToCartesian(pt.xA, pt.xB, pt.xC);
                    p.vertex(pos.x, pos.y);
                }
                p.endShape(p.CLOSE);

                // Draw phase labels
                const twoPhaseCenter = barycentricToCartesian(0.25, 0.4, 0.35);
                const onePhaseCenter = barycentricToCartesian(0.6, 0.2, 0.2);
                p.fill(0);
                p.textSize(16);
                p.textAlign(p.CENTER, p.CENTER);
                p.text("two phases", twoPhaseCenter.x, twoPhaseCenter.y);
                p.text("one phase", onePhaseCenter.x, onePhaseCenter.y);

                // Plait point
                const plaitPointComp = phaseEnvelopePolygon[alphaCurvePoints.length - 1];
                const plaitPointPos = barycentricToCartesian(plaitPointComp.xA, plaitPointComp.xB, plaitPointComp.xC);
                p.fill(0);
                p.noStroke();
                p.ellipse(plaitPointPos.x, plaitPointPos.y, 10, 10);
                p.textAlign(p.LEFT, p.CENTER);
                p.text("plait point", plaitPointPos.x - 80, plaitPointPos.y - 20);
                p.noFill();
                p.stroke(0);
                p.strokeWeight(1);
                p.beginShape();
                p.vertex(plaitPointPos.x - 15, plaitPointPos.y - 15);
                p.quadraticVertex(plaitPointPos.x - 10, plaitPointPos.y + 5, plaitPointPos.x, plaitPointPos.y - 2);
                p.endShape();
                p.line(plaitPointPos.x - 1, plaitPointPos.y - 6, plaitPointPos.x - 6, plaitPointPos.y-1);
                p.line(plaitPointPos.x-1, plaitPointPos.y-6, plaitPointPos.x+3, plaitPointPos.y-8);


                // Curve Labels
                const alphaLabelPos = barycentricToCartesian(0.2, 0.73, 0.07);
                const betaLabelPos = barycentricToCartesian(0.35, 0.15, 0.5);
                p.push();
                p.translate(alphaLabelPos.x, alphaLabelPos.y);
                p.rotate(-p.PI/3 - 0.1);
                p.fill(colorAlpha);
                p.text("alpha phase", 0, -10);
                p.pop();

                p.push();
                p.translate(betaLabelPos.x, betaLabelPos.y);
                p.rotate(p.PI/3 + 0.1);
                p.fill(colorBeta);
                p.text("beta phase", 0, -10);
                p.pop();

                drawPhaseEnvelope();
                drawTriangleFrame();

                 // Draw user point on top
                const userPos = barycentricToCartesian(userPoint.xA, userPoint.xB, userPoint.xC);
                p.fill(0);
                p.noStroke();
                p.ellipse(userPos.x, userPos.y, 10, 10);
            }


            // --- DRAWING HELPER FUNCTIONS ---

            function drawTriangleFrame() {
                p.stroke(0);
                p.strokeWeight(2);
                p.noFill();
                p.triangle(pA.x, pA.y, pB.x, pB.y, pC.x, pC.y);

                p.noStroke();
                p.textSize(20);
                p.textAlign(p.CENTER, p.CENTER);
                p.fill(colorA);
                p.text('A', pA.x, pA.y - 20);
                p.fill(colorB);
                p.text('B', pB.x - 20, pB.y);
                p.fill(colorC);
                p.text('C', pC.x + 20, pC.y);

                drawAxisTicks();
            }

            function drawAxisTicks() {
                p.strokeWeight(1);
                p.textSize(12);

                for (let i = 1; i < 10; i++) {
                    const val = i * 0.1;

                    // A-B side (for C)
                    let p1 = p5.Vector.lerp(pA, pB, val);
                    let p2 = p5.Vector.lerp(pC, pB, 1-val);
                    p.stroke(colorC);
                    p.line(p1.x, p1.y, p1.x + 5, p1.y + 8.66);
                    p.push();
                    p.translate(p2.x, p2.y);
                    p.rotate(p.PI/3);
                    p.noStroke();
                    p.fill(colorC);
                    p.textAlign(p.LEFT, p.CENTER);
                    p.text(val.toFixed(1), 8, 0);
                    p.pop();

                    // B-C side (for A)
                    p1 = p5.Vector.lerp(pB, pC, val);
                    p2 = p5.Vector.lerp(pA, pC, val);
                    p.stroke(colorA);
                    p.line(p1.x, p1.y, p1.x - 10, p1.y);
                    p.push();
                    p.translate(p2.x, p2.y);
                    p.rotate(-p.PI/3);
                    p.noStroke();
                    p.fill(colorA);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text((1-val).toFixed(1), -8, 0);
                    p.pop();

                    // C-A side (for B)
                    p1 = p5.Vector.lerp(pC, pA, val);
                    p2 = p5.Vector.lerp(pB, pA, 1-val);
                    p.stroke(colorB);
                    p.line(p1.x, p1.y, p1.x + 5, p1.y - 8.66);
                    p.push();
                    p.translate(p2.x, p2.y);
                    p.noStroke();
                    p.fill(colorB);
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text((1-val).toFixed(1), -5, 10);
                    p.pop();
                }
            }

            function drawGridLinesFunc() {
                p.stroke(200);
                p.strokeWeight(0.5);
                for (let i = 1; i < 10; i++) {
                    const val = i * 0.1;
                    // Lines of constant C
                    let p1 = barycentricToCartesian(1 - val, 0, val);
                    let p2 = barycentricToCartesian(0, 1 - val, val);
                    p.line(p1.x, p1.y, p2.x, p2.y);
                    // Lines of constant A
                    p1 = barycentricToCartesian(val, 1-val, 0);
                    p2 = barycentricToCartesian(val, 0, 1-val);
                    p.line(p1.x, p1.y, p2.x, p2.y);
                    // Lines of constant B
                    p1 = barycentricToCartesian(1-val, val, 0);
                    p2 = barycentricToCartesian(0, val, 1-val);
                    p.line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            function drawPhaseEnvelope() {
                p.noFill();
                p.strokeWeight(2);

                // Alpha curve
                p.stroke(colorAlpha);
                p.beginShape();
                alphaCurvePoints.forEach(pt => {
                    const pos = barycentricToCartesian(pt.xA, pt.xB, pt.xC);
                    p.vertex(pos.x, pos.y);
                });
                p.endShape();

                // Beta curve
                p.stroke(colorBeta);
                p.beginShape();
                betaCurvePoints.forEach(pt => {
                    const pos = barycentricToCartesian(pt.xA, pt.xB, pt.xC);
                    p.vertex(pos.x, pos.y);
                });
                p.endShape();

                 // Base line
                const start = alphaCurvePoints[0];
                const end = betaCurvePoints[betaCurvePoints.length - 1];
                const startPos = barycentricToCartesian(start.xA, start.xB, start.xC);
                const endPos = barycentricToCartesian(end.xA, end.xB, end.xC);
                p.stroke(0);
                p.line(startPos.x, startPos.y, endPos.x, endPos.y);
            }

            function drawTieLinesFunc() {
                p.stroke(128);
                p.strokeWeight(1);
                tieLinesData.forEach(line => {
                    const pAlpha = barycentricToCartesian(line.alpha.xA, line.alpha.xB, line.alpha.xC);
                    const pBeta = barycentricToCartesian(line.beta.xA, line.beta.xB, line.beta.xC);
                    p.line(pAlpha.x, pAlpha.y, pBeta.x, pBeta.y);
                });
            }

            function drawProjectionLines(pos, comp, colors, weight, style) {
                if (style === 'dashed') {
                    p.drawingContext.setLineDash([5, 5]);
                }

                // To A axis (parallel to C-A side)
                p.stroke(colors[0]); p.strokeWeight(weight);
                let targetA = barycentricToCartesian(comp.xA, 1 - comp.xA, 0);
                drawArrow(pos, targetA, colors[0]);

                // To B axis (parallel to A-B side)
                p.stroke(colors[1]); p.strokeWeight(weight);
                let targetB = barycentricToCartesian(0, comp.xB, 1 - comp.xB);
                drawArrow(pos, targetB, colors[1]);

                // To C axis (parallel to B-C side)
                p.stroke(colors[2]); p.strokeWeight(weight);
                let targetC = barycentricToCartesian(1-comp.xC, 0, comp.xC);
                drawArrow(pos, targetC, colors[2]);

                p.drawingContext.setLineDash([]);
            }

            function drawArrow(base, vec, myColor) {
                p.push();
                p.stroke(myColor);
                p.strokeWeight(p.strokeWeight());
                p.fill(myColor);
                p.line(base.x, base.y, vec.x, vec.y);
                p.translate(vec.x, vec.y);
                let angle = p.atan2(base.y - vec.y, base.x - vec.x);
                p.rotate(angle - p.HALF_PI);
                p.triangle(-5, 0, 5, 0, 0, -8);
                p.pop();
            }

            // --- COORDINATE & GEOMETRY LOGIC ---

            function barycentricToCartesian(xA, xB, xC) {
                let x = xA * pA.x + xB * pB.x + xC * pC.x;
                let y = xA * pA.y + xB * pB.y + xC * pC.y;
                return p.createVector(x, y);
            }

            function cartesianToBarycentric(px, py) {
                const p_vec = p.createVector(px, py);
                const v0 = p5.Vector.sub(pB, pA);
                const v1 = p5.Vector.sub(pC, pA);
                const v2 = p5.Vector.sub(p_vec, pA);

                const d00 = v0.dot(v0);
                const d01 = v0.dot(v1);
                const d11 = v1.dot(v1);
                const d20 = v2.dot(v0);
                const d21 = v2.dot(v1);

                const denom = d00 * d11 - d01 * d01;
                const xB = (d11 * d20 - d01 * d21) / denom;
                const xC = (d00 * d21 - d01 * d20) / denom;
                const xA = 1.0 - xB - xC;

                return { xA, xB, xC };
            }

            function isInsideTriangle(px, py) {
                const {xA, xB, xC} = cartesianToBarycentric(px, py);
                return xA >= -1e-6 && xB >= -1e-6 && xC >= -1e-6 && (xA + xB + xC) <= 1 + 1e-6;
            }

            function isInsideEnvelope(point) {
                const pos = barycentricToCartesian(point.xA, point.xB, point.xC);
                let inside = false;
                for (let i = 0, j = phaseEnvelopePolygon.length - 1; i < phaseEnvelopePolygon.length; j = i++) {
                    const pi = barycentricToCartesian(phaseEnvelopePolygon[i].xA, phaseEnvelopePolygon[i].xB, phaseEnvelopePolygon[i].xC);
                    const pj = barycentricToCartesian(phaseEnvelopePolygon[j].xA, phaseEnvelopePolygon[j].xB, phaseEnvelopePolygon[j].xC);
                    const intersect = ((pi.y > pos.y) !== (pj.y > pos.y)) &&
                        (pos.x < (pj.x - pi.x) * (pos.y - pi.y) / (pj.y - pi.y) + pi.x);
                    if (intersect) inside = !inside;
                }
                return inside;
            }

            function getPhaseCompositions(point) {
                 // Sort tie lines by xB component of alpha phase to make them searchable
                const sortedTieLines = [...tieLinesData].sort((a, b) => b.alpha.xB - a.alpha.xB);

                // Find the two tie lines that bracket the point based on a property, e.g., slope
                // A simpler way: find the intersection of a line through the point and the envelope.
                // For this demo, we'll interpolate between existing tie lines.
                // We find an "interpolation factor" based on the user point's composition relative to the tie lines.

                // Let's use xC as the interpolation guide.
                let t_interp = (point.xC - sortedTieLines[0].alpha.xC) / 
                               (sortedTieLines[sortedTieLines.length-1].alpha.xC - sortedTieLines[0].alpha.xC);
                t_interp = p.constrain(t_interp, 0, 1);

                const i_float = t_interp * (sortedTieLines.length - 1);
                const i1 = Math.floor(i_float);
                const i2 = Math.min(i1 + 1, sortedTieLines.length - 1);

                if (i1 === i2) {
                    return { alpha: sortedTieLines[i1].alpha, beta: sortedTieLines[i1].beta };
                }

                const w = i_float - i1; // weight for line i2

                const alpha = {
                    xA: p.lerp(sortedTieLines[i1].alpha.xA, sortedTieLines[i2].alpha.xA, w),
                    xB: p.lerp(sortedTieLines[i1].alpha.xB, sortedTieLines[i2].alpha.xB, w),
                    xC: 0
                };
                alpha.xC = 1 - alpha.xA - alpha.xB;

                const beta = {
                    xA: p.lerp(sortedTieLines[i1].beta.xA, sortedTieLines[i2].beta.xA, w),
                    xB: p.lerp(sortedTieLines[i1].beta.xB, sortedTieLines[i2].beta.xB, w),
                    xC: 0
                };
                beta.xC = 1 - beta.xA - beta.xB;

                return { alpha, beta };
            }

            // --- MOUSE INTERACTION ---
            p.mousePressed = () => {
                if (p.mouseX > 0 && p.mouseX < p.width && p.mouseY > 0 && p.mouseY < p.height) {
                    if (isInsideTriangle(p.mouseX, p.mouseY)) {
                        const comp = cartesianToBarycentric(p.mouseX, p.mouseY);
                        userPoint = { 
                            xA: Math.max(0, comp.xA), 
                            xB: Math.max(0, comp.xB), 
                            xC: Math.max(0, comp.xC) 
                        };
                         const sum = userPoint.xA + userPoint.xB + userPoint.xC;
                        userPoint.xA /= sum;
                        userPoint.xB /= sum;
                        userPoint.xC /= sum;

                        updateUIState();
                    }
                }
            };

            // --- UI UPDATE LOGIC ---

            function setupDOMListeners() {
                document.getElementById('btn-diagram').onclick = () => { currentView = 'diagram'; updateUIState(); };
                document.getElementById('btn-phases').onclick = () => { currentView = 'phases'; updateUIState(); };

                document.getElementById('check-grid-lines').onchange = (e) => { showGridLines = e.target.checked; updateUIState(); };
                document.getElementById('check-tie-lines').onchange = (e) => { showTieLines = e.target.checked; updateUIState(); };
                document.getElementById('check-alpha-phase').onchange = (e) => { showAlphaPhase = e.target.checked; updateUIState(); };
                document.getElementById('check-beta-phase').onchange = (e) => { showBetaPhase = e.target.checked; updateUIState(); };
            }

            function updateUIState() {
                // Update button active state
                document.getElementById('btn-diagram').classList.toggle('active', currentView === 'diagram');
                document.getElementById('btn-phases').classList.toggle('active', currentView === 'phases');

                // Update checkbox visibility
                const checkboxContainer = document.getElementById('checkbox-container');
                checkboxContainer.style.display = currentView === 'diagram' ? 'inline' : 'none';

                updateInfoBoxes();
                p.redraw();
            }

            async function updateInfoBoxes() {
                const mainBox = document.getElementById('info-box-main');
                const alphaBox = document.getElementById('info-box-alpha');
                const betaBox = document.getElementById('info-box-beta');

                // Always show main box in diagram view
                mainBox.style.display = currentView === 'diagram' ? 'block' : 'none';
                document.getElementById('main-content').innerHTML = `
                    \\(x_A = ${userPoint.xA.toFixed(2)}\\) <br>
                    \\(x_B = ${userPoint.xB.toFixed(2)}\\) <br>
                    \\(x_C = ${userPoint.xC.toFixed(2)}\\)
                `;

                const inEnvelope = isInsideEnvelope(userPoint);

                if (currentView === 'diagram' && inEnvelope) {
                    const { alpha, beta } = getPhaseCompositions(userPoint);
                    if (alpha) {
                        alphaBox.style.display = showAlphaPhase ? 'block' : 'none';
                         document.getElementById('alpha-content').innerHTML = `
                            \\(x_A^\\alpha = ${alpha.xA.toFixed(2)}\\) <br>
                            \\(x_B^\\alpha = ${alpha.xB.toFixed(2)}\\) <br>
                            \\(x_C^\\alpha = ${alpha.xC.toFixed(2)}\\)
                        `;
                    } else {
                         alphaBox.style.display = 'none';
                    }
                    if (beta) {
                        betaBox.style.display = showBetaPhase ? 'block' : 'none';
                        document.getElementById('beta-content').innerHTML = `
                            \\(x_A^\\beta = ${beta.xA.toFixed(2)}\\) <br>
                            \\(x_B^\\beta = ${beta.xB.toFixed(2)}\\) <br>
                            \\(x_C^\\beta = ${beta.xC.toFixed(2)}\\)
                        `;
                    } else {
                        betaBox.style.display = 'none';
                    }
                } else {
                    alphaBox.style.display = 'none';
                    betaBox.style.display = 'none';
                }

                // If the user point is in one phase, the main box moves to the left
                if (currentView === 'diagram' && !inEnvelope) {
                     mainBox.style.left = '20px';
                     mainBox.style.right = 'auto';
                } else { // in two phase, move main box to avoid overlap
                    if (showAlphaPhase && showBetaPhase){
                        mainBox.style.left = '50%';
                        mainBox.style.transform = 'translateX(-50%)';
                    } else if (showAlphaPhase) {
                        mainBox.style.left = 'auto';
                        mainBox.style.right = '20px';
                    } else {
                        mainBox.style.left = '20px';
                        mainBox.style.right = 'auto';
                        mainBox.style.transform = 'none';
                    }
                }
                if (window.MathJax && MathJax.typesetPromise) {
                   await MathJax.typesetPromise();
                }
            }
        };

        new p5(sketch);
    </script>
</body>
