    const winnerDisplay = document.getElementById('winner-display');

    // --- PSEUDO-RANDOM NUMBER GENERATOR ---
    function mulberry32(a) {
        return function() {
            let t = a += 0x6D2B79F5;
            t = Math.imul(t ^ t >>> 15, t | 1);
            t ^= t + Math.imul(t ^ t >>> 7, t | 61);
            return ((t ^ t >>> 14) >>> 0) / 4294967296;
        }
    }

    // --- TSP UTILITIES ---
    const distSq = (p1, p2) => (p1.x - p2.x) ** 2 + (p1.y - p2.y) ** 2;
    const dist = (p1, p2) => Math.sqrt(distSq(p1, p2));

    function calculateTourLength(tour) {
        let totalDist = 0;
        for (let i = 0; i < tour.length; i++) {
            totalDist += dist(tour[i], tour[(i + 1) % tour.length]);
        }
        return totalDist;
    }

    function getNearestNeighborTour(allPoints) {
        if (allPoints.length === 0) return [];
        let unvisited = [...allPoints];
        let tour = [unvisited.splice(0, 1)[0]];
        while (unvisited.length > 0) {
            let lastPoint = tour[tour.length - 1];
            let nearestIndex = -1;
            let min_d_sq = Infinity;
            for (let i = 0; i < unvisited.length; i++) {
                let d_sq = distSq(lastPoint, unvisited[i]);
                if (d_sq < min_d_sq) {
                    min_d_sq = d_sq;
                    nearestIndex = i;
                }
            }
            tour.push(unvisited.splice(nearestIndex, 1)[0]);
        }
        return tour;
    }


    // --- TSP ALGORITHMS ---
    // Note: These are simplified implementations for a frontend demo.

    function solveTwoOpt(initialTour) {
        let tour = [...initialTour];
        let improved = true;
        while (improved) {
            improved = false;
            for (let i = 0; i < tour.length - 1; i++) {
                for (let j = i + 1; j < tour.length; j++) {
                    const current_dist = dist(tour[i], tour[(i + 1) % tour.length]) + dist(tour[j], tour[(j + 1) % tour.length]);
                    const new_dist = dist(tour[i], tour[j]) + dist(tour[(i + 1) % tour.length], tour[(j + 1) % tour.length]);
                    if (new_dist < current_dist) {
                        const newTour = tour.slice(0, i + 1)
                                         .concat(tour.slice(i + 1, j + 1).reverse())
                                         .concat(tour.slice(j + 1));
                        tour = newTour;
                        improved = true;
                    }
                }
            }
        }
        return tour;
    }

    function solveOrOpt(initialTour, segmentLength = 3) {
        let bestTour = [...initialTour];
        let bestLength = calculateTourLength(bestTour);
        let improved = true;

        while (improved) {
            improved = false;
            for (let k = 1; k <= segmentLength; k++) { // Segment size
                for (let i = 0; i < bestTour.length; i++) { // Start of segment
                    if (i + k > bestTour.length) continue;
                    let segment = bestTour.slice(i, i + k);

                    let tempTour = [...bestTour];
                    tempTour.splice(i, k); // Remove segment

                    for (let j = 0; j <= tempTour.length; j++) { // Insertion point
                        if (j >= i && j < i + k) continue; // Don't insert into itself

                        let newTour = [...tempTour];
                        newTour.splice(j, 0, ...segment);

                        let newLength = calculateTourLength(newTour);
                        if (newLength < bestLength) {
                            bestLength = newLength;
                            bestTour = newTour;
                            improved = true;
                            // Break inner loops and restart search
                            goto_outer_loop: break;
                        }
                    }
                }
            }
        }
        return bestTour;
    }


    function solve3Opt(initialTour) {
        let tour = [...initialTour];
        let n = tour.length;
        let improved = true;
        while(improved) {
            improved = false;
            for (let i = 0; i < n; i++) {
                for (let j = i + 2; j < n; j++) {
                    for (let k = j + 2; k < n + (i > 0 ? 1 : 0); k++) {
                        const p1 = tour[i], p2 = tour[(i+1)%n];
                        const p3 = tour[j], p4 = tour[(j+1)%n];
                        const p5 = tour[k%n], p6 = tour[(k+1)%n];

                        // Original cost
                        const d0 = dist(p1,p2) + dist(p3,p4) + dist(p5,p6);

                        // Try 2-opt swaps first (simpler cases of 3-opt)
                        const d1 = dist(p1,p3) + dist(p2,p4) + dist(p5,p6); // 2-opt (i, j)
                        const d2 = dist(p1,p2) + dist(p3,p5) + dist(p4,p6); // 2-opt (j, k)
                        const d3 = dist(p1,p5) + dist(p6,p2) + dist(p3,p4); // 2-opt (k, i)

                        // 3-opt swaps
                        const d4 = dist(p1,p4) + dist(p5,p2) + dist(p3,p6); // reverse(i+1, j), reverse(j+1, k)
                        const d5 = dist(p1,p3) + dist(p2,p5) + dist(p4,p6); // reverse(i+1, j)
                        const d6 = dist(p1,p5) + dist(p4,p2) + dist(p3,p6); // reverse(j+1, k)
                        const d7 = dist(p1,p4) + dist(p2,p6) + dist(p3,p5); // reverse(i+1, k)

                        if (d0 > d5) {
                            tour = tour.slice(0,i+1).concat(tour.slice(i+1,j+1).reverse()).concat(tour.slice(j+1));
                            improved = true; break;
                        } else if (d0 > d6) {
                            tour = tour.slice(0,j+1).concat(tour.slice(j+1,k+1).reverse()).concat(tour.slice(k+1));
                            improved = true; break;
                        } else if (d0 > d7) {
                             tour = tour.slice(0,i+1).concat(tour.slice(i+1,k+1).reverse()).concat(tour.slice(k+1));
                             improved = true; break;
                        } else if (d0 > d4) {
                            const segment1 = tour.slice(i+1,j+1);
                            const segment2 = tour.slice(j+1,k+1);
                            tour = tour.slice(0,i+1).concat(segment2).concat(segment1).concat(tour.slice(k+1));
                            improved = true; break;
                        }
                    }
                    if(improved) break;
                }
                if(improved) break;
            }
        }
        return tour;
    }

    function solveCCA(allPoints) {
        const n = allPoints.length;
        if (n < 3) return allPoints;

        const pointIndex = new Map(allPoints.map((p, i) => [p, i]));
        const indices = Array.from({length: n}, (_, i) => i);

        // 1. Minimum Spanning Tree (Prim's Algorithm)
        const mst_adj = Array.from({length: n}, () => []);
        const key = Array(n).fill(Infinity);
        const parent = Array(n).fill(-1);
        const inMST = Array(n).fill(false);
        key[0] = 0;

        for (let count = 0; count < n - 1; count++) {
            let u = -1, minKey = Infinity;
            for (let v = 0; v < n; v++) {
                if (!inMST[v] && key[v] < minKey) {
                    minKey = key[v];
                    u = v;
                }
            }
            if (u === -1) break;
            inMST[u] = true;
            for (let v = 0; v < n; v++) {
                const d = dist(allPoints[u], allPoints[v]);
                if (!inMST[v] && d < key[v]) {
                    parent[v] = u;
                    key[v] = d;
                }
            }
        }

        for (let i = 1; i < n; i++) {
            mst_adj[i].push(parent[i]);
            mst_adj[parent[i]].push(i);
        }

        // 2. Find odd degree vertices
        const odd_vertices = indices.filter(i => mst_adj[i].length % 2 !== 0);

        // 3. Minimum-weight perfect matching (Greedy Heuristic)
        let unmatched = [...odd_vertices];
        const matching_edges = [];
        while (unmatched.length > 0) {
            const u = unmatched.pop();
            let best_v = -1, min_d = Infinity;
            for (let i = 0; i < unmatched.length; i++) {
                const v = unmatched[i];
                const d = dist(allPoints[u], allPoints[v]);
                if (d < min_d) {
                    min_d = d;
                    best_v = i;
                }
            }
            const v_node = unmatched.splice(best_v, 1)[0];
            matching_edges.push([u, v_node]);
        }

        // 4. Combine MST and matching to form an Eulerian multigraph
        const euler_adj = mst_adj.map(neighbors => [...neighbors]);
        matching_edges.forEach(([u, v]) => {
            euler_adj[u].push(v);
            euler_adj[v].push(u);
        });

        // 5. Find Eulerian circuit (Hierholzer's algorithm)
        let circuit = [];
        let curr_path = [0];
        let curr_v = 0;

        while (curr_path.length > 0) {
            if (euler_adj[curr_v].length > 0) {
                curr_path.push(curr_v);
                let next_v = euler_adj[curr_v].pop();
                euler_adj[next_v] = euler_adj[next_v].filter(node => node !== curr_v); // Remove edge
                curr_v = next_v;
            } else {
                circuit.push(curr_v);
                curr_v = curr_path.pop();
            }
        }
        circuit.reverse();

        // 6. Make Hamiltonian by shortcutting
        const visited = new Set();
        const tour_indices = [];
        for (const index of circuit) {
            if (!visited.has(index)) {
                tour_indices.push(index);
                visited.add(index);
            }
        }

        return tour_indices.map(i => allPoints[i]);
    }

    // --- P5.JS SKETCH DEFINITION ---
    const sketch = (p) => {
        let pointsToDraw = [];
        let tourToDraw = [];
        let pathColor = [0, 0, 0];
        const margin = 40;

        p.setup = () => {
            const container = p.canvas.parentElement;
            const size = container.offsetWidth;
            p.createCanvas(size, size);
            p.noLoop();
        };

        p.windowResized = () => {
             const container = p.canvas.parentElement;
             const size = container.offsetWidth;
             p.resizeCanvas(size, size);
             p.draw();
        }

        p.updateAndDraw = (newPoints, newTour, color) => {
            pointsToDraw = newPoints;
            tourToDraw = newTour;
            pathColor = p.color(color);
            p.redraw();
        };

        p.draw = () => {
            p.background(255);
            if (pointsToDraw.length === 0) return;

            const w = p.width - 2 * margin;
            const h = p.height - 2 * margin;

            // Draw axes and labels
            p.stroke(200);
            p.strokeWeight(1);
            p.line(margin, margin, margin, p.height - margin);
            p.line(margin, p.height - margin, p.width - margin, p.height - margin);
            p.fill(100);
            p.noStroke();
            p.textAlign(p.CENTER, p.CENTER);
            for (let i = 0; i <= 5; i++) {
                const v = i / 5.0;
                // Y-axis
                p.text(v.toFixed(1), margin - 20, p.height - margin - v * h);
                p.stroke(220);
                p.line(margin - 5, p.height - margin - v * h, p.width - margin, p.height - margin - v * h);
                // X-axis
                p.noStroke();
                p.text(v.toFixed(1), margin + v * w, p.height - margin + 15);
                 p.stroke(220);
                p.line(margin + v * w, margin, margin + v * w, p.height - margin + 5);
            }

            // Draw Tour
            p.stroke(pathColor);
            p.strokeWeight(2);
            p.noFill();
            p.beginShape();
            tourToDraw.forEach(pt => {
                p.vertex(margin + pt.x * w, p.height - margin - pt.y * h);
            });
            p.endShape(p.CLOSE);

            // Draw Points
            p.noStroke();
            p.fill(255, 0, 0);
            pointsToDraw.forEach(pt => {
                p.ellipse(margin + pt.x * w, p.height - margin - pt.y * h, 8, 8);
            });
        };
    };

    // --- MAIN SIMULATION AND UI UPDATE LOGIC ---
    function runSimulation() {
        const numPoints = parseInt(sliderPoints.value);
        const seed = parseInt(seedInput.value);

        // Generate points
        const random = mulberry32(seed);
        points = Array.from({ length: numPoints }, () => ({ x: random(), y: random() }));

        // Base tour for local search algorithms
        const nnTour = getNearestNeighborTour(points);

        const algorithms = {
            'OrZweig': (pts) => solveTwoOpt(getNearestNeighborTour(pts)), // Implemented as 2-Opt
            'OrOpt': (pts) => solveOrOpt(getNearestNeighborTour(pts)),
            'TwoOpt': (pts) => solveTwoOpt(getNearestNeighborTour(pts)),
            'CCA': (pts) => solveCCA(pts),
            '3-Opt': (pts) => solve3Opt(getNearestNeighborTour(pts))
        };

        results = {};
        for (const name in algorithms) {
            const startTime = performance.now();
            const tour = algorithms[name](points);
            const endTime = performance.now();

            results[name] = {
                tour: tour,
                length: calculateTourLength(tour),
                time: (endTime - startTime) / 1000
            };
        }

        updateUI();
    }

    function updateUI() {
        // Update table
        for (const name in results) {
            const idSuffix = name.replace('-', ''); // 3-Opt -> 3Opt
            const timeCell = document.getElementById(`timing-${idSuffix.toLowerCase()}`);
            const resultCell = document.getElementById(`result-${idSuffix.toLowerCase()}`);
            if (timeCell && resultCell) {
                timeCell.textContent = results[name].time.toFixed(6);
                resultCell.textContent = results[name].length.toFixed(5);
            }
        }

        // Update winner
        const mathematicaResults = ['OrZweig', 'OrOpt', 'TwoOpt', 'CCA'].map(n => results[n].length);
        const minMathResult = Math.min(...mathematicaResults);
        const result3Opt = results['3-Opt'].length;

        const epsilon = 1e-9;
        if (result3Opt < minMathResult - epsilon) {
            winnerDisplay.textContent = "3-Opt wins";
        } else if (minMathResult < result3Opt - epsilon) {
            winnerDisplay.textContent = "Mathematica wins";
        } else {
            winnerDisplay.textContent = "Tie";
        }

        // Redraw plots
        redrawPlots();
    }

    function redrawPlots() {
        const selectedMethod = document.querySelector('input[name="math-method"]:checked').value;
        leftPlotTitle.textContent = selectedMethod;

        if (results[selectedMethod]) {
            leftSketch.updateAndDraw(points, results[selectedMethod].tour, '#0072B2');
        }
        if (results['3-Opt']) {
            rightSketch.updateAndDraw(points, results['3-Opt'].tour, '#E69F00');
        }
    }


    // --- EVENT LISTENERS ---
    btnRandomSet.addEventListener('click', () => {
        seedInput.value = Math.floor(Math.random() * 99999);
        runSimulation();
    });

    btnResetSeed.addEventListener('click', () => {
        seedInput.value = '1245';
        runSimulation();
    });

    sliderPoints.addEventListener('input', runSimulation);

    radioMethods.forEach(radio => {
        radio.addEventListener('change', redrawPlots);
    });

    window.addEventListener('resize', () => {
        leftSketch.windowResized();
        rightSketch.windowResized();
    });

    // --- INITIALIZATION ---
    function init() {
        leftSketch = new p5(sketch, 'canvas-left-wrapper');
        rightSketch = new p5(sketch, 'canvas-right-wrapper');
        runSimulation();
    }

    init();
});
</script>

