<script>
const sketch = (p) => {
    // --- DOM Elements ---
    let sliderInitialization, valueInitialization;
    let sliderMaxSteps, valueMaxSteps;
    let checkboxCycle4, checkboxCycle2, checkboxCycle1, checkboxAllPoints, checkboxModify;
    let infoText;

    // --- State Variables ---
    let initialState, maxSteps;
    let showCycle4, showCycle2, showCycle1, showAllPoints, modifyInit;
    let currentPath = [];

    // --- Pre-calculated Data ---
    const allMapPoints = [];
    const cycle1Points = [];
    const cycle2Pairs = [[10922, 21845], [13107, 26214], [13653, 27306], [15339, 30678], [21844, 43691], [26213, 52427], [27305, 54611], [30677, 61355], [43690, 54613]];
    const cycle4Points = [43690, 54612, 32768, 49152];

    const canvasSize = 512;
    const gridDim = 256;

    // --- Core Automaton Logic ---
    const reverseBits = (num, bits) => {
        let reversedNum = 0;
        for (let i = 0; i < bits; i++) {
            if ((num >> i) & 1) {
                reversedNum |= 1 << ((bits - 1) - i);
            }
        }
        return reversedNum;
    };

    const BitReverse15 = (n) => reverseBits(n, 15);

    const automatonFunc = (n) => {
        return (n + BitReverse15(Math.floor(n / 2))) % 65536;
    };

    // --- Coordinate Mapping ---
    const mapNToPos = (n) => {
        const x = n & 255;
        const y = (n >> 8) & 255;
        return {
            x: p.map(x, 0, gridDim - 1, 0, canvasSize),
            y: p.map(y, 0, gridDim - 1, 0, canvasSize)
        };
    };

    // --- Pre-calculation Functions ---
    const precomputeAllMapPoints = () => {
        if (allMapPoints.length > 0) return;
        for (let n = 0; n <= 65535; n++) {
            const nextN = automatonFunc(n);
            allMapPoints.push(mapNToPos(nextN));
        }
    };

    const precomputeCycle1Points = () => {
        if (cycle1Points.length > 0) return;
        for (let n = 0; n <= 65535; n++) {
            if (automatonFunc(n) === n) {
                cycle1Points.push(n);
            }
        }
    };

    // --- Update and Redraw Logic ---
    const updateSimulation = () => {
        // 1. Read values from controls
        initialState = parseInt(sliderInitialization.value);
        maxSteps = parseInt(sliderMaxSteps.value);
        showCycle4 = checkboxCycle4.checked;
        showCycle2 = checkboxCycle2.checked;
        showCycle1 = checkboxCycle1.checked;
        showAllPoints = checkboxAllPoints.checked;
        modifyInit = checkboxModify.checked;

        // 2. Update value display spans
        valueInitialization.innerText = `+ ${initialState}`;
        valueMaxSteps.innerText = `+ ${maxSteps}`;

        // 3. Generate path and detect cycles
        let pathForDetection = [initialState];
        let n = initialState;
        let cycleFound = false;

        for (let i = 0; i < maxSteps + 1; i++) {
            n = automatonFunc(n);
            pathForDetection.push(n);
        }

        const history = new Map();
        history.set(pathForDetection[0], 0);
        for (let j = 1; j < pathForDetection.length; j++) {
            if (history.has(pathForDetection[j])) {
                const prevIndex = history.get(pathForDetection[j]);
                const cycleLength = j - prevIndex;
                infoText.innerText = `result of initial condition ${initialState} repeats in ${cycleLength} steps`;
                cycleFound = true;
                break;
            }
            history.set(pathForDetection[j], j);
        }

        if (!cycleFound) {
             infoText.innerText = `result of initial condition ${initialState} does not repeat in ${maxSteps + 1} steps`;
        }

        // The path to draw should have maxSteps segments, which is maxSteps + 1 points.
        currentPath = pathForDetection.slice(0, maxSteps + 2);

        // 4. Trigger redraw
        p.redraw();
    };

    const handleCheckboxChange = () => {
