        checkboxCycle2 = document.getElementById('checkbox-cycle2');
        checkboxCycle1 = document.getElementById('checkbox-cycle1');
        checkboxAllPoints = document.getElementById('checkbox-all-points');
        checkboxModify = document.getElementById('checkbox-modify');
        infoText = document.getElementById('info-text');

        // --- Attach Event Listeners ---
        sliderInitialization.addEventListener('input', updateSimulation);
        sliderMaxSteps.addEventListener('input', updateSimulation);
        checkboxCycle4.addEventListener('change', handleCheckboxChange);
        checkboxCycle2.addEventListener('change', handleCheckboxChange);
        checkboxCycle1.addEventListener('change', handleCheckboxChange);
        checkboxModify.addEventListener('change', handleCheckboxChange);
        checkboxAllPoints.addEventListener('change', () => {
            if (checkboxAllPoints.checked && allMapPoints.length === 0) {
