            }
            handleCheckboxChange();
        });

        // --- Pre-computation ---
        precomputeCycle1Points();

        // --- Initial Draw ---
        updateSimulation();
    };

    p.draw = () => {
        p.noStroke();
        p.fill(255);

        // 1. Background
        p.background('#e0f0e0');

        // 2. "All Map Points" Overlay
        if (showAllPoints) {
            p.stroke('#808080');
            p.strokeWeight(1.5);
            allMapPoints.forEach(pos => p.point(pos.x, pos.y));
        }

        // 3. Automaton Path (drawn after overlays to appear on top)
        if (currentPath.length > 1) {
            p.noFill();
            p.strokeWeight(2);
            p.stroke('#800080'); // Purple
            p.beginShape();
            currentPath.forEach(n => {
                const pos = mapNToPos(n);
                p.vertex(pos.x, pos.y);
            });
            p.endShape();

            // Start and end points
            const startPos = mapNToPos(currentPath[0]);
            const endPos = mapNToPos(currentPath[currentPath.length - 1]);

            p.noStroke();
            p.fill('#FF0000'); // Red
            p.circle(startPos.x, startPos.y, 6);

            p.fill('#0000FF'); // Blue
            p.circle(endPos.x, endPos.y, 6);
        }

        // 4. "Length 1 Cycle Points" Overlay
        if (showCycle1) {
            p.stroke('#00FFFF'); // Cyan
            p.strokeWeight(5);
            cycle1Points.forEach(n => {
                const pos = mapNToPos(n);
                p.point(pos.x, pos.y);
            });
        }

        // 5. "Length 2 Cycles" Overlay
        if (showCycle2) {
            p.strokeWeight(1);
            p.stroke('#008000'); // Green
            cycle2Pairs.forEach(pair => {
                const p1 = mapNToPos(pair[0]);
                const p2 = mapNToPos(pair[1]);
                p.line(p1.x, p1.y, p2.x, p2.y);
            });
        }

        // 6. "Length 4 Cycle" Overlay
        if (showCycle4) {
            const mappedCycle4 = cycle4Points.map(n => mapNToPos(n));

            // Bounding box
            const xs = mappedCycle4.map(pt => pt.x);
            const ys = mappedCycle4.map(pt => pt.y);
            const minX = Math.min(...xs);
            const maxX = Math.max(...xs);
            const minY = Math.min(...ys);
            const maxY = Math.max(...ys);

            p.noFill();
            p.strokeWeight(2);
            p.stroke('#FFA500'); // Orange
            p.rect(minX, minY, maxX - minX, maxY - minY);

            // Cycle path and bisectors
            p.strokeWeight(1);
            p.stroke('#008000'); // Green
            for (let i = 0; i < mappedCycle4.length; i++) {
                const p1 = mappedCycle4[i];
                const p2 = mappedCycle4[(i + 1) % mappedCycle4.length];

                // Draw cycle edge
                p.line(p1.x, p1.y, p2.x, p2.y);

                // Draw perpendicular bisector segment
                const midX = (p1.x + p2.x) / 2;
                const midY = (p1.y + p2.y) / 2;
                const vecX = p2.x - p1.x;
                const vecY = p2.y - p1.y;
                const perpVecX = -vecY;
                const perpVecY = vecX;
                const len = Math.sqrt(perpVecX * perpVecX + perpVecY * perpVecY);
                if (len > 0) {
                    const scale = 30 / len; // Bisector length
                    const B1x = midX + perpVecX * scale;
                    const B1y = midY + perpVecY * scale;
                    const B2x = midX - perpVecX * scale;
                    const B2y = midY - perpVecY * scale;
                    p.line(B1x, B1y, B2x, B2y);
                }
            }
        }
    };
};

